INSERT INTO `post` (`post_id`, `image`, `author_id`, `title`, `content`, `link`, `tag`, `featured`, `sort_order`, `status`, `date_added`, `date_modified`, `parent_id`, `summary`, `sub_title`, `top`, `menu`) VALUES
(1, 'catalog/AnhCatBa/313cb45834517041686543cde225bc3b.jpg', 4, 'Các Điểm Tham Quan Du Lịch', '<h2 style=\"color: rgb(68, 68, 68); font-family: \" open=\"\" sans\",=\"\" sans-serif;=\"\" font-size:=\"\" 27px;\"=\"\"><span style=\"color: rgb(1, 106, 209); font-family: FuturaHeavy; font-size: 24px; font-weight: unset;\">Pháo đài thần công, điểm cao vọng cảnh Cát Bà</span><br></h2><h3 style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0.5rem; font-family: \" noto=\"\" sans\",=\"\" sans-serif;=\"\" font-weight:=\"\" 700;=\"\" color:=\"\" rgb(65,=\"\" 65,=\"\" 65);=\"\" font-size:=\"\" 1.125rem;\"=\"\"><div class=\"row\" style=\"margin-right: -15px; margin-left: -15px; color: rgb(102, 102, 102); font-family: \" open=\"\" sans\",=\"\" sans-serif;=\"\" font-size:=\"\" 12px;\"=\"\"><div class=\"col-sm-10\" style=\"padding-right: 15px; padding-left: 15px; width: 731.219px;\"><div class=\"thongke\" style=\"margin: 10px 0px 20px; padding: 0px; height: 29px; background: rgb(247, 247, 247); border-top: 1px solid rgb(234, 234, 234); color: rgb(153, 153, 153); font-size: 13px; font-family: SegoeUI;\"><a class=\"thongke_ngay\" images=\"\" common=\"\" date_ct.png\");=\"\" background-position:=\"\" left=\"\" center;=\"\" background-size:=\"\" 10px=\"\" 10px;=\"\" background-repeat:=\"\" no-repeat;=\"\" color:=\"\" rgb(153,=\"\" 153,=\"\" 153);=\"\" float:=\"\" left;=\"\" line-height:=\"\" 24px;\"=\"\" style=\"background-image: url(\" \");=\"\" 0px=\"\" 0px;=\"\" rgb(35,=\"\" 161,=\"\" 209);=\"\" margin:=\"\" padding:=\"\" 20px;\"=\"\">Ngày đăng: 03:47 04/06/2020</a><a class=\"thongke_luotxem\" images=\"\" common=\"\" view_ct.png\");=\"\" background-position:=\"\" left=\"\" center;=\"\" background-size:=\"\" 13px=\"\" 7px;=\"\" background-repeat:=\"\" no-repeat;=\"\" color:=\"\" rgb(153,=\"\" 153,=\"\" 153);=\"\" float:=\"\" left;=\"\" line-height:=\"\" 24px;\"=\"\" style=\"background-image: url(\" \");=\"\" 0px=\"\" 0px;=\"\" rgb(35,=\"\" 161,=\"\" 209);=\"\" margin:=\"\" 20px;=\"\" padding:=\"\" 20px;\"=\"\">Lượt xem: 192</a><div class=\"cochu\" style=\"margin: 0px; padding: 0px; display: flex; -webkit-box-pack: end; justify-content: flex-end; float: right; flex-direction: row; align-items: center;\"><a class=\"NormalSize\" style=\"background-position: 0px 0px; color: rgb(153, 153, 153); margin: 0px 10px 0px 3px; padding: 0px; line-height: 25px; display: block; float: left;\">Cỡ chữ</a><a class=\"SmallSize\" images=\"\" common=\"\" tru.png\");=\"\" background-position:=\"\" center=\"\" center;=\"\" background-repeat:=\"\" no-repeat;=\"\" color:=\"\" rgb(153,=\"\" 153,=\"\" 153);=\"\" height:=\"\" 11px;=\"\" width:=\"\" display:=\"\" block;=\"\" float:=\"\" left;\"=\"\" style=\"background-image: url(\" \");=\"\" 0px=\"\" 0px;=\"\" rgb(35,=\"\" 161,=\"\" 209);=\"\" margin:=\"\" 3px;=\"\" padding:=\"\" 0px;\"=\"\"></a><a class=\"LargeSize\" images=\"\" common=\"\" cong.png\");=\"\" background-position:=\"\" center=\"\" center;=\"\" background-repeat:=\"\" no-repeat;=\"\" color:=\"\" rgb(153,=\"\" 153,=\"\" 153);=\"\" height:=\"\" 11px;=\"\" width:=\"\" display:=\"\" block;=\"\" float:=\"\" left;\"=\"\" style=\"background-image: url(\" \");=\"\" 0px=\"\" 0px;=\"\" rgb(35,=\"\" 161,=\"\" 209);=\"\" margin:=\"\" 3px;=\"\" padding:=\"\" 0px;\"=\"\"></a></div></div><div class=\"noidung TextSize\" style=\"margin: 0px; padding: 0px; font-family: SegoeUI; color: rgb(51, 51, 51); font-size: 15px; line-height: 24px;\"><span style=\"margin: 0px; padding: 0px; font-family: arial, helvetica, sans-serif;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\">Pháo Đài Thần Công nay còn gọi là Cao điểm 177, Pháo đài quân sự của Cát Bà được quân đội pháp cho xây dựng vào năm 1942 nhằm khống chế toàn bộ cửa Đông Bắc của Việt Nam và cửa ngõ Đông Nam của thành phố Hải Phòng.<br style=\"margin: 0px; padding: 0px;\"><br style=\"margin: 0px; padding: 0px;\">Nằm trên đỉnh ngọn đồi có độ cao 177m so với mực nước biển, gồm trận địa các khẩu pháo đối hải cỡ lớn, hệ thống hầm và hào công sự được xây dựng từ những năm 40 của thế kỷ 20. Nhờ vào vị trí đắc địa, trường quan sát nhô ra 3 phía hướng biển, điểm cao thu trọn một dải biển Đông trong tầm mắt, đằng sau lưng là những rặng núi trùng điệp làm điểm tựa. Pháo đài thần công trở thành nhân chứng lịch sử đồng hành cùng quân và dân Cát Bà trải qua hai cuộc chiến kháng chiến vệ quốc. Tại đây lưu giữ nhiều kỷ vật lịch sử, nhất là hai khẩu pháo với trọng lượng mỗi khẩu hàng chục tấn.<br style=\"margin: 0px; padding: 0px;\"><img alt=\"\" src=\"http://catbafreedom.com/pic/AboutUs/images/Cannon%20fort/From%20Cannon%20fort.jpg\" style=\"border-color: currentcolor; margin: 3px 0px; padding: 0px; max-width: 100%; height: 538px; width: 850px;\"><br style=\"margin: 0px; padding: 0px;\">Tại cao điểm Pháo đài Thần Công có 3 đài quan sát, 3 khẩu pháo, hầm chỉ huy, nhà truyền thống, hầm chống bom nguyên tử và cũng là nơi sinh hoạt của các chiến sỹ trong thời kỳ kháng chiến.</span></span><br style=\"margin: 0px; padding: 0px;\"><br style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px; font-family: arial, helvetica, sans-serif; text-align: justify;\">Đài quan sát số 1 (Đài quan sát phía Tây Nam).</span><br style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-family: arial, helvetica, sans-serif;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\">Từ đài quan sát này, quý khách có thể chiêm ngưỡng vẻ đẹp độc đáo của các bãi tắm tại quần đảo Cát Bà, nơi cát trắng nước trong, không gian yên tĩnh như cõi tiên bồng. Theo truyền thuyết, xưa kia, các tiên nữ nhà trời thường hoá thánh những cánh cò hạ xuống các bãi tắm này để thưởng ngoạn vui đùa.<br style=\"margin: 0px; padding: 0px;\"><br style=\"margin: 0px; padding: 0px;\">Cát Cò 1 và Cát Cò 2 là cách gọi của cư dân Cát Bà, phía trước là&nbsp;một hòn đảo giống như chiếc guốc trôi trên sóng nước Cát Bà, đó chính là Hòn Guốc hay có tên gọi khác là Hòn Hài. Chuyện kể rằng, các nàng tiên xuống trần du ngoạn đã đam mê chiêm ngưỡng cảnh quan nơi đây. Khi trời chiều, Ngọc Hoàng Thượng Đế gọi về trời, nàng tiên út do vội vã đã “ vô tình” làm rơi chiếc hài xuống nước và hoá thành ngọn núi giống hình chiếc Hài, để đến hôm nay có hòn Hài cứ bồng bềnh trôi.<br style=\"margin: 0px; padding: 0px;\"><br style=\"margin: 0px; padding: 0px;\">Phía xa là hòn Khoá, trong thời kỳ kháng chiến chống Mỹ vì hòn Khoá có hình giống một tàu chiến nên bị không quân Mỹ lầm tưởng là mục tiêu nên đã bắn hàng trăm tấn bom đạn xuống. Chính vì thế, hòn Khoá còn được nhân dân Cát Bà gọi là hòn Bắn. Nhìn xa hơn nữa là đảo đèn Long Châu hay còn gọi là Mắt Rồng, đêm đêm toả sáng dẫn đường cho hàng vạn con tàu vào ra cập bến cảng Hải Phòng. Có nhà thơ của huyện đảo đã viết:</span></span><br style=\"margin: 0px; padding: 0px;\"><br style=\"margin: 0px; padding: 0px;\"><div style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-family: arial, helvetica, sans-serif;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><em style=\"margin: 0px; padding: 0px;\">“Đây đảo đèn Long Châu sáng trong đêm tối<br style=\"margin: 0px; padding: 0px;\">Cần mẫn bao năm chỉ lối dẫn đường<br style=\"margin: 0px; padding: 0px;\">Đưa những con tàu tàu khắp các đại dương<br style=\"margin: 0px; padding: 0px;\">Về cập bến quê mình thành phố hoa phượng đỏ<br style=\"margin: 0px; padding: 0px;\">Đây bãi tắm Cát Cò nắng vàng rực rỡ<br style=\"margin: 0px; padding: 0px;\">Có hòn Guốc bồng bềnh một thuở Tiên Sa...”</em></span></span></div><br style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-family: arial, helvetica, sans-serif;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\">Đảo đèn Long Châu là công trình hàng hải trọng yếu cho khu vực Vịnh Bắc Bộ, được xây dựng từ thời đầu Pháp thuộc cách đây trên 100 năm cùng với ngọn hải đăng hòn Dấu Đồ Sơn, Đảo đèn Long Châu giờ đây đã trở thành điểm du lịch cho khách tới thăm quan.<br style=\"margin: 0px; padding: 0px;\"><img alt=\"Đảo đèn Long Châu\" class=\"Đảo đèn biển Long Châu\" id=\"Đảo đèn biển Long Châu\" src=\"http://catbafreedom.com/pic/AboutUs/images/Cannon%20fort/%C4%90%E1%BA%A3o%20Long%20Ch%C3%A2u.jpg\" title=\"Đảo đèn biển Long Châu\" style=\"border-color: currentcolor; margin: 3px 0px; padding: 0px; max-width: 100%; height: 566px; width: 850px;\"></span></span><div style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-family: arial, helvetica, sans-serif;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\"><em style=\"margin: 0px; padding: 0px;\">Đảo đèn biển Long Châu</em></span></span></div><br style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; font-family: arial, helvetica, sans-serif;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px;\">Tiếp theo là “ Sở chỉ huy pháp đài”. Toàn bộ pháo đài đặt dưới sự chỉ đạo của sở chỉ huy. Lệnh từ sở chỉ huy sẽ được phát ra cho tất cả các bộ phận. Đây là nơi sinh hoạt của chỉ huy pháo đài và là nơi diễn ra các cuộc họp triển khai tác chiến, thông qua quyết tâm chiến đấu của từng trận đánh.</span></span></div></div></div></h3><h1 class=\"title ct\" style=\"margin-top: 0px; margin-bottom: 15px; font-size: 24px; color: rgb(1, 106, 209); line-height: 24px; font-family: FuturaHeavy; font-weight: unset; padding: 0px;\">Đảo Khỉ (tên gọi cũ là Đảo Cát Dứa)</h1><h1 class=\"title ct\" style=\"margin-top: 0px; margin-bottom: 15px; font-size: 24px; color: rgb(1, 106, 209); line-height: 24px; font-family: FuturaHeavy; font-weight: unset; padding: 0px;\"><span style=\"color: rgb(51, 51, 51); font-family: SegoeUI; font-size: 16px;\">Đảo Khỉ cách thị trấn Cát Bà khoảng 1,5 km theo đường chim bay. Để đến Đảo Khỉ, du khách thường đi bằng tàu hoặc thuyền từ bến Bèo mất khoảng 20 phút. Trong sóng nước yên bình, bốn bề là những dãy núi đá vôi được phủ xanh mướt bởi rừng nguyên sinh, tàu đi qua làng chài Cái Bèo thoáng mắt đã hiện ra Đảo Khỉ.</span><br style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: SegoeUI; font-size: 16px;\"><br style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: SegoeUI; font-size: 16px;\"><span style=\"color: rgb(51, 51, 51); font-family: SegoeUI; font-size: 16px;\">Đảo Khỉ còn có tên gọi là Cát Dứa là vì trên đảo có nhiều cây dứa dại. Vào mùa quả chín có màu vàng trông rất đẹp và bắt mắt. Trước đây trên đảo có rất nhiều cây dứa sai trĩu quả. Người dân đi đánh cá trên vịnh Lan Hạ thường ghé vào đây lấy đem về nấu nước uống hoặc sấy khô làm thuốc chữa bệnh mát gan và có thể ngâm rượu uống.</span><br style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: SegoeUI; font-size: 16px;\"><img alt=\"\" src=\"http://catbafreedom.com/pic/AboutUs/images/Monkey%20island/IMG_7693.JPG\" style=\"border-color: currentcolor; margin: 2px 0px; padding: 0px; max-width: 100%; height: 566px; color: rgb(51, 51, 51); font-family: SegoeUI; font-size: 16px; width: 850px;\"><br style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: SegoeUI; font-size: 16px;\"><span style=\"color: rgb(51, 51, 51); font-family: SegoeUI; font-size: 16px;\">Đảo Khỉ thuộc sự quản lý của vườn Quốc Gia Cát Bà được đưa vào điểm tham quan du lịch nhiều năm nay. Trên đảo có khoảng 30 con khỉ vàng. Chúng thường xuống bãi tắm chơi đùa với du khách, ăn thức ăn do du khách cung cấp như hoa quả, bánh kẹo. Những chú khỉ ngộ nghĩnh chơi đùa, leo trèo tạo ấn tượng cho những ai từng tới đây và dần quen người ta gọi đây là Đảo Khỉ.</span><br style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: SegoeUI; font-size: 16px;\"><img alt=\"\" src=\"http://catbafreedom.com/pic/AboutUs/images/Monkey%20island/Beach.jpg\" style=\"border-color: currentcolor; margin: 2px 0px; padding: 0px; max-width: 100%; height: 477px; color: rgb(51, 51, 51); font-family: SegoeUI; font-size: 16px; width: 850px;\"><br style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: SegoeUI; font-size: 16px;\"><span style=\"color: rgb(51, 51, 51); font-family: SegoeUI; font-size: 16px;\">Đảo Khỉ có chu vi khoảng 3km. Đây là một hòn đảo cấu tạo dạng núi có bãi cát được tạo thành qua hàng triệu năm. Sóng và gió đưa cát, đá, san hô và các loại vỏ sinh vật vào chân núi tạo thành bãi cát dài hàng km. Đảo Khỉ có 2 bãi tắm dạng vòng cung đó là bãi Cát Dứa 1 và Cát Dứa 2.</span></h1><h1 class=\"title ct\" style=\"margin-top: 0px; margin-bottom: 15px; font-size: 24px; color: rgb(1, 106, 209); line-height: 24px; font-family: FuturaHeavy; font-weight: unset; padding: 0px;\">Làng du lịch sinh thái Việt Hải</h1><h3 style=\"box-sizing: inherit; margin-top: 0px; margin-bottom: 0.5rem; font-family: \" noto=\"\" sans\",=\"\" sans-serif;=\"\" font-weight:=\"\" 700;=\"\" color:=\"\" rgb(65,=\"\" 65,=\"\" 65);=\"\" font-size:=\"\" 1.125rem;\"=\"\"><div class=\"row\" style=\"margin-right: -15px; margin-left: -15px; color: rgb(102, 102, 102); font-family: \" open=\"\" sans\",=\"\" sans-serif;=\"\" font-size:=\"\" 12px;\"=\"\"><div class=\"col-sm-10\" style=\"padding-right: 15px; padding-left: 15px; width: 731.219px;\"><div class=\"noidung TextSize\" style=\"margin: 0px; padding: 0px; font-family: SegoeUI; color: rgb(51, 51, 51); font-size: 15px; line-height: 24px;\"></div></div></div></h3><h1 class=\"title ct\" style=\"box-sizing: border-box; margin: 0px 0px 15px; font-size: 24px; color: rgb(1, 106, 209); line-height: 24px; font-family: FuturaHeavy; font-weight: unset; padding: 0px;\"><p style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: SegoeUI; font-size: 15px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: arial, helvetica, sans-serif;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-size: 16px;\">Việt Hải là một trong 12 xã, thị trấn của huyện đảo với diện tích 6.817 ha và dân số khoảng trên 400 người. Để đến Việt Hải, có thể lựa chọn một trong hai con đường: một là từ bến Bèo, du khách đi đò hoặc thuê một tàu du lịch chở du khách chạy vào vịnh Lan Hạ, chừng 45 phút tàu sẽ cập bến.</span></span></p><a href=\"http://catbafreedom.com/cat-ba-town-viet-hai-village.htm\" target=\"_blank\" style=\"box-sizing: border-box; background: 0px 0px rgb(255, 255, 255); color: rgb(66, 139, 202); text-decoration: none; margin: 0px; padding: 0px; font-family: SegoeUI; font-size: 15px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(0, 0, 205);\"><em style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: arial, helvetica, sans-serif;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-size: 16px;\">(Xem lịch trình từ thị trấn Cát Bà đến cảng Việt Hải và ngược lại bằng phương tiện công cộng tại đây)</span></span></em></span></a><span style=\"box-sizing: border-box; color: rgb(51, 51, 51); font-family: SegoeUI; font-size: 15px;\"></span><p style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: SegoeUI; font-size: 15px;\"><a href=\"http://catbafreedom.com/pic/general/images/L%C3%A0ng%20Vi%E1%BB%87t%20H%E1%BA%A3i/Viet%20Hai%202.JPG\" target=\"_self\" style=\"box-sizing: border-box; background: 0px 0px; color: rgb(66, 139, 202); text-decoration: none; margin: 0px; padding: 0px;\"><img alt=\"\" src=\"http://catbafreedom.com/pic/AboutUs/images/L%C3%A0ng%20Vi%E1%BB%87t%20H%E1%BA%A3i/Viet%20Hai%202.JPG\" style=\"box-sizing: border-box; border: 0px currentcolor; vertical-align: middle; margin: 4px 0px; padding: 0px; max-width: 100%; height: 567px; font-family: arial, helvetica, sans-serif; font-size: 16px; width: 850px;\"></a><br style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-family: arial, helvetica, sans-serif;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; font-size: 16px;\">Tại đây, du khách có thể đi bằng phương tiện xe ôm, xe điện, xe đạp hoặc đi bộ để vào làng. Qua ba cái dốc cắt núi, có chỗ đi dưới những tán cây rừng, làng Việt Hải hiện ra lọt thỏm giữa biển khơi, được bao bọc bốn bề là những cánh rừng già và núi đá vôi cao chót vót như hàng trăm kim tự tháp xanh vây bủa bốn xung quanh.<br style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><a href=\"http://catbafreedom.com/pic/general/images/L%C3%A0ng%20Vi%E1%BB%87t%20H%E1%BA%A3i/Viet%20Hai%203.JPG\" target=\"_self\" style=\"box-sizing: border-box; background: 0px 0px; color: rgb(66, 139, 202); text-decoration: none; margin: 0px; padding: 0px;\"><img alt=\"\" src=\"http://catbafreedom.com/pic/AboutUs/images/L%C3%A0ng%20Vi%E1%BB%87t%20H%E1%BA%A3i/Viet%20Hai%203.JPG\" style=\"box-sizing: border-box; border: 0px currentcolor; vertical-align: middle; margin: 4px 0px; padding: 0px; max-width: 100%; height: 567px; width: 850px;\"></a><br style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Với du khách ưa thích mạo hiểm có thể băng rừng nguyên sinh, xuyên Vườn quốc gia Cát Bà, theo con đường mòn cheo leo qua nhiều dốc đá dựng đứng, những hang núi, khe sâu để đến làng Việt Hải.<br style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><br style=\"box-sizing: border-box; margin: 0px; padding: 0px;\">Việt Hải còn gọi là xã đảo của đảo, hiện có khoảng 90 nóc nhà; nhiều ngôi nhà trong làng là nhà tranh tre, vách đất được làm bằng rơm với bùn đắp lên những ô nứa mắt cáo thành tường vách che mưa, ngăn gió. Tuy không giữ được nhiều nhưng Việt Hải vẫn còn những ngôi nhà cổ kính nằm im lìm dưới rặng nhãn, vườn na, vườn mít trĩu quả; có những cây mít cổ thụ đã mấy trăm năm tuổi. Những ngôi nhà tranh vách đất này là một hình ảnh điển hình của đồng bằng Bắc Bộ ngày xưa, mà nay ít làng xã nào còn giữ được....<br style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><a href=\"http://catbafreedom.com/pic/general/images/L%C3%A0ng%20Vi%E1%BB%87t%20H%E1%BA%A3i/Nh%C3%A0%20c%E1%BB%95%20Vi%E1%BB%87t%20H%E1%BA%A3i.jpg\" target=\"_self\" style=\"box-sizing: border-box; background: 0px 0px; color: unset; text-decoration: underline; margin: 0px; padding: 0px; outline: -webkit-focus-ring-color auto 5px; outline-offset: -2px;\"><img alt=\"\" src=\"http://catbafreedom.com/pic/AboutUs/images/L%C3%A0ng%20Vi%E1%BB%87t%20H%E1%BA%A3i/Nh%C3%A0%20c%E1%BB%95%20Vi%E1%BB%87t%20H%E1%BA%A3i.jpg\" style=\"box-sizing: border-box; border: 0px currentcolor; vertical-align: middle; margin: 4px 0px; padding: 0px; max-width: 100%; height: 567px; width: 850px;\"></a></span></span></p></h1>', 'http://fb.com/dau_tien_lan2', 'bài viết, đầu tiên, bai viet, dau tien, first one', 1, 2, 1, '2020-07-15 10:12:38', '2020-09-15 12:14:06', 0, NULL, NULL, 1, 'Bài M1'),
(2, 'catalog/e35a6f31b18d1ceccd63a6c3fa1b0f93.jpg', 4, 'Top 9  vườn quốc gia nổi tiếng VN', '<h2 style=\"margin-top: 0px; margin-right: 0px; margin-left: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 700; font-stretch: inherit; font-size: 20px; line-height: 20px; font-family: \" roboto=\"\" slab\",=\"\" palatino,=\"\" cambria,=\"\" \"droid=\"\" serif\",=\"\" georgia,=\"\" \"times=\"\" new=\"\" roman\",=\"\" times,=\"\" serif;=\"\" vertical-align:=\"\" baseline;=\"\" color:=\"\" rgb(17,=\"\" 17,=\"\" 17);=\"\" text-rendering:=\"\" optimizelegibility;=\"\" text-align:=\"\" justify;\"=\"\">9 vườn quốc gia ở Việt Nam bạn phải ghé thăm một lần trong đời</h2><h1 class=\"title ct\" style=\"font-size: 24px; margin-top: 0px; margin-bottom: 15px; font-family: FuturaHeavy; font-weight: unset; line-height: 24px; padding: 0px;\"><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 14px; line-height: inherit;\" open=\"\" sans\",=\"\" \"helvetica=\"\" neue\",=\"\" calibri,=\"\" \"droid=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" vertical-align:=\"\" baseline;=\"\" color:=\"\" rgb(17,=\"\" 17,=\"\" 17);=\"\" text-align:=\"\" justify;\"=\"\"><strong style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Vườn quốc gia Nam Cát Tiên</strong></p><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 14px; line-height: inherit;\" open=\"\" sans\",=\"\" \"helvetica=\"\" neue\",=\"\" calibri,=\"\" \"droid=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" vertical-align:=\"\" baseline;=\"\" color:=\"\" rgb(17,=\"\" 17,=\"\" 17);=\"\" text-align:=\"\" justify;\"=\"\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Rừng Nam Cát Tiên cách TP HCM khoảng 160 km, nằm trên địa bàn ba tỉnh Đồng Nai, Lâm Đồng và Bình Phước. Để đến Nam Cát Tiên, bạn đi theo quốc lộ 1A đến ngã ba Dầu Giây, rẽ trái theo quốc lộ 20 (đường lên Đà Lạt) đi khoảng 58 km thì tới ngã ba Tà Lài, rẽ trái và đi tiếp khoảng 24 km là đến Nam Cát Tiên.</span></p><div id=\"attachment_161174\" class=\"wp-caption aligncenter\" style=\"margin: 0.4em auto 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 14px; line-height: inherit;\" open=\"\" sans\",=\"\" \"helvetica=\"\" neue\",=\"\" calibri,=\"\" \"droid=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" vertical-align:=\"\" baseline;=\"\" clear:=\"\" both;=\"\" max-width:=\"\" 96%;=\"\" text-align:=\"\" center;=\"\" color:=\"\" rgb(17,=\"\" 17,=\"\" 17);=\"\" width:=\"\" 1090px;\"=\"\"><img class=\"wp-image-161174 size-full\" title=\"Ảnh:@Lorenzo Kaleolani\" src=\"https://cdn3.ivivu.com/2016/08/nam-cat-tien-ivivu-1.jpg\" alt=\"Ảnh:@Lorenzo Kaleolani\" width=\"1080\" height=\"809\" style=\"margin: 0px auto; padding: 0px; font: inherit; vertical-align: baseline; max-width: 100%; display: block; width: 680.5px;\"></div><div id=\"attachment_161175\" class=\"wp-caption aligncenter\" style=\"margin: 0.4em auto 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 14px; line-height: inherit;\" open=\"\" sans\",=\"\" \"helvetica=\"\" neue\",=\"\" calibri,=\"\" \"droid=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" vertical-align:=\"\" baseline;=\"\" clear:=\"\" both;=\"\" max-width:=\"\" 96%;=\"\" text-align:=\"\" center;=\"\" color:=\"\" rgb(17,=\"\" 17,=\"\" 17);=\"\" width:=\"\" 970px;\"=\"\"><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Nơi đây thu hút du khách thích cảm giác hòa mình với thiên nhiên hoặc phiêu lưu tìm đến nơi bình yên với động thực vật phong phú. Đến Nam Cát Tiên, bạn có thể chọn cho mình hình thức lưu trú là ngủ lều với giá thuê lều dao động từ 80.000 đến 200.000 đồng một chiếc.</span></p><div id=\"attachment_161176\" class=\"wp-caption aligncenter\" style=\"margin: 0.4em auto 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; clear: both; max-width: 96%; width: 760px;\"><img class=\"wp-image-161176 size-full\" title=\"Ảnh:@maimeo169\" src=\"https://cdn3.ivivu.com/2016/08/nam-cat-tien-ivivu-3.jpg\" alt=\"Ảnh:@maimeo169\" width=\"750\" height=\"560\" style=\"margin: 0px auto; padding: 0px; font: inherit; vertical-align: baseline; max-width: 100%; display: block; width: 680.5px;\"><p class=\"wp-caption-text\" style=\"margin-top: 10px; margin-bottom: 1.25em; padding: 0px; border: 0px; font-style: italic; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 0.92857rem; line-height: 1.39286rem; font-family: \" roboto=\"\" slab\",=\"\" palatino,=\"\" cambria,=\"\" \"droid=\"\" serif\",=\"\" georgia,=\"\" \"times=\"\" new=\"\" roman\",=\"\" times,=\"\" serif;=\"\" vertical-align:=\"\" baseline;=\"\" color:=\"\" rgb(102,=\"\" 102,=\"\" 102);\"=\"\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Ảnh:@maimeo16</span></p></div><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Những hoạt động bạn có thể thích thú ở Nam Cát Tiên là đạp xe băng rừng, tham quan ghềnh đá Bến Cự, vườn thực vật, Bàu Sấu, tuyến thác Mỏ Vẹt hay tuyến xem thú vào ban đêm… Khi đi Nam Cát Tiên, bạn nên mang theo những trang phục dài tay, màu tối, mũ mềm, vớ, đèn pin, áo mưa…</span></p><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><strong style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Vườn quốc gia Núi Chúa</strong></p><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Vườn quốc gia Núi Chúa nằm phía Đông Bắc tỉnh Ninh Thuận, thuộc huyện Ninh Hải. Với diện tích gần 30.000 ha cùng chiều dài 57km, Núi Chúa có hệ sinh thái rừng khô hạn đặc trưng hiếm thấy ở nước ta, có nhiều sinh vật quần tụ phong phú và đa dạng. Tuy tại đây ít mưa nhưng lại có “Hồ treo” quanh năm nước trong xanh cùng nhiều con suối lớn như Lồ Ô, Đông Nha, Kiền Kiền không bao giờ cạn nướ</span></p><div id=\"attachment_161183\" class=\"wp-caption aligncenter\" style=\"margin: 0.4em auto 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; clear: both; max-width: 96%; width: 650px;\"><img class=\"wp-image-161183 size-full\" title=\"Ảnh:@dattanpham\" src=\"https://cdn3.ivivu.com/2016/08/nui-chua-ivivu-2.jpg\" alt=\"Ảnh:@dattanpham \" width=\"640\" height=\"640\" style=\"margin: 0px auto; padding: 0px; font: inherit; vertical-align: baseline; max-width: 100%; height: auto; display: block;\"><p class=\"wp-caption-text\" style=\"margin-top: 10px; margin-bottom: 1.25em; padding: 0px; border: 0px; font-style: italic; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 0.92857rem; line-height: 1.39286rem; font-family: \" roboto=\"\" slab\",=\"\" palatino,=\"\" cambria,=\"\" \"droid=\"\" serif\",=\"\" georgia,=\"\" \"times=\"\" new=\"\" roman\",=\"\" times,=\"\" serif;=\"\" vertical-align:=\"\" baseline;=\"\" color:=\"\" rgb(102,=\"\" 102,=\"\" 102);\"=\"\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Ảnh:@dattanpha</span></p></div><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Nơi rừng và biển giao hòa này rất phù hợp cho những chuyến đi dã ngoại. Giữa không gian trầm lắng giữ nguyên vẻ đẹp hoang sơ quyến rũ, bạn sẽ được vùng vẫy trong làn nước mát lạnh, ngắm nhiều cảnh đẹp nên thơ, mạo hiểm trên những vách núi chênh vênh bên bờ biển hoặc khám phá những hang động thiên nhiên tuyệt đẹp. Nếu dựng lều cắm trại qua đêm ở Núi Chúa vào đúng thời điểm, bạn còn có thể tận mắt xem rùa biển đẻ trứng.</span></p><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><strong style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Vườn quốc gia Bạch Mã</strong></p><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Bạch Mã là vườn quốc gia thuộc tỉnh Thừa Thiên – Huế, cách thành phố Huế 40 km. Trước khi trở thành một vườn quốc gia, Bạch Mã được nhiều người quan tâm vì sự nổi tiếng về tài nguyên đa dạng sinh học của nhiều loài động – thực vật quý hiếm.</span></p><div id=\"attachment_161169\" class=\"wp-caption aligncenter\" style=\"margin: 0.4em auto 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; clear: both; max-width: 96%; width: 1090px;\"><img class=\"wp-image-161169 size-full\" title=\"Ảnh:@MrSe\" src=\"https://cdn3.ivivu.com/2016/08/vuon-quoc-gia-bach-ma-ivivu-1.jpg\" alt=\"Ảnh:@MrSe\" width=\"1080\" height=\"810\" style=\"margin: 0px auto; padding: 0px; font: inherit; vertical-align: baseline; max-width: 100%; height: auto; display: block;\"><p class=\"wp-caption-text\" style=\"margin-top: 10px; margin-bottom: 1.25em; padding: 0px; border: 0px; font-style: italic; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 0.92857rem; line-height: 1.39286rem; font-family: \" roboto=\"\" slab\",=\"\" palatino,=\"\" cambria,=\"\" \"droid=\"\" serif\",=\"\" georgia,=\"\" \"times=\"\" new=\"\" roman\",=\"\" times,=\"\" serif;=\"\" vertical-align:=\"\" baseline;=\"\" color:=\"\" rgb(102,=\"\" 102,=\"\" 102);\"=\"\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Ảnh:@MrSe</span></p></div><div id=\"attachment_161170\" class=\"wp-caption aligncenter\" style=\"margin: 0.4em auto 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; clear: both; max-width: 96%; width: 1090px;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\"><br></span></div><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Với những người thích tìm về với thiên nhiên, yêu sự hoang sơ, đơn giản, tĩnh lặng… thì Bạch Mã đúng là vật báu. Một nàng sơn nữ đầy hương sắc càng khiến cho ta say như điếu đổ.</span></p><div id=\"attachment_161171\" class=\"wp-caption aligncenter\" style=\"margin: 0.4em auto 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; clear: both; max-width: 96%; width: 1090px;\"><img class=\"wp-image-161171 size-full\" title=\"Ảnh:@tmtgon\" src=\"https://cdn3.ivivu.com/2016/08/vuon-quoc-gia-bach-ma-ivivu-4.jpg\" alt=\"Ảnh:@tmtgon\" width=\"1080\" height=\"1080\" style=\"margin: 0px auto; padding: 0px; font: inherit; vertical-align: baseline; max-width: 100%; display: block; width: 680.5px;\"></div><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Không chỉ đa dạng về các động vật, nơi đây cuốn hút du khách bởi những thảm thực vật xanh tươi với những loài hoa dân dã mà đẹp lạ.</span></p><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><strong style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Vườn quốc gia Cúc Phương</strong></p><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Vườn quốc gia Cúc Phương nằm trên địa phận 3 tỉnh Ninh Bình, Hòa Bình và Thanh Hóa, là khu bảo tồn thiên nhiên đầu tiên của Việt Nam. Đây là điểm tham quan hấp dẫn khách du lịch trong và ngoài nước về sự đa dạng của các chủng loài trong hệ sinh thái rừng cùng cảnh quan thiên nhiên tươi đẹp.</span></p><div id=\"attachment_161190\" class=\"wp-caption aligncenter\" style=\"margin: 0.4em auto 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; clear: both; max-width: 96%; width: 1090px;\"><img class=\"wp-image-161190 size-full\" title=\"Ảnh:@Jerich Abon \" src=\"https://cdn3.ivivu.com/2016/08/vuon-quoc-gia-cuc-phuong-ivivu-1.jpg\" alt=\"Ảnh:@Jerich Abon \" width=\"1080\" height=\"1080\" style=\"margin: 0px auto; padding: 0px; font: inherit; vertical-align: baseline; max-width: 100%; height: auto; display: block;\"><p class=\"wp-caption-text\" style=\"margin-top: 10px; margin-bottom: 1.25em; padding: 0px; border: 0px; font-style: italic; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 0.92857rem; line-height: 1.39286rem; font-family: \" roboto=\"\" slab\",=\"\" palatino,=\"\" cambria,=\"\" \"droid=\"\" serif\",=\"\" georgia,=\"\" \"times=\"\" new=\"\" roman\",=\"\" times,=\"\" serif;=\"\" vertical-align:=\"\" baseline;=\"\" color:=\"\" rgb(102,=\"\" 102,=\"\" 102);\"=\"\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Ảnh:@Jerich Ab</span></p></div><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Tham quan vườn quốc gia Ba Vì là dịp để bạn len lỏi qua vạt cây rừng, tìm nét hoang sơ của thiên nhiên. Bạn có thể khám phá thác nước khuất sâu trong núi, hay chiêm ngưỡng cảnh mây mù bao phủ như chốn thần tiên trên đỉnh Vọng Cảnh, hoặc đến với khu bảo tồn động vật, ngắm những loài chim nhiều màu sắc và thăm vườn dược liệu với những loài cây quý hiếm.</span></p><div id=\"attachment_161198\" class=\"wp-caption aligncenter\" style=\"margin: 0.4em auto 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; clear: both; max-width: 96%; width: 1090px;\"></div><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">biệt từ giữa tháng 11 đến đầu tháng 12 bạn có thể tới đây ngắm hoa dã quỳ.</span></p><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><strong style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Vườn quốc gia Ba Bể</strong></p><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Vườn quốc gia Ba Bể nằm trên địa bàn 5 xã Nam Mẫu, Khang Ninh, Cao Thương, Quảng Khê, Cao Trĩ thuộc huyện Ba Bể, tỉnh Bắc Kạn. Vườn quốc gia này cách thị xã Bắc Kạn 50 km và Hà Nội 250 km về phía Bắc. Nơi đây là một điểm du lịch sinh thái lý tưởng với phong cảnh kỳ thú và sự đa dạng sinh học.</span></p><div id=\"attachment_161201\" class=\"wp-caption aligncenter\" style=\"margin: 0.4em auto 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; clear: both; max-width: 96%; width: 1090px;\"></div><div id=\"attachment_161202\" class=\"wp-caption aligncenter\" style=\"margin: 0.4em auto 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; clear: both; max-width: 96%; width: 1090px;\"><img class=\"wp-image-161202 size-full\" title=\"Ảnh:@kend.pdm\" src=\"https://cdn3.ivivu.com/2016/08/vuon-quoc-gia-ba-be-ivivu-2.jpg\" alt=\"Ảnh:@kend.pdm\" width=\"1080\" height=\"1080\" style=\"margin: 0px auto; padding: 0px; font: inherit; vertical-align: baseline; max-width: 100%; height: auto; display: block;\"><p class=\"wp-caption-text\" style=\"margin-top: 10px; margin-bottom: 1.25em; padding: 0px; border: 0px; font-style: italic; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 0.92857rem; line-height: 1.39286rem; font-family: \" roboto=\"\" slab\",=\"\" palatino,=\"\" cambria,=\"\" \"droid=\"\" serif\",=\"\" georgia,=\"\" \"times=\"\" new=\"\" roman\",=\"\" times,=\"\" serif;=\"\" vertical-align:=\"\" baseline;=\"\" color:=\"\" rgb(102,=\"\" 102,=\"\" 102);\"=\"\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\"><br></span></p></div><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Đến với hồ Ba Bể, hẳn ai cũng muốn đến với Ao Tiên. Cái tên “Ao Tiên” vốn đã gắn với câu chuyện truyền thuyết về người thợ săn trong lúc qua Ao Tiên đã bị hút hồn bởi vẻ đẹp kiều diễm của các nàng tiên đang tắm trong ao. Ao Tiên nằm ở góc hồ ba của hồ Ba Bể, là một hồ nước nhỏ nằm lọt giữa thung lũng của những cánh rừng nguyên sinh, có những mạch ngầm thông nhau giữa Ao Tiên và hồ Ba Bể. Đây chính là nét độc đáo của Ao Tiên, nơi đây còn là nơi cư trú của nhiều loài động thực vật thủy sinh, cũng như nét đặc trưng khác của vườn Quốc gia Ba Bể.</span></p><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><strong style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Vườn quốc gia Cát Bà</strong></p><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Vườn quốc gia Cát Bà là một điểm du lịch sinh thái hấp dẫn và đặc biệt thu hút đối với những du khách thích tìm tòi phiêu lưu mạo hiểm. Với cảnh quan thiên nhiên hùng vĩ, núi non trùng điệp cùng hệ sinh thái rừng phong phú, đa dạng nên nơi đây được UNESCO công nhận là khu dự trữ sinh quyển thế giới.</span></p><div id=\"attachment_161205\" class=\"wp-caption aligncenter\" style=\"margin: 0.4em auto 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; clear: both; max-width: 96%; width: 1090px;\"><img class=\"wp-image-161205 size-full\" title=\"Ảnh:@Kurt\" src=\"https://cdn3.ivivu.com/2016/08/vuon-quoc-gia-cat-ba-ivivu-2.jpg\" alt=\"Ảnh:@Kurt\" width=\"1080\" height=\"810\" style=\"margin: 0px auto; padding: 0px; font: inherit; vertical-align: baseline; max-width: 100%; height: auto; display: block;\"><p class=\"wp-caption-text\" style=\"margin-top: 10px; margin-bottom: 1.25em; padding: 0px; border: 0px; font-style: italic; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 0.92857rem; line-height: 1.39286rem; font-family: \" roboto=\"\" slab\",=\"\" palatino,=\"\" cambria,=\"\" \"droid=\"\" serif\",=\"\" georgia,=\"\" \"times=\"\" new=\"\" roman\",=\"\" times,=\"\" serif;=\"\" vertical-align:=\"\" baseline;=\"\" color:=\"\" rgb(102,=\"\" 102,=\"\" 102);\"=\"\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Ảnh:@Kurt</span></p></div><div id=\"attachment_161204\" class=\"wp-caption aligncenter\" style=\"margin: 0.4em auto 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; clear: both; max-width: 96%; width: 650px;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\"><br></span></div><div id=\"attachment_161206\" class=\"wp-caption aligncenter\" style=\"margin: 0.4em auto 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; clear: both; max-width: 96%; width: 1090px;\"><p class=\"wp-caption-text\" style=\"margin-top: 10px; margin-bottom: 1.25em; padding: 0px; border: 0px; font-style: italic; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 0.92857rem; line-height: 1.39286rem; font-family: \" roboto=\"\" slab\",=\"\" palatino,=\"\" cambria,=\"\" \"droid=\"\" serif\",=\"\" georgia,=\"\" \"times=\"\" new=\"\" roman\",=\"\" times,=\"\" serif;=\"\" vertical-align:=\"\" baseline;=\"\" color:=\"\" rgb(102,=\"\" 102,=\"\" 102);\"=\"\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Ảnh:@Chels Chew</span></p></div><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Đến vườn quốc gia Cát Bà, bạn sẽ có dịp tham gia các hoạt động dã ngoại, khám phá thiên nhiên thú vị như: trekking, quan sát các loại linh trưởng, ngắm chim, xem thú ban đêm, tắm biển trên các bãi tắm nguyên sơ hay câu cá giải trí…</span></p><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><strong style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Vườn quốc gia Tràm Chim</strong></p><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Vườn quốc gia Tràm Chim là nơi cư trú của loài sếu đầu đỏ nổi tiếng trên thế giới hứa hẹn là điểm đến hấp dẫn trong hành trình khám phá miền tây. Du khách đến đây sẽ được thư giãn ngắm các loài chim và khung cảnh thiên nhiên hiền hòa.</span></p><div id=\"attachment_161208\" class=\"wp-caption aligncenter\" style=\"margin: 0.4em auto 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; clear: both; max-width: 96%; width: 650px;\"><img class=\"wp-image-161208 size-full\" title=\"Ảnh:@tambui\" src=\"https://cdn3.ivivu.com/2016/08/tram-chim-ivivu-1.jpg\" alt=\"Ảnh:@tambui\" width=\"640\" height=\"640\" style=\"margin: 0px auto; padding: 0px; font: inherit; vertical-align: baseline; max-width: 100%; height: auto; display: block;\"></div><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Tháng 8 đến tháng 11 âm lịch là mùa du lịch chính ở vườn quốc gia Tràm Chim. Không chỉ ngắm cảnh ngập nước xanh tốt điểm xuyết sắc hồng của sen và súng, bạn còn có dịp thưởng thức đặc sản hấp dẫn của miền sông nước như cá lóc đồng nướng trui cuốn với lá sen non hay vạc ra ràng nướng.</span></p><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><strong style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Vườn quốc gia Chư Yang Sin</strong></p><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Vườn quốc gia Chư Yang Sin nằm trên địa phận hai huyện Lắk và Krông Bông của tỉnh Đắk Lắk, cách thành phố Buôn Ma Thuột 60 km về hướng Đông. Nếu bạn muốn tìm một địa điểm du lịch sinh thái hấp dẫn thì nơi đây chính là lựa chọn hoàn hảo.</span></p><div id=\"attachment_161212\" class=\"wp-caption aligncenter\" style=\"margin: 0.4em auto 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; clear: both; max-width: 96%; width: 650px;\"><img class=\"wp-image-161212 size-full\" title=\"Ảnh:@trinhdoandoan\" src=\"https://cdn3.ivivu.com/2016/08/chu-Yang-sin-ivivu-1.jpg\" alt=\"Ảnh:@trinhdoandoan\" width=\"640\" height=\"640\" style=\"margin: 0px auto; padding: 0px; font: inherit; vertical-align: baseline; max-width: 100%; height: auto; display: block;\"><p class=\"wp-caption-text\" style=\"margin-top: 10px; margin-bottom: 1.25em; padding: 0px; border: 0px; font-style: italic; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 0.92857rem; line-height: 1.39286rem; font-family: \" roboto=\"\" slab\",=\"\" palatino,=\"\" cambria,=\"\" \"droid=\"\" serif\",=\"\" georgia,=\"\" \"times=\"\" new=\"\" roman\",=\"\" times,=\"\" serif;=\"\" vertical-align:=\"\" baseline;=\"\" color:=\"\" rgb(102,=\"\" 102,=\"\" 102);\"=\"\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\"><br></span></p></div><p style=\"margin-bottom: 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; text-align: justify;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">Đạp xe đạp địa hình, ngắm rừng thông xanh, tìm hiểu về các loài chim thú hoang dã hay chèo thuyền độc mộc trên sông cho tới tổ chức picnic ngoài trời là những trải nghiệm thú vị bạn chẳng thể bỏ lỡ khi đến đây.</span></p><p class=\"wp-caption-text\" style=\"margin-top: 10px; margin-bottom: 1.25em; padding: 0px; border: 0px; font-style: italic; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 0.92857rem; line-height: 1.39286rem;\" roboto=\"\" slab\",=\"\" palatino,=\"\" cambria,=\"\" \"droid=\"\" serif\",=\"\" georgia,=\"\" \"times=\"\" new=\"\" roman\",=\"\" times,=\"\" serif;=\"\" vertical-align:=\"\" baseline;=\"\" color:=\"\" rgb(102,=\"\" 102,=\"\" 102);\"=\"\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;&nbsp;&nbsp;&nbsp;</span><br></p></div></h1>', '...', 'Cát Bà, hệ sinh thái , rừng quốc gia, vườn quốc gia, sinh thái, vùng, động vật, đảo ', 1, 1, 1, '2020-07-28 15:13:46', '2020-09-15 12:20:32', 0, NULL, NULL, 1, 'Bài M2');
INSERT INTO `post` (`post_id`, `image`, `author_id`, `title`, `content`, `link`, `tag`, `featured`, `sort_order`, `status`, `date_added`, `date_modified`, `parent_id`, `summary`, `sub_title`, `top`, `menu`) VALUES
(3, 'catalog/NuiChuaDongVat/7503d12fe1d65518a06f88b4a6f0d510.jpg', 1, 'Đa dạng sinh học', '<h1 class=\"News_Detail_Title\" style=\"margin-top: 8px; font-size: 20px; color: rgb(140, 40, 3); font-family: \" open=\"\" sans\",=\"\" sans-serif;=\"\" font-weight:=\"\" bold;=\"\" width:=\"\" 827px;=\"\" float:=\"\" left;=\"\" min-height:=\"\" 10px;=\"\" text-align:=\"\" justify;\"=\"\"><span style=\"font-family: inherit;\">Đa dạng sinh học ở Việt Nam: Thực trạng và thách thức bảo tồn</span><br></h1><h1 style=\"margin-top: 10px; font-size: 30px; color: rgb(69, 103, 38); font-family: UVNHongHaHep; text-transform: uppercase; padding-left: 15px;\"><div id=\"ctl00_SPWebPartManager1_g_429f6401_ba7b_49b0_8365_9c8e3d5f6718_ctl00_pnHide\" style=\"color: rgb(0, 0, 0); font-family: Roboto, sans-serif; font-size: 14px; text-align: justify; text-transform: none;\"><div class=\"title\"><div id=\"divArticleDescription3\"></div></div><div class=\"Author_Write\" style=\"float: left; width: 827px; margin-bottom: 10px;\"><div class=\"Author_Write_TG\" style=\"width: 827px; float: left; font-style: italic;\"><p style=\"margin-bottom: 5px; padding: 0px; float: left; width: 827px;\">Trần Văn Bằng</p></div><div class=\"Author_Write_NT\" style=\"width: 827px; float: left; font-style: italic;\"><p style=\"margin-bottom: 5px; padding: 0px; float: left; width: 827px;\">Viện Sinh thái học miền Nam, Viện Hàn lâm KH&amp;CN Việt Nam</p></div></div><div id=\"divArticleDescription1\" style=\"float: left; width: 827px; margin-bottom: 15px; color: rgb(51, 51, 51); font-weight: bold; font-style: italic;\"><p style=\"margin-bottom: 0px; padding: 0px;\">Ngày quốc tế Đa dạng sinh học (22/5) năm 2020 có chủ đề: “Các giải pháp của chúng ta sẵn có ở thiên nhiên”, kêu gọi con người sống hài hoà với thiên nhiên, áp dụng các giải pháp dựa vào thiên nhiên để tạo sự thay đổi tích cực, bảo vệ thiên nhiên, đa dạng sinh học và phát triển bền vững. Ngày môi trường thế giới (5/6) năm 2020 cũng tập trung vào chủ đề đa dạng sinh học. Năm nay cũng là năm quan trọng đối với các quốc gia cam kết bảo tồn và khôi phục đa dạng sinh học, với sự kiện Hội nghị quốc tế COP 15 về đa dạng sinh học - hội nghị bản lề chuẩn bị cho Thập kỷ phục hồi hệ sinh thái của Liên hợp quốc (2021-2030), sẽ được tổ chức vào cuối năm.</p><p style=\"margin-bottom: 0px; padding: 0px;\">Việt Nam được đánh giá là 1 trong 25 quốc gia có sự đa dạng sinh học cao. Tuy nhiên, cũng như nhiều quốc gia khác, chúng ta đang đứng trước không ít thách thức khi mà bảo tồn đa dạng sinh học có liên quan mật thiết tới các vấn đề chính trị, phát triển kinh tế và ổn định xã hội.</p></div><div id=\"divArticleDescription\"></div></div><div id=\"divArticleDescription2\" style=\"line-height: 22px; float: left; color: rgb(0, 0, 0); font-family: Roboto, sans-serif; font-size: 14px; text-align: justify; text-transform: none;\"><p style=\"padding: 0px;\"><span style=\"font-weight: 700;\">Đa dạng sinh học ở Việt Nam</span></p><p style=\"padding: 0px;\">Để hiểu về đa dạng sinh học ở Việt Nam thì trước hết cần hiểu về đa dạng hệ sinh thái (nơi trú ngụ của các loài sinh vật và có sự tương tác với môi trường). Theo các kết quả nghiên cứu, Việt Nam nằm ở vị trí chuyển giao của nhiều luồng sinh vật: phía đông mang các đặc điểm địa sinh học của dãy Hymalaya; phía nam có các kiểu hệ sinh thái tương tự với các hệ sinh thái biển đảo và đất liền của khu vực Đông Nam Á; dãy Trường Sơn là vùng chuyển tiếp giữa kiểu khí hậu nhiệt đới và cận nhiệt đới [ 1]. Bên cạnh các dãy núi, Việt Nam còn có 16 hệ thống sông chính, trong đó có hơn 10 hệ thống sông mà lưu vực có diện tích trên 10.000 km<span style=\"line-height: 0; font-size: 10.5px; vertical-align: baseline; position: relative; top: -0.5em;\">2</span>&nbsp;như hệ thống sông Hồng, sông Cửu Long, sông Đồng Nai... Bên cạnh hệ sinh thái rừng thì Việt Nam còn có nhiều kiểu hệ sinh thái khác như trảng cỏ, đất ngập nước nội địa, đồi cát, bãi bồi ven biển, cửa sông, bãi cỏ biển, rạn san hô và vùng biển sâu [2]. Song song đó còn có các hệ sinh thái nhân tạo như đập nước, đất nông nghiệp, đô thị.</p><p style=\"padding: 0px; text-align: center;\"><em><img alt=\"\" src=\"https://vjst.vn/Images/editor/images/2020/Thang%204/%C4%90a%20d%E1%BA%A1ng%20sinh%20hocjVN_BangTran.JPG\" style=\"border-color: currentcolor; margin-bottom: 5px; max-width: 95%; width: 500px; height: 333px;\"></em></p><p style=\"padding: 0px; text-align: center;\"><em>Chà vá chân nâu (Tên khoa học: Pygathrix nemaeus), một trong những loài linh trưởng đẹp nhất trên thế giới, trên bán đảo Sơn Trà (Ảnh: Trần Văn Bằng).</em></p><p style=\"padding: 0px;\">Đa dạng về hệ sinh thái là cơ sở cho sự đa dạng loài động/thực vật, vi sinh vật. Việt Nam nằm trong khu vực Indo - Burma, là 1 trong 25 điểm nóng về đa dạng sinh học toàn cầu [3]. Đa dạng sinh học ở Việt Nam đứng thứ 16 trên thế giới [4]. Một thống kê chưa đầy đủ vào năm 2011 cho thấy, Việt Nam là nơi trú ngụ của 13.766 loài thực vật, 10.300 loài động vật trên cạn (312 loài thú, 840 loài chim, 167 loài ếch nhái, 317 loài bò sát, trên 7.700 loài côn trùng, và nhiều loài động vật không xương sống khác) [5]. Số loài sinh vật nước ngọt đã được biết đến là hơn 1.438 loài vi tảo, 800 loài động vật không xương sống, 1.028 loài cá nước ngọt. Số lượng loài sinh vật biển được biết là hơn 11.000 loài (6.300 loài động vật đáy, 2.500 loài cá biển, 653 loài rong biển, 657 loài động vật nổi, 537 loài thực vật nổi, 94 loài thực vật ngập mặn, 225 loài tôm biển, 14 loài cỏ biển, 15 loài rắn biển, 25 loài thú biển và 5 loài rùa biển) [5].</p><p style=\"padding: 0px;\">Các con số thống kê nêu trên chưa thực sự phản ánh đầy đủ tính đa dạng sinh học của Việt Nam, khi mà số lượng loài mới được phát hiện không ngừng tăng nhanh trong những năm gần đây. Ví dụ, hơn 18 loài côn trùng mới được công bố vào năm 2017 chỉ từ một nghiên cứu [6], 5 loài mới của một giống thực vật mới cũng đã được mô tả từ một công trình vào năm 2014 [7]. Điều đó chứng minh nguồn tài nguyên về đa dạng loài động, thực vật ở Việt Nam chưa thực sự được hiểu biết đầy đủ.</p><p style=\"padding: 0px;\">Một đặc điểm nổi bật trong nguồn tài nguyên về đa dạng sinh học ở Việt Nam là tính đặc hữu về loài, đồng thời cũng là về nguồn gen quý hiếm. Thống kê từ cơ sở dữ liệu các nhóm động, thực vật cho thấy, Việt Nam có ít nhất 467 loài động vật đặc hữu, cao hơn nhiều so với các quốc gia lân cận như Lào, Campuchia, Thái Lan (bảng 1). Bên cạnh đó, Việt Nam có đến 25 giống thực vật có mạch đặc hữu, trong khi con số này ở Lào là 3 và Campuchia là 1. Rõ ràng nguồn gen thực vật đặc hữu này là nguồn tài nguyên vô cùng quý giá mà Việt Nam có được. Tính đặc hữu ở Việt Nam trong khu hệ động vật đã ngày một gia tăng khi số lượng nghiên cứu về côn trùng được mở rộng ra các nhóm đối tượng khác như Chuồn chuồn (Bộ Odonata), Bọ cánh cứng (Bộ Coleoptera), Bọ ngựa (Bộ bọ ngựa Mantodea), Bọ que (Bộ Phasmatodea). Một số phát hiện điển hình đã gia tăng tính đặc hữu về động vật ở Việt Nam như phát hiện các giống và loài côn trùng mới cho Việt Nam [6, 8-10]. Những nghiên cứu trên đã thêm khẳng định tính độc đáo về đa dạng sinh học của Việt Nam và giá trị to lớn trong sự đa dạng về nguồn gen sinh vật.</p><p style=\"padding: 0px; text-align: center;\"><em>Bảng 1. Thống kê tính đặc hữu về động, thực vật ở Việt Nam so sánh với các quốc gia lân cận.</em></p><p style=\"padding: 0px; text-align: center;\"><img alt=\"\" src=\"https://vjst.vn/Images/editor/images/2020/Thang%204/Bang-1.jpg\" style=\"border-color: currentcolor; margin-bottom: 5px; max-width: 95%; width: 600px; height: 280px;\"></p><p style=\"padding: 0px;\"><span style=\"font-weight: 700;\">Tình trạng bảo tồn</span></p><p style=\"padding: 0px;\">Giá trị đa dạng sinh học cao nhưng hiện trạng tình trạng bảo tồn về đa dạng sinh học cũng là một vấn đề cấp bách của quốc gia. Có thể nói hiện trạng đa dạng sinh học ở Việt Nam chịu nhiều ảnh hưởng bởi lịch sử chiến tranh và nền văn hóa phụ thuộc vào nguồn tài nguyên thiên nhiên. Trong giai đoạn chiến tranh, ít nhất 2,2 triệu ha rừng đã bị ảnh hưởng trong giai đoạn 1943-1973 [11]. Sự suy giảm nguồn tài nguyên rừng tiếp tục diễn ra sau khi chiến tranh kết thúc do nhu cầu phát triển kinh tế [4, 11, 12]. Bên cạnh đó, sử dụng động vật hoang dã để phục vụ nhu cầu sống của cộng đồng dân cư sống dựa vào rừng đã đẩy nhiều loài động vật đứng trước nguy cơ tuyệt chủng, đơn cử như đối với các loài linh trưởng [13]. Bên cạnh đó là nhiều nguyên nhân khác như: chuyển đổi đất khi chưa có đủ luận cứ khoa học, phát triển cơ sở hạ tầng, loài ngoại lai xâm hại, khai thác quá mức nguồn tài nguyên, ô nhiễm môi trường, và áp lực từ việc tăng nhanh dân số [4, 14, 15].</p><p style=\"padding: 0px;\">Từ những hệ quả trên, nhiều loài động, thực vật ở Việt Nam đã được các chuyên gia trong và ngoài nước đánh giá. Kết quả đến năm 2007 cho thấy, có ít nhất 4 loài động vật và 1 loài thực vật đã được xem là tuyệt chủng ở Việt Nam [16] như loài Heo vòi (<em>Tapirus indicus</em>), Tê giác 2 sừng (<em>Dicerorhinus sumatrensis</em>). Gần đây nhất (2012) là sự tuyệt chủng của loài Tê giác 1 sừng (<em>Rhinoceros sondaicus</em>) [17]. Số lượng loài động, thực vật đã tuyệt chủng và đang đứng trước nguy cơ tuyệt chủng tại Việt Nam được đánh giá là 728 loài (trong 855 loài được đánh giá), trong đó có 420 loài thực vật [18] và 308 loài động vật [16]. Một thực tế là công tác đánh giá đã thực hiện khá lâu và hiện trạng của nhiều loài cũng đã thay đổi. Do vậy, cập nhật lại tình hình bảo tồn của các loài sinh vật ở Việt Nam là nhu cầu cấp thiết, giúp hoạch định các chiến lược ưu tiên bảo tồn về sau.</p><p style=\"padding: 0px; text-align: center;\"><em>Bảng 2. Thống kế số lượng loài động, thực vật đang có nguy cơ tuyệt chủng và đã tuyệt chủng theo Sách đỏ Việt Nam.</em></p><p style=\"padding: 0px; text-align: center;\"><img alt=\"\" src=\"https://vjst.vn/Images/editor/images/2020/Thang%204/Bang-2.jpg\" style=\"border-color: currentcolor; margin-bottom: 5px; max-width: 95%; width: 500px; height: 578px;\"></p><p style=\"padding: 0px;\">Không chỉ vậy, số lượng các loài có nguy cơ tuyệt chủng toàn cầu có phân bố ở Việt Nam hiện cũng rất nhiều. Theo dữ liệu từ Danh lục đỏ IUCN năm 2020 [19], tổng cộng có 6.640 loài sinh vật đã được đánh giá, trong đó có 1.081 loài (gồm 771 loài động vật và 310 loài thực vật) đang có nguy cơ tuyệt chủng, chiếm 16,28% tổng số loài đã được đánh giá (bảng 3). Con số này đã tăng hơn so với tỷ lệ 13% ở năm 2012 [16]. Như vậy có thể thấy rằng số lượng loài động, thực vật cần được bảo tồn ở Việt Nam là rất lớn và có thể xem đây là một thách thức không hề nhỏ đối với công tác bảo tồn đa dạng sinh học ở Việt Nam.</p><p style=\"padding: 0px; text-align: center;\"><em>Bảng 3. Thống kê số lượng loài đang bị đe dọa được đánh giá theo IUCN.</em></p><p style=\"padding: 0px; text-align: center;\"><img alt=\"\" src=\"https://vjst.vn/Images/editor/images/2020/Thang%204/Bang-3.jpg\" style=\"border-color: currentcolor; margin-bottom: 5px; max-width: 95%; width: 500px; height: 502px;\"></p><p style=\"padding: 0px;\">Có thể nói, những vấn đề liên quan đến đa dạng sinh học ở Việt Nam ngày càng trở nên cấp bách. Tín hiệu đáng mừng là công tác bảo tồn đa dạng sinh học đã được Chính phủ nhìn nhận và hoàn thiện khung pháp lý. Đáng kể nhất là Luật Đa dạng sinh học năm 2008, Luật Lâm nghiệp năm 2018, Chiến lược quốc gia về bảo tồn đa dạng sinh học năm 2014. Nhiều kế hoạch hành động bảo tồn cho nhóm loài đang có nguy cơ tuyệt chủng cao đã được đưa ra, như kế hoạch bảo tồn rùa biển, voi, hổ, linh trưởng, thú ăn thịt nhỏ… Và nhiều văn bản pháp luật liên quan đến công tác thực thi pháp luật, bảo tồn đa dạng sinh học như Nghị định 06/2019/NĐ-CP liên quan đến động thực vật rừng ưu tiên bảo tồn, Nghị định 26/2019/NĐ-CP liên quan đến thủy sản ưu tiên bảo tồn.</p><p style=\"padding: 0px;\"><span style=\"font-weight: 700;\">Thách thức bảo tồn đa dạng sinh học</span></p><p style=\"padding: 0px;\">Vai trò của ngành bảo tồn đa dạng sinh học ngày một tăng lên và đã trở thành một phần mở rộng của xã hội, kinh tế, chính trị; đồng thời gắn liền với các chiến dịch nâng cao nhận thức như một phần của nền kinh tế bền vững [20]. Ở Việt Nam, bảo tồn đa dạng sinh học có thể tính từ khi Chính phủ thành lập Vườn quốc gia Cúc Phương (1962), mở đầu cho một hệ thống vườn quốc gia và các rừng đặc dụng ở Việt Nam. Sự hình thành nên hệ thống pháp luật liên quan đến đa dạng sinh học về sau khẳng định hệ thống chính trị của Việt Nam đã đưa bảo tồn đa dạng sinh học là một vấn đề quốc gia, và gắn liền nó với sự phát triển kinh tế. Tuy vậy trong bối cảnh phát triển như hiện nay, bảo tồn đa dạng sinh học ở Việt Nam đang đứng trước nhiều thách thức, đòi hỏi sự chung tay của cả xã hội.</p><p style=\"padding: 0px;\"><em>Đầu tiên là thách thức trong việc giữ ổn định hệ sinh thái hiện có để đảm bảo điều kiện tồn tại và phát triển của các loài sinh vật</em>. Mặc dù Việt Nam đã công bố Chiến lược quốc gia về bảo tồn đa dạng sinh học, quy hoạch hệ thống rừng đặc dụng và các khu bảo tồn biển để bảo tồn đa dạng sinh học đến năm 2020, định hướng đến năm 2030 (Quyết định 45/QĐ-TTg ngày 8/1/2014). Tuy nhiên, có thể thấy diện tích bảo tồn của các khu vực được bảo vệ không thực sự đủ lớn (phần lớn có diện tích dưới 50.000 ha) để đảm bảo cho nhiều loài động vật có kích thước lớn như voi, hổ. Bên cạnh đó, sự tác động do biến đổi khí hậu cũng đe dọa đa dạng sinh học của Việt Nam. Ước tính có ít nhất 38,9% diện tích Đồng bằng sông Cửu Long và 16,8% diện tích Đồng bằng sông Hồng sẽ bị ngập nếu nước biển dâng lên 100 cm [21]. Các hệ sinh thái ven biển ở Việt Nam cũng chịu ảnh hưởng [22]. Rõ ràng đây là một thách thức không hề nhỏ và cần có thêm các nghiên cứu liên quan để từ đó kịp thời đưa ra được các giải pháp thích ứng.</p><p style=\"padding: 0px;\"><em>Thách thức tiếp theo là ổn định được môi trường trong bối cảnh phát triển kinh tế nhanh.&nbsp;</em>Việt Nam được đánh giá có tốc độ phát triển kinh tế nhanh trong khu vực và trên thế giới, bình quân 6,53%/năm trong giai đoạn từ năm 2000-2017 (số liệu của Tổng cục Thống kê). Tuy vậy, cùng với sự phát triển kinh tế, tình trạng ô nhiễm ngày một gia tăng, đe dọa môi trường sống của nhiều loài động, thực vật, đặc biệt nghiêm trọng đối với các thủy vực [23]. Bên cạnh đó, tốc độ phát triển nhanh cũng kéo theo tốc độ chuyển đổi đất và mở rộng các đô thị [24], đã phần nào làm giảm diện tích các sinh cảnh tự nhiên, làm suy giảm không gian sống của các loài động, thực vật hoang dã. Thêm nữa, sự hình thành nên hệ thống cơ sở giao thông và hạ tầng phục vụ phát triển kinh tế đã làm gia tăng khả năng cách ly giữa các khu bảo tồn. Rõ ràng áp lực phát triển kinh tế và áp lực bảo tồn đa dạng sinh học, môi trường đòi hỏi các quốc gia phải thay đổi cách tiếp cận trong phát triển kinh tế, định hướng đến một nền kinh tế phát triển bền vững, và có thể thấy rằng Việt Nam cũng không thể tránh khỏi xu thế đó.</p><p style=\"padding: 0px;\"><em>Thách thức trong nâng cao nhận thức của cộng đồng</em>. Các chiến dịch nâng cao nhận thức là một phần quan trọng trong công tác bảo tồn đa dạng sinh học [21]. Trong những năm gần đây, công tác tăng cường nhận thức cho người dân sinh sống xung quanh các khu rừng đặc dụng (Vườn quốc gia, Khu dự trữ thiên nhiên) đã được quan tâm. Nhưng có thể thấy rằng trình độ dân trí và mức sống của người dân trong các khu vực này là một trở ngại lớn trong việc tiếp nhận kiến thức. Bên cạnh đó, nhận thức liên quan đến bảo tồn đa dạng sinh học ở Việt Nam cũng chưa được chú trọng ở khu vực đô thị, trong khi tiềm lực xã hội huy động cho công tác này tập trung chủ yếu ở đây.</p><p style=\"padding: 0px;\"><em>Thách thức trong việc thực thi các luật liên quan về đa dạng sinh học</em>. Một điều thuận lợi là hệ thống pháp luật của Việt Nam liên quan đến các loài quý hiếm và ưu tiên bảo tồn đã được hình thành, bao gồm cả danh sách loài và hình thức xử phạt. Tuy nhiên, hệ thống luật và chính sách này lại thiếu các hướng dẫn thực thi, còn có sự chồng chéo về trách nhiệm giữa các chính sách và luật trong khi thiếu sự giải thích chi tiết, đã dẫn đến tình trạng xung đột giữa các văn bản pháp luật, gây khó khăn trong quá trình thực thi [2]. Do đó, trong thời gian tới, các dự án hoàn thiện hệ thống cơ sở pháp luật và chính sách liên quan đến đa dạng sinh học của Việt Nam nên là một trong những ưu tiên của quốc gia [2].&nbsp; &nbsp;</p></div></h1><div class=\"news-single\" style=\"padding-left: 15px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 14px;\"=\"\"><div class=\"news-content\"></div></div>', '...', '...', 1, 1, 1, '2020-07-28 15:14:53', '2020-09-15 12:20:53', 0, NULL, NULL, 0, ''),
(4, 'catalog/9454c0bd45ca784272a3b722a9430e46.jpg', 1, 'Nghiên Cứu Khoa Học', '<h1 style=\"margin-top: 10px; font-size: 30px; font-family: UVNHongHaHep; color: rgb(69, 103, 38); text-transform: uppercase; padding-left: 15px;\">NGHIÊN CỨU KHOA HỌC</h1><div class=\"news-single\" style=\"padding-left: 15px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 14px;\"=\"\"><div class=\"news-content\"><div class=\"like-form\"><div id=\"fb-root\"></div><div class=\"fb-like\" data-href=\"https://catbanationalpark.vn/cat-ba-national-park-9-2-2-2\" data-layout=\"button_count\" data-action=\"like\" data-show-faces=\"true\" data-share=\"false\"></div><div class=\"fb-share-button\" data-href=\"https://catbanationalpark.vn/cat-ba-national-park-9-2-2-2\" data-layout=\"button_count\" data-mobile-iframe=\"true\"></div><div id=\"___plusone_0\" style=\"position: absolute; width: 450px; left: -10000px;\"><iframe ng-non-bindable=\"\" frameborder=\"0\" hspace=\"0\" marginheight=\"0\" marginwidth=\"0\" scrolling=\"no\" tabindex=\"0\" vspace=\"0\" width=\"100%\" id=\"I0_1596801330956\" name=\"I0_1596801330956\" src=\"https://apis.google.com/u/0/se/0/_/+1/fastbutton?usegapi=1&amp;size=medium&amp;width=120&amp;hl=vi&amp;origin=https%3A%2F%2Fcatbanationalpark.vn&amp;url=https%3A%2F%2Fcatbanationalpark.vn%2Fcat-ba-national-park-9-2-2-2&amp;gsrc=3p&amp;ic=1&amp;jsh=m%3B%2F_%2Fscs%2Fapps-static%2F_%2Fjs%2Fk%3Doz.gapi.en.cDdhVphf8T0.O%2Fam%3DwQc%2Fd%3D1%2Fct%3Dzgms%2Frs%3DAGLTcCPgxnVLH-m1Wb1NpO4DLY9DNtv-bQ%2Fm%3D__features__#_methods=onPlusOne%2C_ready%2C_close%2C_open%2C_resizeMe%2C_renderstart%2Concircled%2Cdrefresh%2Cerefresh%2Conload&amp;id=I0_1596801330956&amp;_gfid=I0_1596801330956&amp;parent=https%3A%2F%2Fcatbanationalpark.vn&amp;pfname=&amp;rpctoken=19925273\" data-gapiattached=\"true\" style=\"position: absolute; top: -10000px; width: 450px; margin: 0px; border-style: none;\"></iframe></div><div class=\"g-plusone\" data-size=\"medium\" data-width=\"120\" data-gapiscan=\"true\" data-onload=\"true\" data-gapistub=\"true\"></div></div><span class=\"news-time\" style=\"font-style: italic; font-family: Roboto-Regular; display: block; font-size: 13px; margin-top: 5px; margin-bottom: 15px;\">Thời gian: 01/01/1970 | Lượt xem: 1512</span><div class=\"single-content\"><p style=\"vertical-align: inherit; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit;\"><span style=\"font-weight: inherit; vertical-align: inherit; font-family: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit;\">Nghiên cứu khoa học</span></p><p style=\"vertical-align: inherit; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit;\">&nbsp;</p><p style=\"vertical-align: inherit; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit; text-align: justify;\">&nbsp;Công tác nghiên cứu khoa học của Vườn tiến hành với hai mục đích chính là nghiên cứu phục vụ cho công tác bảo tồn và nghiên cứu cho việc phát triển bền vững. &nbsp;</p><p style=\"vertical-align: inherit; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit; text-align: justify;\">+ Ngiên cứu để bảo tồn gồm một số hoạt động như : &nbsp;Nghiên cứu đa dạng sinh học, Nghiên cứu qui hoạch cho công tác bảo tồn; &nbsp;Nghiên cứu các giải pháp phục hồi các loài và các hệ sinh thái đã bị khai thác cạn kiệt, &nbsp;Nghiên cứu ảnh hưởng tiêu cực của các nhân tố kinh tế - xã hội đến việc sử dụng tài nguyên rừng, biển và đề xuất các giải pháp giảm thiểu; Nghiên cứu các giải pháp phòng cháy và chữa cháy rừng; Nghiên cứu các giải pháp phòng trừ sâu, bệnh hại rừng;&nbsp; Giám sát đa dạng sinh học; &nbsp;Nghiên cứu các loài quí hiếm, đặc hữu; &nbsp;Nghiên cứu xây dựng Cơ sở dữ liệu; sưu tầm, bảo quản, trưng bầy mẫu vật; &nbsp;Xuất bản sách và các tài liệu khoa học…</p><p style=\"vertical-align: inherit; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit; text-align: justify;\">+ Nghiên cứu phục vụ phát triển bền vững gồm một số hoạt động chính như: Nghiên cứu qui hoạch và phát triển vùng đệm; &nbsp;Nghiên cứu xây dựng các mô hình sinh kế thay thế, &nbsp;có hiệu quả kinh tế cao , mô hình làng du lịch sinh thái cộng đồng; Nghiên cứu phát triển các loài động thực vật có giá trị kinh tế cao, ứng dụng phát triển và chuyển giao kĩ thuật.</p></div><div class=\"fb-comments\" data-href=\"https://catbanationalpark.vn/cat-ba-national-park-9-2-2-2\" data-width=\"100%\" data-numposts=\"5\"></div></div></div><div class=\"news-list\" style=\"padding-left: 15px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 14px;\"=\"\"><h3 style=\"font-family: UVNHongHaHep; color: rgb(69, 103, 38); text-transform: uppercase;\">TIN LIÊN QUAN</h3></div>', '...', '...', 1, 1, 1, '2020-07-28 15:55:08', '2020-09-15 12:19:12', 1, NULL, NULL, 1, 'Bài M4'),
