INSERT INTO `post` (`post_id`, `image`, `author_id`, `title`, `content`, `link`, `tag`, `featured`, `sort_order`, `status`, `date_added`, `date_modified`, `parent_id`, `summary`, `sub_title`, `top`, `menu`) VALUES
(8, 'catalog/AnhCatBa/6309c285429018a849eef357bd7132ae.jpg', 4, 'Hướng Phát Triển Du Lịch', '<h1 class=\"title ct\" style=\"font-size: 24px; margin-top: 0px; margin-bottom: 15px; font-family: FuturaHeavy; font-weight: unset; line-height: 24px; color: rgb(1, 106, 209); padding: 0px;\"><p class=\"MsoNormal\" style=\"margin-bottom: 0in; line-height: normal;\"><span style=\"font-weight: 700;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\">Hướng phát triển du lịch sinh thái bền vững tại các vườn quốc gia và khu bảo tồn</span></span><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\">&nbsp; &nbsp;<span style=\"font-weight: 700;\"><i>Việt Nam là quốc gia có tiềm năng lớn về phát triển du lịch sinh thái (DLST) vì sự đa dạng sinh học (ĐDSH) với nhiều loài đặc hữu hoang dã, các hệ sinh thái (HST) đặc sắc khác nhau, trong đó, các Vườn quốc gia (VQG) và Khu bảo tồn thiên nhiên (BTTN) đã thực sự trở thành “kho báu” lưu giữ và bảo vệ ĐDSH, phòng hộ, BVMT, phát triển DLST. Hệ thống rừng đặc dụng Việt Nam hiện nay có 164 khu, trong đó 30 VQG và 69 khu BTTN với tiềm năng rất lớn cho phát triển DLST. Tuy nhiên, hoạt động du lịch ở một số VQG và khu BTTN đang có những tác động tiêu cực lên tài nguyên thiên nhiên và văn hóa bản địa.</i></span></span></p><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\"><span style=\"font-weight: 700;\"><i><br></i>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></p><div align=\"center\"><table class=\"MsoNormalTable\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"500\" style=\"background: white; width: 375pt;\"><tbody><tr><td style=\"padding: 0in;\"><p class=\"MsoNormal\" style=\"margin-bottom: 0in; line-height: normal;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\"></span><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p></td></tr><tr><td style=\"padding: 0in;\"><p class=\"MsoNormal\" align=\"center\" style=\"margin-bottom: 7.5pt; text-align: center; line-height: normal;\"><i><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\">Với cảnh sắc thiên nhiên hùng vĩ, VQG Ba Vì là điểm du lịch hấp hẫn giới trẻ trải nghiệm</span></i><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p></td></tr></tbody></table></div><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-weight: 700;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\">&nbsp; &nbsp;Thực trạng hoạt động DLST ở các VQG, Khu BTTN</span></span><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\">&nbsp; &nbsp;Hiện có 3 hình thức tổ chức kinh doanh DLST phổ biến tại các VQG, Khu BTTN như: Tự tổ chức kinh doanh dịch vụ DLST; Cho các tổ chức, cá nhân thuê môi trường rừng để kinh doanh du lịch; Liên kết với các tổ chức, cá nhân để đầu tư phát triển du lịch.</span><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\">&nbsp; &nbsp;Các loại hình và chương trình du lịch mới chỉ tập trung chủ yếu vào một số VQG có điều kiện tiếp cận về cơ sở hạ tầng và vật chất kỹ thuật du lịch tốt. Nhiều VQG, khu BTTN có điều kiện, khả năng tổ chức các loại hình và chương trình du lịch đặc sắc, phong phú nhưng do nằm xa và điều kiện cơ sở hạ tầng giao thông tiếp cận điểm đến còn nhiều khó khăn nên không thu hút được khách du lịch.</span><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\">&nbsp; &nbsp;Theo kết quả nghiên cứu của Hiệp hội VQG và khu BTTN năm 2010, có đến 80% du khách nội địa đến các VQG, khách du lịch quốc tế có xu hướng tăng dần qua các năm. Khách du lịch đến các VQG chủ yếu với mục đích nghỉ ngơi, thư giãn, tham quan thưởng thức phong cảnh tự nhiên và nghiên cứu học tập, do vậy đối tượng khách chủ yếu là những người thích khám phá thiên nhiên, những hộ gia đình, học sinh, sinh viên và các nhà nghiên cứu khoa học. Số lượng khách nội địa từ các doanh nghiệp, nhà máy đến tham quan các VQG, Khu BTTN còn ít.</span><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\">&nbsp; &nbsp;Các VQG, Khu BTTN có tiềm năng phát triển DLST rất lớn nhưng cho đến nay việc phát triển DLST còn hạn chế, chưa tương xứng với tiềm năng mà thiên nhiên ban tặng. DLST phát triển chủ yếu tập trung ở một số VQG có điều kiện tiếp cận giao thông thuận lợi, cơ sở hạ tầng và cơ sở vật chất kỹ thuật tương đối tốt như: VQG Cát Bà, Tam Đảo, Cúc Phương, Ba Vì, Phong Nha - Kẻ Bàng, Cát Tiên... Nhưng ngay ở các VQG có hoạt động du lịch phát triển khá tốt này, loại hình DLST vẫn còn nhiều tồn tại, chưa tuân thủ tuyệt đối các nguyên tắc, quy chuẩn, tiêu chuẩn của DLST, chưa chú trọng các hoạt động giáo dục, BVMT; chưa tuân thủ chặt chẽ các tiêu chí, tiêu chuẩn BVMT bền vững; chưa thu hút sự tham gia của cộng đồng địa phương làm du lịch, phát triển bền vững về sinh thái, môi trường…</span><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\">&nbsp; &nbsp;Ngoài các VQG Phong Nha - Kẻ Bảng và Ba Vì, các nguồn thu từ hoạt động DLST của các VQG và khu BTTN còn lại hết sức hạn chế, chưa đủ bù đắp chi phí và tái đầu tư cho công tác bảo tồn.</span><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\">&nbsp; &nbsp;Tại một số VQG, do sự phát triển nhanh của hoạt động du lịch dẫn đến những tác động tiêu cực lên tài nguyên thiên nhiên và văn hóa bản địa, như: vấn đề rác thải; sự tác động lên cảnh quan; có tình trạng săn bắt chim, thú, bẻ cây, khắc đẽo lên thân cây, vách đá, xâm hại các nhũ đá trong hang động; sự quá tải về lượng du khách đối với một số điểm đến...</span><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\">&nbsp; &nbsp;Nguyên nhân chính là do trình độ nhận thức về BVMT của người dân địa phương và đa số khách tham quan còn hạn chế. Phần lớn các VQG, các khu BTTN chưa có chiến lược và quy hoạch phát triển DLST nên không có định hướng đầu tư, không có kế hoạch, dự án phát triển DLST. Số ít các VQG, khu BTTN đã có quy hoạch phát triển DLST (Côn Đảo, Phong Nha - Kẻ Bàng, Bidoup - Núi Bà, Yok Đôn, Cúc Phương, Cát Tiên, Ba Vì...) nhưng thiếu nguồn lực, tài chính triển khai. Cơ sở hạ tầng, cơ sở vật chất kỹ thuật dịch vụ du lịch, các dịch vụ hỗ trợ du lịch như cung cấp thông tin, y tế, bảo hiểm, vận tải hành khách, ăn uống, vui chơi, giải trí… đảm bảo đúng tiêu chuẩn, quy chuẩn chất lượng đáp ứng tốt yêu cầu của khách DLST còn rất hạn chế và chưa đồng bộ. Công tác phối, kết hợp giữa các cơ quan quản lý, các cấp, tổ chức xã hội nghề nghiệp trong hoạch định, xây dựng chính sách phát triển, quản lý du lịch và DLST còn bất cập. Bên cạnh đó, hệ thống chính sách phát triển du lịch và DLST chưa thống nhất, đồng bộ, dẫn đến tình trạng phát triển tự phát, chồng chéo, nảy sinh những mâu thuẫn giữa phát triển và bảo tồn, giữa kinh tế và môi trường, ảnh hưởng tiêu cực đến môi trường tự nhiên, văn hóa - xã hội.</span><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\">&nbsp; &nbsp;<span style=\"font-weight: 700;\">Đề xuất phát triển DLST bền vững tại các VQG, Khu BTTN</span></span><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\">&nbsp; &nbsp;Để phát triển DLST bền vững ở các VQG, Khu BTTN, Bộ NN&amp;PTNT, UBND các tỉnh/thành phố, Sở NN&amp;PTNT, các VQG và Khu BTTN cần chú ý một số vấn đề trọng tâm như: Xây dựng Chiến lược quốc gia về phát triển DLST bền vững ở các VQG và khu BTTN, làm cơ sở định hướng phát triển trên phạm vi toàn quốc và ở từng địa phương, VQG, Khu BTTN.</span></p><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\"><br></span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<img src=\"http://localhost/images-epj1-blog-parks/catalog/%E1%BA%A3nh%20c%C3%A1t%20b%C3%A0/99159b3d97ab72107a575216b1c8965e.png\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\"><br></span><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p><div align=\"center\"><table class=\"MsoNormalTable\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"500\" style=\"background: white; width: 375pt;\"><tbody><tr><td style=\"padding: 0in;\"><p class=\"MsoNormal\" style=\"margin-bottom: 0in; line-height: normal;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\"></span><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p></td></tr><tr><td style=\"padding: 0in;\"><p class=\"MsoNormal\" align=\"center\" style=\"margin-bottom: 7.5pt; text-align: center; line-height: normal;\"><i><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\">VQG Phong Nha - Kẻ Bàng triển khai nhiều hoạt động giải trí hấp dẫn nhằm thu hút khách du lịch quốc tế</span></i><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p></td></tr></tbody></table></div><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\">&nbsp; &nbsp;&nbsp;Đẩy mạnh quy hoạch phát triển DLST theo các vùng lãnh thổ, đến từng khu rừng đặc dụng, VQG, Khu BTTN là yêu cầu cấp bách nhằm đảm bảo phát triển DLST đi đôi với BVMT, bảo tồn ĐDSH, các giá trị văn hóa, xã hội, nhân văn của các vùng lãnh thổ, các cộng đồng dân cư. Hiện nay mới chỉ có một vài VQG, Khu BTTN có quy hoạch DLST như Phong Nha - Kẻ Bàng, Bến En…, các VQG thuộc Trung ương thì đang có đề án chờ phê duyệt.</span><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\">&nbsp; &nbsp;Cần sửa đổi, bổ sung và hoàn thiện các văn bản luật (Luật Bảo vệ phát triển rừng, Luật ĐDSH), các văn bản dưới Luật có liên quan tới phát triển du lịch, DLST cũng như xây dựng các chính sách tạo hành lang pháp lý và cơ chế rõ ràng, thuận lợi nhất cho các bên tham gia trong phát triển DLST.</span><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\">&nbsp; &nbsp;Đào tạo nâng cao chất lượng và có cơ chế tạo điều kiện làm việc cho nhân lực làm DLST để chuyên nghiệp hóa hoạt động, góp phần tuyên truyền nâng cao ý thức về môi trường, bảo tồn cảnh quan thiên nhiên, HST các VQG, các khu BTTN. DLST là một phương thức đặc biệt của du lịch và nó chỉ thành công khi có đội ngũ có chuyên môn, nghiệp vụ, kỹ năng tốt.</span><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-bottom: 7.5pt; text-align: justify; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span style=\"font-size: 12pt; font-family: Arial, sans-serif; color: rgb(51, 51, 51);\">&nbsp; &nbsp;Ngoài ra, cần có chính sách và quy định đối với các tổ chức kinh doanh du lịch để đảm bảo có sự chia sẻ lợi nhuận bằng vật chất cho cộng đồng địa phương, bảo tồn thiên nhiên và BVMT nơi các tổ chức này khai thác phát triển du lịch. Xây dựng và hoàn thiện cơ sở vật chất kỹ thuật như nhà nghỉ, nhà ăn, trạm dừng chân, chòi quan sát, đường mòn… sao cho thích hợp với nhiệm vụ bảo tồn, không để công tác xây dựng cơ sở vật chất trở thành nguyên nhân của sự tàn phá môi trường.</span><span style=\"font-size: 12pt; font-family: Verdana, sans-serif; color: rgb(51, 51, 51);\"><o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin-bottom: 0in; line-height: normal;\"></p><p class=\"MsoNormal\"><span style=\"font-size: 12pt; line-height: 17.12px;\"><o:p>&nbsp;</o:p></span></p></h1>', '...', '...', 0, 1, 1, '2020-08-07 19:06:11', '2020-09-15 12:18:03', 0, NULL, NULL, 0, ''),
(9, 'catalog/AnhCatBa/841fb6613db924c3a9ca277d912c59d5.jpg', 5, 'Du Lịch Sinh Thái(Phần 1)', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><br></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Một trong những địa điểm tham quan, du lịch nổi tiếng ở rừng Nam Cát Tiên mà bạn nhất định phải tới đó là tuyến du lịch sinh thái. Tại đây du khách không chỉ được hòa mình vào thiên nhiên để thư giãn, mà bạn còn có cơ hội khám phá hơn 300 loại gỗ quý tại tuyến sinh thái này, trong đó chủ yếu là của vùng Đông Nam Bộ. Bên cạnh đó, khi tới đây bạn cũng có thể thuê xe đẹp với mức giá trung bình khoảng 30.000VND/giờ, để khám phá toàn bộ khu rừng cũng là một trải nghiệm vô cùng thú vị và đáng nhớ đấy. Còn nếu bạn muốn tìm hiểu rõ hơn, thì nên thuê hướng dẫn viên du lịch nhé, với giá khoảng 150.000VND/ngày.</span></p></li></ul><p><b style=\"font-weight:normal;\" id=\"docs-internal-guid-a4f0ba6a-7fff-ef6b-16b3-4eb2003de253\"><br></b></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><a href=\"https://dulichfun.com/chia-se-kinh-nghiem-du-lich-rung-nam-cat-tien-2-ngay-1-dem.html#:~:text=M%E1%BB%99t%20trong%20nh%E1%BB%AFng%20%C4%91%E1%BB%8Ba%20%C4%91i%E1%BB%83m,c%E1%BB%A7a%20v%C3%B9ng%20%C4%90%C3%B4ng%20Nam%20B%E1%BB%99\" style=\"text-decoration:none;\"><span style=\"font-size:11pt;font-family:Arial;color:#1155cc;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:underline;-webkit-text-decoration-skip:none;text-decoration-skip-ink:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">https://dulichfun.com/chia-se-kinh-nghiem-du-lich-rung-nam-cat-tien-2-ngay-1-dem.html#:~:text=M%E1%BB%99t%20trong%20nh%E1%BB%AFng%20%C4%91%E1%BB%8Ba%20%C4%91i%E1%BB%83m,c%E1%BB%A7a%20v%C3%B9ng%20%C4%90%C3%B4ng%20Nam%20B%E1%BB%99</span></a><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">.</span></p><p><b style=\"font-weight:normal;\"><br><br></b></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Với tổng diện tích tự nhiên trên 24.300 ha, nằm trên địa giới của 5 xã (Công Hải, Lợi Hải, Phương Hải, Vĩnh Hải, Nhơn Hải) thuộc huyện Ninh Hải - tỉnh Ninh Thuận. Vườn quốc gia Núi Chúa đang thực sự cuốn hút khách du lịch với những giá trị nổi bật về địa hình, địa thế, khí hậu và tính đa dạng sinh học cao. Tuy chưa được phát triển mạnh về du lịch nhưng đã có nhiều nhà kinh doanh \"để mắt\" đầu tư các \"tour\" du lịch sinh thái vào vùng đất này.</span></p></li></ul><p><b style=\"font-weight:normal;\"><br><br></b></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><a href=\"https://phutho.gov.vn/vi/vuon-quoc-gia-nui-chua-ninh-thuan-diem-du-lich-sinh-thai-ly-tuong\" style=\"text-decoration:none;\"><span style=\"font-size:11pt;font-family:Arial;color:#1155cc;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:underline;-webkit-text-decoration-skip:none;text-decoration-skip-ink:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">https://phutho.gov.vn/vi/vuon-quoc-gia-nui-chua-ninh-thuan-diem-du-lich-sinh-thai-ly-tuong</span></a></p><p><b style=\"font-weight:normal;\"><br><br></b></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Cách thành phố Huế khoảng 40 km, vườn quốc gia (VQG) Bạch Mã đang là điểm du lịch sinh thái hấp dẫn du khách, bởi cảnh quan thiên nhiên kỳ vĩ cùng các trải nghiệm đa dạng. Đây là dải rừng nguyên sinh duy nhất của Việt Nam nối ngang đất nước từ biển Đông đến biên giới Việt - Lào.</span></p></li></ul><p><b style=\"font-weight:normal;\"><br></b></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Sau khi đến cổng VQG Bạch Mã, bạn mua vé với giá 60.000 đồng một người lớn và 20.000 đồng mỗi trẻ em. Ngoài đi bộ, cách thuận tiện nhất di chuyển lên đỉnh núi là thuê một ôtô giá 900.000 đồng khứ hồi. Để tiết kiệm chi phí, bạn nên đi theo nhóm, đặt tour trước ở Huế, Đà Nẵng, hoặc đến cổng VQG sớm để xin ghép đoàn.</span></p><p><br></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><a href=\"https://vnexpress.net/mot-ngay-kham-pha-vuon-quoc-gia-bach-ma-4035745.html\" style=\"text-decoration:none;\"><span style=\"font-size:11pt;font-family:Arial;color:#1155cc;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:underline;-webkit-text-decoration-skip:none;text-decoration-skip-ink:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">https://vnexpress.net/mot-ngay-kham-pha-vuon-quoc-gia-bach-ma-4035745.html</span></a></p>', '...', '...', 0, 1, 1, '2020-08-10 12:40:55', '2020-09-15 12:14:27', 0, NULL, NULL, 0, ''),
(10, 'catalog/AnhCatBa/58768034174ee8ca057afe3c0cb365db.jpg', 5, 'Du Lịch SInh Thái(Phần 2)', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Tuyến du lịch sinh thái (phần 2)</span></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Vườn quốc gia Cúc Phương được thành lập ngày 7/7/1962 và là Vườn quốc gia đầu tiên của Việt Nam. Nhờ những lợi thế về cảnh quan thiên nhiên, sự đa dạng về hệ sinh thái, các giá trị văn hoá và lịch sử, Vườn đã trở thành điểm du lịch sinh thái hấp dẫn của nhiều du khách trong và ngoài nước</span></p></li></ul><p><b style=\"font-weight:normal;\" id=\"docs-internal-guid-87b693e7-7fff-7ba4-a36f-8ceaed01d4c8\"><br><br><br></b></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><a href=\"https://dantocmiennui.vn/du-lich-sinh-thai-ben-vung-bai-2-mo-hinh-cua-vuon-quoc-gia-cuc-phuong/178875.html\" style=\"text-decoration:none;\"><span style=\"font-size:11pt;font-family:Arial;color:#1155cc;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:underline;-webkit-text-decoration-skip:none;text-decoration-skip-ink:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">https://dantocmiennui.vn/du-lich-sinh-thai-ben-vung-bai-2-mo-hinh-cua-vuon-quoc-gia-cuc-phuong/178875.html</span></a></p><p><b style=\"font-weight:normal;\"><br></b></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">VƯỜN QUỐC GIA BA VÌ</span></p></li></ul><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">với khí hậu núi cao trong lành và mát mẻ, mang vẻ đẹp hoang sơ và lôi cuốn với màu xanh ngát của núi rừng, cùng những dòng suối nhỏ trong vắt chảy ngang lối đi qua thảm động thực vật phong phú. Vườn Quốc Gia là điểm thu hút mọi du khách khi đến Ba Vì, nơi đây đặc biệt phù hợp cho các hoạt động dã ngoại như đi bộ, teambuilding, cắm trại.</span></p><p><b style=\"font-weight:normal;\"><br><br><br></b></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><a href=\"http://dulich24.com.vn/bai-viet/danh-thang/nhung-diem-du-lich-hap-dan-o-ba-vi-id-1928\" style=\"text-decoration:none;\"><span style=\"font-size:11pt;font-family:Arial;color:#1155cc;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:underline;-webkit-text-decoration-skip:none;text-decoration-skip-ink:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">http://dulich24.com.vn/bai-viet/danh-thang/nhung-diem-du-lich-hap-dan-o-ba-vi-id-1928</span></a></p><p><br></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Hồ Ba Bể là điểm đến hấp dẫn nhất, nổi tiếng nhất trong Tour Hồ Ba Bể - Bắc Kạn, thu hút vô vàn lượt khách thăm quan mỗi năm. Tuy nhiên, Bắc Kạn còn có rất nhiều thắng cảnh tuyệt đẹp khác, nền văn hóa dân tộc muôn màu, đa dạng và hàng loạt những món ăn ẩm thực miền cao đặc sắc....</span></p></li></ul>', '...', '...', 0, 1, 1, '2020-08-10 12:41:18', '2020-09-15 12:14:37', 0, NULL, NULL, 0, ''),
(11, 'catalog/Anh_Dai_Dien/81f8b7e937f733c926382b0708f1436d.jpg', 5, 'Du Lịch Sinh Thái(Phẩn 3)', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Tuyến Du lịch sinh thái (phần 3)</span></p><p><b style=\"font-weight:normal;\" id=\"docs-internal-guid-c491e38e-7fff-3d39-97ad-aa3a6856c0eb\"><br></b></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Đảo Cát Bà là nơi rừng biển gặp nhau tạo nên một quần thể thiên nhiên phong phú với nhiều loài động thực vật quý hiếm. Vườn quốc gia Cát Bà nằm trên đảo Cát Bà, huyện Cát Hải - Thành phố Hải Phòng và cách trung tâm thành phố 60 km. Đây là điểm trekking không thể bỏ qua của các bạn trẻ đam mê khám phá</span></p></li><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><br></li></ul><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><a href=\"https://mytour.vn/location/4659-mot-ngay-trekking-vuon-quoc-gia-cat-ba.html\" style=\"text-decoration:none;\"><span style=\"font-size:11pt;font-family:Arial;color:#1155cc;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:underline;-webkit-text-decoration-skip:none;text-decoration-skip-ink:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">https://mytour.vn/location/4659-mot-ngay-trekking-vuon-quoc-gia-cat-ba.html</span></a></p><p><b style=\"font-weight:normal;\"><br></b></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Khu du lịch Tràm Chim Đồng Tháp là điểm du lịch sinh thái nổi bật của tỉnh Đồng Tháp. Với diện tích lớn thứ 4 ở Việt Nam, khu du lịch Tràm Chim Đồng Tháp hay còn gọi là vườn quốc gia Tràm Chim thu hút rất nhiều khách du lịch tới tham quan mỗi năm không chỉ vì cảnh quan thiên nhiên tươi đẹp và các món ăn ngon mà còn vì nơi đây có rất nhiều loài động, thực vật nằm trong sách Đỏ, nổi bật nhất là loài sếu đầu đỏ vô cùng quý hiếm.</span></p></li></ul><p><b style=\"font-weight:normal;\"><br></b></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><a href=\"https://luhanhvietnam.com.vn/du-lich/tat-tan-tat-kinh-nghiem-kham-pha-khu-du-lich-tram-chim-dong-thap.html\" style=\"text-decoration:none;\"><span style=\"font-size:11pt;font-family:Arial;color:#1155cc;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:underline;-webkit-text-decoration-skip:none;text-decoration-skip-ink:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">https://luhanhvietnam.com.vn/du-lich/tat-tan-tat-kinh-nghiem-kham-pha-khu-du-lich-tram-chim-dong-thap.html</span></a></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Nằm ở điểm cuối cùng của dãy Trường Sơn thuộc khu vực Nam Tây Nguyên và là một phần của Vùng chim đặc hữu của Cao Nguyên Đà Lạt, Vườn Quốc gia Chư Yang Sin có hệ sinh thái thực vật đa dạng, độc đáo và có tính đặc hữu cao.</span></p></li></ul><p><b style=\"font-weight:normal;\"><br></b></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><a href=\"http://baodaklak.vn/channel/9803/201509/gioi-thieu-tuyen-diem-du-lich-sinh-thai-tai-vuon-quoc-gia-chu-yang-sin-2408956/\" style=\"text-decoration:none;\"><span style=\"font-size:11pt;font-family:Arial;color:#1155cc;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:underline;-webkit-text-decoration-skip:none;text-decoration-skip-ink:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">http://baodaklak.vn/channel/9803/201509/gioi-thieu-tuyen-diem-du-lich-sinh-thai-tai-vuon-quoc-gia-chu-yang-sin-2408956/</span></a></p><p><br></p>', '...', '...', 0, 1, 1, '2020-08-10 12:41:39', '2020-09-15 12:14:57', 0, NULL, NULL, 0, ''),
(12, 'catalog/AnhCongVien/fd13470d99b001869cbd1ad120ee3e6c.jpg', 5, 'Các tuyến du lịch sinh thái (phần 1)', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><img src=\"https://i.ytimg.com/vi/B4toLINCEmE/maxresdefault.jpg\" alt=\"Rừng quốc gia Nam Cát Tiên tuyệt đẹp qua góc máy Flycam - YouTube\" style=\"color: rgb(0, 0, 0); font-family: Arial; font-size: 11pt; white-space: pre-wrap; width: 680.5px;\"><br></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Một trong những địa điểm tham quan, du lịch nổi tiếng ở rừng Nam Cát Tiên mà bạn nhất định phải tới đó là tuyến du lịch sinh thái. Tại đây du khách không chỉ được hòa mình vào thiên nhiên để thư giãn, mà bạn còn có cơ hội khám phá hơn 300 loại gỗ quý tại tuyến sinh thái này, trong đó chủ yếu là của vùng Đông Nam Bộ. Bên cạnh đó, khi tới đây bạn cũng có thể thuê xe đẹp với mức giá trung bình khoảng 30.000VND/giờ, để khám phá toàn bộ khu rừng cũng là một trải nghiệm vô cùng thú vị và đáng nhớ đấy. Còn nếu bạn muốn tìm hiểu rõ hơn, thì nên thuê hướng dẫn viên du lịch nhé, với giá khoảng 150.000VND/ngày.</span></p></li></ul><p><b style=\"font-weight:normal;\" id=\"docs-internal-guid-7e06f24e-7fff-f950-3050-0c88194758ad\"><br></b></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><img src=\"https://dulichvietnam.com.vn/kinh-nghiem/wp-content/uploads/2019/06/kinh-nghiem-du-lich-vuon-quoc-gia-nui-chua-1.jpg\" alt=\"Kinh nghiệm du lịch vườn quốc gia Núi Chúa tại Ninh Thuận\"><br></p><p><b style=\"font-weight:normal;\"><br></b></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Với tổng diện tích tự nhiên trên 24.300 ha, nằm trên địa giới của 5 xã (Công Hải, Lợi Hải, Phương Hải, Vĩnh Hải, Nhơn Hải) thuộc huyện Ninh Hải - tỉnh Ninh Thuận. Vườn quốc gia Núi Chúa đang thực sự cuốn hút khách du lịch với những giá trị nổi bật về địa hình, địa thế, khí hậu và tính đa dạng sinh học cao. Tuy chưa được phát triển mạnh về du lịch nhưng đã có nhiều nhà kinh doanh \"để mắt\" đầu tư các \"tour\" du lịch sinh thái vào vùng đất này.</span></p></li></ul><p><b style=\"font-weight:normal;\"><br><br></b></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><img src=\"https://divui.com/blog/wp-content/uploads/2018/04/vuon-quoc-gia-bach-ma-1.jpg\" alt=\"Kinh nghiệm đi vườn quốc gia Bạch Mã Huế\" style=\"width: 680.5px;\"><br></p><p><b style=\"font-weight:normal;\"><br><br></b></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Cách thành phố Huế khoảng 40 km, vườn quốc gia (VQG) Bạch Mã đang là điểm du lịch sinh thái hấp dẫn du khách, bởi cảnh quan thiên nhiên kỳ vĩ cùng các trải nghiệm đa dạng. Đây là dải rừng nguyên sinh duy nhất của Việt Nam nối ngang đất nước từ biển Đông đến biên giới Việt - Lào.</span></p></li></ul>', '...', 'sinh thái, du lịch, vườn quốc gia, rừng', 0, 1, 1, '2020-08-10 17:20:14', '2020-09-15 12:13:18', 0, NULL, NULL, 0, ''),
(13, 'catalog/AnhCongVien/483c0d050ef236a791ffc9e9b2987359.jpg', 5, 'Các tuyến du lịch sinh thái phần 2', '<ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Vườn quốc gia Cúc Phương được thành lập ngày 7/7/1962 và là Vườn quốc gia đầu tiên của Việt Nam. Nhờ những lợi thế về cảnh quan thiên nhiên, sự đa dạng về hệ sinh thái, các giá trị văn hoá và lịch sử, Vườn đã trở thành điểm du lịch sinh thái hấp dẫn của nhiều du khách trong và ngoài nước</span></p></li></ul><p><br></p><p><img src=\"https://phunuvietnam.mediacdn.vn/thumb_w/700/179072216278405120/2020/6/7/vuon-quoc-gia-ba-vi-13-15914907855821697540171-230-0-905-1080-crop-159149079946265579554.jpg\" alt=\"Trốn nóng” cuối tuần ở Vườn Quốc gia Ba Vì » Báo Phụ Nữ Việt Nam\"><br></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size: 11pt; white-space: pre-wrap;\">Với khí hậu núi cao trong lành và mát mẻ, mang vẻ đẹp hoang sơ và lôi cuốn với màu xanh ngát của núi rừng, cùng những dòng suối nhỏ trong vắt chảy ngang lối đi qua thảm động thực vật phong phú. Vườn Quốc Gia là điểm thu hút mọi du khách khi đến Ba Vì, nơi đây đặc biệt phù hợp cho các hoạt động dã ngoại như đi bộ, teambuilding, cắm trại.</span><br></p></li></ul><p><img src=\"https://image.bnews.vn/MediaUpload/Org/2016/11/06/195225_ho-ba-be.jpg\" alt=\"Hồ Ba Bể: Điểm đến hấp dẫn trên cung đường Đông Bắc\"><br></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Hồ Ba Bể là điểm đến hấp dẫn nhất, nổi tiếng nhất trong Tour Hồ Ba Bể - Bắc Kạn, thu hút vô vàn lượt khách thăm quan mỗi năm. Tuy nhiên, Bắc Kạn còn có rất nhiều thắng cảnh tuyệt đẹp khác, nền văn hóa dân tộc muôn màu, đa dạng và hàng loạt những món ăn ẩm thực miền cao đặc sắc....</span></p></li></ul>', '...', '...', 0, 2, 1, '2020-08-10 17:23:40', '2020-09-15 12:13:48', 0, NULL, NULL, 0, ''),
(14, 'catalog/AnhCongVien/60492b987617adf3f34f1520f58e9205.jpg', 5, 'Các tuyến Du lịch sinh thái (phần 3)', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><img src=\"https://img.vtcnew.com.vn/files/lcm/2017/10/06/cf9b9e9d-70fc-4314-9eb3-02b3b8f3766d1-221533.jpg\" alt=\"Cuối tuần đi khám phá Vườn quốc gia Cát Bà tuyệt đẹp trải nghiệm ...\" style=\"width: 680.5px;\"><br></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Đảo Cát Bà là nơi rừng biển gặp nhau tạo nên một quần thể thiên nhiên phong phú với nhiều loài động thực vật quý hiếm. Vườn quốc gia Cát Bà nằm trên đảo Cát Bà, huyện Cát Hải - Thành phố Hải Phòng và cách trung tâm thành phố 60 km. Đây là điểm trekking không thể bỏ qua của các bạn trẻ đam mê khám phá</span></p></li></ul><p><img src=\"https://thamhiemmekong.com/wp-content/uploads/2019/12/vuon-quoc-gia-tram-chim-2.jpg\" alt=\"Khám phá Vườn quốc gia Tràm Chim - Đồng Tháp\" style=\"width: 680.5px;\"><b style=\"font-weight:normal;\"><br></b></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Khu du lịch Tràm Chim Đồng Tháp là điểm du lịch sinh thái nổi bật của tỉnh Đồng Tháp. Với diện tích lớn thứ 4 ở Việt Nam, khu du lịch Tràm Chim Đồng Tháp hay còn gọi là vườn quốc gia Tràm Chim thu hút rất nhiều khách du lịch tới tham quan mỗi năm không chỉ vì cảnh quan thiên nhiên tươi đẹp và các món ăn ngon mà còn vì nơi đây có rất nhiều loài động, thực vật nằm trong sách Đỏ, nổi bật nhất là loài sếu đầu đỏ vô cùng quý hiếm.</span></p></li></ul><p><img src=\"https://thienviettour.vn/upload/images/chu-yang-sin-4(1).jpg\" alt=\"VƯỜN QUỐC GIA CHƯ YANG SIN - VẺ ĐẸP NÚI RỪNG ĐĂK LĂK\" style=\"width: 680.5px;\"><br></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:11pt;font-family:Arial;color:#000000;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:11pt;font-family:Arial;color:#000000;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Nằm ở điểm cuối cùng của dãy Trường Sơn thuộc khu vực Nam Tây Nguyên và là một phần của Vùng chim đặc hữu của Cao Nguyên Đà Lạt, Vườn Quốc gia Chư Yang Sin có hệ sinh thái thực vật đa dạng, độc đáo và có tính đặc hữu cao.</span></p></li></ul>', '...', '...', 0, 3, 1, '2020-08-10 17:26:22', '2020-09-15 12:13:31', 0, NULL, NULL, 0, '');
INSERT INTO `post` (`post_id`, `image`, `author_id`, `title`, `content`, `link`, `tag`, `featured`, `sort_order`, `status`, `date_added`, `date_modified`, `parent_id`, `summary`, `sub_title`, `top`, `menu`) VALUES
(15, 'catalog/AnhCongVien/68066bcc0a9615e07b6d592e09cb484b.jpg', 5, 'Các loại hình du lịch phần 1', '<div><img src=\"https://toplist.vn/images/800px/kinh-nghiem-phuot-vuon-quoc-gia-nam-cat-tien-chi-tiet-nhat-318894.jpg\" alt=\"Top 6 Kinh nghiệm phượt Vườn Quốc gia Nam Cát Tiên chi tiết nhất ...\" style=\"width: 680.5px;\"><span style=\"color: rgb(34, 34, 34); font-family: Arial; font-size: 16px; white-space: pre;\"><br></span></div><div><span id=\"docs-internal-guid-52023d54-7fff-2afb-51e0-78082128d2cc\"><ul style=\"margin-bottom: 0px;\"><li dir=\"ltr\" style=\"list-style-type: disc; font-size: 10pt; font-family: Arial; color: rgb(34, 34, 34); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đi </span><span style=\"font-size: 10pt; font-weight: 700; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">du lịch</span><span style=\"font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> rừng </span><span style=\"font-size: 10pt; font-weight: 700; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nam Cát Tiên</span><span style=\"font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> có 2 </span><span style=\"font-size: 10pt; font-weight: 700; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">hình</span><span style=\"font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> thức chính, đó là </span><span style=\"font-size: 10pt; font-weight: 700; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">hình</span><span style=\"font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> thức đi </span><span style=\"font-size: 10pt; font-weight: 700; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">du lịch</span><span style=\"font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> theo kiểu cắm trại và nghỉ dưỡng. Nếu bạn cắm trại, thì nên mang theo lều, bạt, đồ ăn, thức uống… và thuê bãi để cắm trại với giá dao động khoảng từ 30.000VND – 40.000VND/đêm. (Giá gửi xe ô tô qua đêm </span><span style=\"font-size: 10pt; font-weight: 700; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">ở</span><span style=\"font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"> đây khoảng 150.000VND/đêm).</span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"><br></span></p></li><li dir=\"ltr\" style=\"list-style-type: disc; font-size: 10pt; font-family: Arial; color: rgb(34, 34, 34); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Các loại hình du lịch tạ</span><span style=\"font-size: 10pt; color: rgb(0, 0, 0); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">i Vườn quốc gia Núi Chúa</span></p></li></ul><br><p dir=\"ltr\" style=\"line-height: 1.38; text-align: center; margin-top: 0pt; margin-bottom: 0pt;\">&nbsp;</p><ul style=\"margin-bottom: 0px;\"><li dir=\"ltr\" style=\"list-style-type: disc; font-size: 10pt; font-family: Arial; color: rgb(34, 34, 34); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:9pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đi bộ, leo núi tham quan rừng, xem động vật rừng.</span><span style=\"font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"><span style=\"border:none;display:inline-block;overflow:hidden;width:211px;height:199px;\"><img alt=\"Picture5.png\" src=\"https://lh6.googleusercontent.com/SSxBDjJBzffIWmhVWjrCSkAB5VCpzhLVAeZBuczNh-Ae8bB0fyGmjQ_Ok_Iq4LUolXHm2kXfOiV6MrhbaydkY90QaU13YpePF7lZ_qJagwU_RMPRITjvRnlE9FGggLgdpMnASNrI\" width=\"211\" height=\"199\" style=\"margin-left:0px;margin-top:0px;\"></span></span></p></li></ul><ul style=\"margin-bottom: 0px;\"><li dir=\"ltr\" style=\"list-style-type: disc; font-size: 10pt; font-family: Arial; color: rgb(34, 34, 34); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thăm&nbsp; làng đồng bào dân tộc và tham quan xưởng thủ công mỹ nghệ</span></p></li></ul><ul style=\"margin-bottom: 0px;\"><li dir=\"ltr\" style=\"list-style-type: disc; font-size: 10pt; font-family: Arial; color: rgb(34, 34, 34); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Thăm quan bãi rùa biển đẻ</span></p></li></ul><ul style=\"margin-bottom: 0px;\"><li dir=\"ltr\" style=\"list-style-type: disc; font-size: 10pt; font-family: Arial; color: rgb(34, 34, 34); background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre;\"><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:9pt;\" role=\"presentation\"><span style=\"font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Đi tàu đáy kính xem san hô, thưởng thức&nbsp; cảnh đẹp ven&nbsp; biển</span></p></li></ul><p dir=\"ltr\" style=\"line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;\"><span style=\"font-size: 10pt; font-family: Arial; color: rgb(34, 34, 34); font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"><span style=\"border:none;display:inline-block;overflow:hidden;width:267px;height:204px;\"><img alt=\"Picture2.jpg\" src=\"https://lh6.googleusercontent.com/5XM2DAEuPAUFFpY1xMszbCXDtKgF0x11mtkjwvofrWnNe88FtqwtKuyNJyiE-VGgX-3ccVPX2yvSumrPxFby5XjezL-TP_3vH_XO5bi1BlAvIPyj5bSNQjzcmtjqjIitdznQU07k\" width=\"267\" height=\"204\" style=\"margin-left:0px;margin-top:0px;\"></span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;\">&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;\">&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;\">&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;\"><span style=\"font-size: 10pt; font-family: Arial; color: rgb(34, 34, 34); font-style: italic; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Tàu đáy kính phục vụ khách tham quan du lịch xem san hô tại Vịnh Vĩnh Hy</span></p><p dir=\"ltr\" style=\"line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;\">&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;\">&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;\">&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;\">&nbsp;</p><p dir=\"ltr\" style=\"line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;\">&nbsp;<span style=\"background-color: transparent; color: rgb(34, 34, 34); font-family: Arial; font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Lặn có ống thở xem san hô, du lịch mạo hiểm </span><span style=\"background-color: transparent; color: rgb(34, 34, 34); font-family: Arial; font-size: 10pt; font-style: italic; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;\"><span style=\"background-color: transparent; color: rgb(34, 34, 34); font-family: Arial; font-size: 10pt; font-style: italic; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"><br></span><span style=\"background-color: transparent; color: rgb(34, 34, 34); font-family: Arial; font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Du lịch sinh thái cộng đồng (tìm hiểu về tự nhiên, đời sống, văn hóa của địa phương, homestay)</span><span style=\"background-color: transparent; color: rgb(34, 34, 34); font-family: Arial; font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\"><span style=\"border:none;display:inline-block;overflow:hidden;width:291px;height:208px;\"><img alt=\"Picture1.png\" src=\"https://lh5.googleusercontent.com/h-o8Jys32Md3e4GroMJAU33T7f1CNs3YJo0TuCi6eWZlf4gzhNaNxXf4q-kpzd9RP1rjQTLA00LTYAzfZue7-fJTEmU2apuGFtvofGqqpCfZwhVHbT6gID0Msjyz4y2CKS3Xby7N\" width=\"291\" height=\"208\" style=\"margin-left:0px;margin-top:0px;\"></span></span></p><br></span></div><div><span id=\"docs-internal-guid-52023d54-7fff-2afb-51e0-78082128d2cc\"><br></span></div><div><img src=\"https://www.taidanang.com/wp-content/uploads/2019/06/tong-hop-kinh-nghiem-du-lich-dinh-bach-ma-sieu-chi-tiet-7.jpg\" alt=\"Tổng hợp kinh nghiệm du lịch đỉnh Bạch Mã siêu chi tiết -\" style=\"width: 680.5px;\"><br></div><div><span id=\"docs-internal-guid-52023d54-7fff-2afb-51e0-78082128d2cc\"><ul style=\"margin-bottom: 0px;\"><li dir=\"ltr\" style=\"list-style-type: disc; font-size: 10pt; font-family: Arial; color: rgb(34, 34, 34); background-color: transparent; font-style: italic; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre;\"><p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:11pt;margin-bottom:11pt;\" role=\"presentation\"><span style=\"font-size: 10pt; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Nếu bạn đi về trong ngày thì không cần quá quan tâm đến vấn đề chỗ nghỉ, bạn có thể tham khảo các loại hình lưu trú dưới đây:</span></p></li></ul><p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:11pt;margin-bottom:11pt;\"><span style=\"font-size: 10pt; font-family: Arial; color: rgb(34, 34, 34); font-weight: 700; font-style: italic; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">- </span><span style=\"font-size: 10pt; font-family: Arial; color: rgb(34, 34, 34); font-style: italic; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">Cắm trại: Ở Bạch Mã có rất nhiều bãi trống phẳng, đẹp cho phép bạn cắm trại. Lưu ý khu vực cắm trại nằm ở vị trí Km17 dưới chân núi, bạn phải trả phí cắm trại là 30 ngìn VND cho 1 người lớn.</span></p><p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:11pt;margin-bottom:11pt;\"><span style=\"font-size: 10pt; font-family: Arial; color: rgb(34, 34, 34); font-style: italic; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">- Homestay hoặc nhà nghỉ bình dân dưới chân núi: ở khu vực Km 17 có nhiều nhà nghỉ và homestay của người dân phục vụ du khách giá phòng khoảng 300 nghìn VND / 1 đêm. Những chỗ nghỉ này có phục vụ ăn uống.</span></p><p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;margin-top:11pt;margin-bottom:11pt;\"><span style=\"font-size: 10pt; font-family: Arial; color: rgb(34, 34, 34); font-style: italic; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;\">- Chỗ nghỉ trên khu vực đỉnh núi: Biệt thự Đỗ Quyên, biệt thự Kim Giao, biệt thự Phong Lan.</span></p></span></div>', '...', '...', 0, 4, 1, '2020-08-10 17:33:27', '2020-09-17 21:45:13', 0, NULL, NULL, 0, ''),
(16, 'catalog/AnhCongVien/9bc5d251ebccea1c56d574aaaf385534.jpg', 5, 'Các loại hình du lịch (phần 2)', '<p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><img src=\"https://media.gody.vn/images/ninh-binh/vuon-quoc-gia-cuc-phuong/12-2016/20161214034047-vuon-cuc-phuong-gody%20(11).jpg\" alt=\"Cùng khám phá địa điểm Vườn quốc gia Cúc Phương tại Ninh Bình ...\" style=\"width: 680.5px;\"><br></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><span style=\"font-size:10pt;font-family:Arial;color:#222222;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">-</span><span style=\"font-size:10pt;font-family:Arial;color:#262626;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Ở vườn quốc gia Cúc Phương có 3 khu vực lưu trú, ăn uống, vui chơi giải trí để du khách có thể lựa chọn:</span></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:10pt;font-family:Arial;color:#262626;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:10pt;font-family:Arial;color:#262626;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Khu cổng vườn: có đầy đủ phòng nghỉ từ giá 200.000đ. Ngoài ra ở đây có dịch vụ cho thuê các thiết bị đi rừng, xe đạp leo núi.</span></p></li><li dir=\"ltr\" style=\"list-style-type:disc;font-size:10pt;font-family:Arial;color:#262626;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:10pt;font-family:Arial;color:#262626;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Khu hồ Mạc: ở đây cho thuê nhà sàn tập thể, phòng nghỉ. Đây là khu vực thích hợp cho nhóm đi đông người, bạn có thể đốt lửa trại, ăn uống hát hò, giao lưu văn nghệ tại đây.</span></p></li><li dir=\"ltr\" style=\"list-style-type:disc;font-size:10pt;font-family:Arial;color:#262626;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:10pt;font-family:Arial;color:#262626;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Khu trung tâm: gồm các nhà sàn và căn hộ riêng biệt.</span></p><p dir=\"ltr\" style=\"line-height:1.38;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><br></p><p dir=\"ltr\" style=\"line-height:1.38;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><img src=\"https://wecheckin.b-cdn.net/wp-content/uploads/2018/01/%C4%90%E1%BB%93i-th%C3%B4ng-Ba-V%C3%AC.jpg\" alt=\"Đồi thông Ba Vì - Chuyên Trang Chia Sẻ Kinh Nghiệm Du Lịch Bụi và ...\" style=\"width: 680.5px;\"><br></p></li><li dir=\"ltr\" style=\"list-style-type:disc;font-size:10pt;font-family:Arial;color:#262626;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:10pt;font-family:Arial;color:#222222;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Bên trong quần thể rừng Quốc Gia Ba Vì có rất nhiều điểm du lịch sinh thái, tâm linh, các điểm cắm trại, nghỉ ngơi vui chơi.</span></p></li><li dir=\"ltr\" style=\"list-style-type:disc;font-size:10pt;font-family:Arial;color:#262626;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;text-align: justify;background-color:#ffffff;margin-top:0pt;margin-bottom:11pt;\" role=\"presentation\"><span style=\"font-size:10pt;font-family:Arial;color:#222222;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Ngoài ra ở Ba Vì còn có các khu du lịch sinh thái với nhiều loại dịch vụ du lịch hấp dẫn du khách.</span></p></li></ul><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:10pt;font-family:Arial;color:#262626;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.4727272727272727;text-align: justify;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:10pt;font-family:Arial;color:#262626;background-color:#ffffff;font-weight:400;font-style:italic;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Nằm trong vùng du lịch miền núi Đông Bắc, khu du lịch Ba Bể (huyện Ba Bể, tỉnh Bắc Kạn) có một hệ thống hang động, thác ghềnh đặc sắc và độc đáo với nhiều loại hình du lịch phong phú với gần 20 điểm di tích danh lam thắng cảnh, đã hấp dẫn được đông đảo du khách thập phương đến tham quan, trở thành nơi có một tiềm năng du lịch sinh thái và văn hoá lớn mạnh.</span></p><p dir=\"ltr\" style=\"line-height:1.4727272727272727;text-align: justify;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><br></p></li></ul>', '...', '...', 0, 5, 1, '2020-08-10 17:36:42', '2020-09-17 21:46:02', 0, NULL, NULL, 0, ''),
(17, 'catalog/AnhCongVien/9f788ad300033aa990484229899e8379.jpg', 5, 'Các loại hình du lịch (phần 3)', '<p dir=\"ltr\" style=\"line-height:1.38;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;\"><img src=\"https://dulichdaocatba.com/view/at_vuon-quoc-gia-cat-ba_528f76f8bc9ae38bbf2c0cf114d653db.jpg\" alt=\"Vườn Quốc Gia Cát Bà, Rừng Quốc Gia Cát Bà\" style=\"width: 680.5px;\"><br></p><p dir=\"ltr\" style=\"line-height:1.38;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;\"><span style=\"font-size:10pt;font-family:Arial;color:#212529;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Đến với vườn quốc gia Cát Bà, du khách có thể lựa chọn giữa các tuyến du lịch với các hệ sinh thái rừng khác nhau để dễ dàng cho việc tìm hiểu. Du lịch sinh thái rừng ở vườn quốc gia Cát Bà bạn sẽ được khám phá tài nguyên động thực vật của các kiểu rừng khác nhau&nbsp; như hệ thống rừng nguyên sinh thường xanh mưa ẩm nhiệt đới, rừng ngập mặn, rừng ngập nước trên núi đá vôi hay khu rừng gỗ quý Kim Giao… Du khách cũng có thể lựa chọn&nbsp; tuyến du lịch dài hoặc ngắn để phù hợp với thời gian của mình như:</span></p><p dir=\"ltr\" style=\"line-height:1.38;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;\">&nbsp;<img src=\"https://lh3.googleusercontent.com/proxy/DkDSvlUwticgIM60g5ggXUQHwmOUyX27lWxySKkrnnfHvfp8RRntaXbiXpog8RI7wUiKnyx0-ihDLaq8y2ht-VxIcA9HNFQaB0HVg2y4pM3NHDFVbfYiw9Xq3EL8M9vZnBtzC3S1w-V0diyyGR8W2DoPOe6KrS_lk0BKF6MQkLhUPNlfvag_IJ88eqiq0v9xxDOoUSPKmSsYuNMXh0UjlMgJDVK3HXNhIsLfv3cXAccnbGOCXP7WLH4Ykbz79wyBWN6jd1pttAJkuwj_Mr8-Cq3OU2mjeBu_zlfliVw8xM5IYWlzP7_nXsbe2XOafZiKmfCS4t_8CUypiYNKXIkqk-Q_JgKrswi00TRn-DP0mZFYanc1xdMnPYlwMA\" alt=\"Vườn quốc gia Tràm Chim, Tam Nông - Du lịch Đồng Tháp\" style=\"width: 680.5px;\"></p><p dir=\"ltr\" style=\"line-height:1.38;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;\"><span style=\"font-size:10pt;font-family:Roboto,sans-serif;color:#212529;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Đi tắc ráng xuyên qua những bèo nước, những cánh rừng tràm hai bên. Thỉnh thoảng là những chú chim bay xào xạc các tán cây. Bạn sẽ được chiêm ngưỡng Vườn quốc gia bảo tồn thuộc công ước Ramsar tuyệt vời một cách sống động nhất.</span></p><p dir=\"ltr\" style=\"line-height:1.38;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;\"><img src=\"https://cdn.vntrip.vn/cam-nang/wp-content/uploads/2017/04/anhhh2.jpg\" alt=\"Một vòng khám phá vườn quốc gia Chư Yang Sin - Vntrip.vn\" style=\"width: 680.5px;\">&nbsp;<br></p><p dir=\"ltr\" style=\"line-height:1.38;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;\"><span style=\"font-size:10pt;font-family:Arial;color:#222222;background-color:#ffffff;font-weight:700;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">hình</span><span style=\"font-size:10pt;font-family:Arial;color:#222222;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"> thức </span><span style=\"font-size:10pt;font-family:Arial;color:#222222;background-color:#ffffff;font-weight:700;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">du lịch</span><span style=\"font-size:10pt;font-family:Arial;color:#222222;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"> khác nhau nhưng đầy thú vị như: đi ngắm rừng thông cổ thụ, quần thể pơ mu ngàn năm tuổi, quan sát chim thú hoang dã, hay đạp xe địa </span><span style=\"font-size:10pt;font-family:Arial;color:#222222;background-color:#ffffff;font-weight:700;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">hình</span><span style=\"font-size:10pt;font-family:Arial;color:#222222;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"> theo tuyến đường rừng, chèo thuyền độc mộc trên sông, tới thác vui chơi và tổ chức picnic…</span></p>', '...', '...', 0, 6, 1, '2020-08-10 17:39:39', '2020-09-15 12:02:48', 0, NULL, NULL, 0, ''),
(18, 'catalog/AnhCongVien/60492b987617adf3f34f1520f58e9205.jpg', 5, 'Tiềm năng du lịch phần 1', '<p><img src=\"https://cucthongke.binhdinh.gov.vn/uploads/news/2019_10/bien-cat-tien.jpg\" alt=\"Tiềm năng du lịch ở xã Cát Tiến\" style=\"width: 680.5px;\"><br></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:0pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#707478;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Vườn quốc gia cát tiên được thiên nhiên ưu đãi, là nơi giàu tiềm năng về đa dạng sinh học, có nhiều cảnh quan thiên nhiên kỳ thú, hùng vĩ. Nơi đây còn có nhiều cộng đồng dân tộc sinh sống, tạo nên nét văn hóa phong phú, đa dạng. Hơn nữa, vườn lại có vị trí địa lý thuận lợi, gần các thành phố Iớn và các khu công nghiệp trọng điểm phía Nam, nằm trên tuyến đường du lịch Thành phố Hồ Chí Minh — Đà Lạt — Nha Trang... tất cả đã tạo cho Cát Tiên một tiềm năng du lịch phong phú, đa dạng, tạo sức cuốn hút mạnh mẽ với khách du lịch trong và ngoài nước.</span></p><p><b style=\"font-weight:normal;\" id=\"docs-internal-guid-b04c892a-7fff-8ac6-a36b-297904948ea0\"><img src=\"https://lh3.googleusercontent.com/proxy/6J53Rl4d35lQFH1FsOMH6pxIATwEv3vgTdUux4YIUImWO3cLxg9ci_lXrHE4TDfSgtmYd225j1RxW0FUv6pwu6pg1r_Q5nnRpCuvzGolOQMU-_OUad0DKOdVrfsjzdLnxLkPNBhBQe_VtT6QWqDlPPx3pbQo9xX2s7FUmRg\" alt=\"NTO - Phát triển du lịch sinh thái tại Vườn quốc gia Núi Chúa\" style=\"width: 680.5px;\"><br></b></p><p dir=\"ltr\" style=\"line-height:1.38;background-color:#f7f7f7;margin-top:0pt;margin-bottom:0pt;padding:0pt 0pt 11pt 0pt;\"><span style=\"font-size:12pt;font-family:Roboto,sans-serif;color:#333333;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Điểm thú vị nhất ở Vườn quốc gia Núi Chúa có lẽ là “Hồ treo” trên vách núi đá. Mặt hồ có đường kính 70-80 m, quanh năm có nước trong xanh và nhiều động, thực vật sinh sống.</span></p><p dir=\"ltr\" style=\"line-height:1.38;background-color:#f7f7f7;margin-top:0pt;margin-bottom:0pt;padding:0pt 0pt 11pt 0pt;\"><span style=\"font-size:12pt;font-family:Roboto,sans-serif;color:#333333;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Hồ treo đẹp nhất có lẽ là vào mùa mưa, khi màu xanh của thảm thực vật bao trùm, những vỉa đá nổi lên trên mặt nước như những hòn non bộ xen lẫn những tán cây truông gai, găng néo…</span></p><p dir=\"ltr\" style=\"line-height:1.38;background-color:#f7f7f7;margin-top:0pt;margin-bottom:0pt;padding:0pt 0pt 11pt 0pt;\"><span style=\"font-size:12pt;font-family:Roboto,sans-serif;color:#333333;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Công viên đá của Vườn quốc gia Núi Chúa là nơi giao hòa giữa biển, núi, đất, trời; là bức tranh thiên nhiên hùng vĩ với hàng nghìn tảng đá xếp chồng lên nhau, thách thức thời gian, gió cát mà đứng vững từ ngàn đời.</span></p><p dir=\"ltr\" style=\"line-height:1.38;background-color:#f7f7f7;margin-top:0pt;margin-bottom:0pt;padding:0pt 0pt 11pt 0pt;\"><span style=\"font-size:12pt;font-family:Roboto,sans-serif;color:#333333;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Dưới chân núi, bãi Thịt là nơi duy nhất ở đất liền của Việt Nam có các loài rùa biển tìm đến đẻ trứng như rùa xanh, đồi mồi, đồi mồi dứa.</span></p><p dir=\"ltr\" style=\"line-height:1.38;background-color:#f7f7f7;margin-top:0pt;margin-bottom:0pt;padding:0pt 0pt 11pt 0pt;\"><span style=\"font-size:12pt;font-family:Roboto,sans-serif;color:#333333;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Nằm trong khu vực Vườn quốc gia Núi Chúa, biển Bình Tiên tựa như viên ngọc quý được cất giấu giữa núi rừng. Không như nhiều bãi biển khác, du khách phải vượt qua con đường đèo gian nan rồi mới được hít hà mùi gió biển, đắm mình trong làn nước xanh trong và chiêm ngưỡng toàn cảnh biển trời trong lòng những dãy núi.</span></p><p dir=\"ltr\" style=\"line-height:1.38;background-color:#f7f7f7;margin-top:0pt;margin-bottom:11pt;\">&nbsp;<img src=\"https://www.aseantraveller.net/source/img_news/4704.jpg\" alt=\"KHU DU LỊCH BẠCH MÃ VILLAGE - ĐIỂM ĐẾN THÚ VỊ\" style=\"background-color: rgb(255, 255, 255); width: 680.5px;\"></p><p></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:12pt;font-family:Roboto,sans-serif;color:#333333;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;background-color:#f7f7f7;margin-top:0pt;margin-bottom:11pt;\" role=\"presentation\"><span style=\"font-size:12pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Đến vườn quốc gia Bạch Mã, du khách có dịp khám phá những tuyến đường mòn thiên nhiên kỳ thú, như đường mòn Trĩ Sao dẫn đến thác Trĩ Sao - nơi có nhiều chim trĩ sao sinh sống; đường mòn Đỗ Quyên dẫn đến thác Đỗ Quyên; đường mòn Ngũ Hồ dẫn đến 5 hồ nước nằm nối tiếp nhau được hình thành từ một con suối lớn; đường mòn Vọng Hải Đài dẫn đến đài ngắm cảnh trên đỉnh Bạch Mã. Đứng từ đây, du khách có thể ngắm toàn cảnh đèo Hải Vân, núi Túy Vân, đầm Cầu Hai, cửa Tư Hiền, vịnh Chân Mây sát bờ biển Ðông và cả không gian huyền ảo, lắng đọng của thành phố Huế và thành phố Đà Nẵng lúc đêm về. Ngoài ra, du khách có thể khám phá đường mòn dài 300 m xuyên qua rừng chò đen, nơi có khá nhiều cây cổ thụ, nhiều cây có đường kính trên 1 m và cao tới hơn 30 m. Không chỉ có vậy, vườn quốc gia Bạch Mã còn có khu vườn sưu tập lan – nơi tập trung hàng trăm loài hoa lan với nhiều màu sắc và kiểu dáng độc đáo.</span></p></li></ul>', '...', '...', 0, 1, 1, '2020-08-10 17:51:46', '2020-09-15 12:19:45', 0, NULL, NULL, 0, ''),
(19, 'catalog/Anh_Dai_Dien/060fc6f501edb760ec20d93e48ff960c.jpg', 5, 'Tiềm năng du lịch phần 2', '<h3 style=\"line-height:1.38;background-color:#f7f7f7;margin-top:0pt;margin-bottom:11pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"><img src=\"https://laodongthudo.vn/stores/news_dataimages/baogiay/082015/21/09/2258502d292850e0f185cbdabdf412fa_cuc_phYYng_2.jpg\" alt=\"Du Lịch Cúc Phương: Vẻ đẹp tiềm ẩn \" vườn=\"\" quốc=\"\" gia=\"\" cúc=\"\" phương\"\"=\"\" style=\"width: 680.5px;\"><br></span></h3><h3 style=\"line-height:1.38;background-color:#f7f7f7;margin-top:0pt;margin-bottom:11pt;\"><span style=\"font-size:12pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Cúc phương<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Tính đa ngành: tính đa ngành thể hiện ở đối tượng được khai thác để phục vụ du<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">lịch. Thu nhập xã hội từ du lịch cũng mang lại nguồn thu cho nhiều ngành kinh tế<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">khác nhau thông qua các sản phẩm dịch vụ cung cấp cho khách du lịch.<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">• Tính đa thành phần: biểu hiên ở tính đa dạng trong thành phần khách du lịch,<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">những người phục vụ du lịch, cộng đồng địa phương, các tổ chức chính phủ, các tổ<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">chức tư nhân tham gia vào hoạt động du lịch.<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">• Tính đa mục tiêu: biểu hiện ở những mục đích đa dạng về bảo tồn thiên nhiên,<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">cảnh quan lịch sử - văn hóa, nâng cao chất lượng cuộc sống của khách du lịch và<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">người tham gia hoạt động dịch vụ du lịch, mở rộng giao lưu văn hóa, kinh tế và<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">nâng cao ý thức trách nhiệm của mọi thành viên trong xã hội.<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">• Tính liên vùng: biểu hiện thông qua các tuyến du lịch, với một quần thể các điểm<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">du lịch trong khu vục, một quốc gia hay giữa các quốc gia với nhau.<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">• Tính mùa vụ: biểu hiện ở thời gian diễn ra hoạt động du lịch tập trung ở các cường<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">độ cao trong năm.<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">• Tính chi phí: biểu hiện ở mục đích đi du lịch là hưởng thụ các sản phẩm du lịch<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">chứ không phải với mục đích kiếm tiền.<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">• Tính xã hội hóa: biểu hiện ở việc thu hút toàn bộ mọi thành viên trong xã hội tham<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">gia ( có thể trực tiếp hoặc gián tiếp) vào hoạt động du lịch.<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Bên cạnh các đặc trưng của ngành du lịch nói chung, DLST cũng hàm chứa<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">những đặc trưng riêng, bao gồm:<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">• Tính giáo dục cao về môi trường: DLST hướng con người tiếp cận hơn nữa với<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">các vùng tự nhiên và các khu bảo tồn, nơi có giá trị cao về đa dạng sinh học và rất<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">nhạy cảm về mặt môi trường. Hoạt động du lịch gây nên những áp lực lớn đối với<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">môi trường, và DLST được coi là chìa khóa nhằm cân bằng giữa các mục tiêu phát<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">triển du lịch với việc bảo vệ môi trường.<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">• Góp phần bảo tồn các nguồn tài nguyên thiên nhiên và duy trì tính đa dạng sinh<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">học: Hoạt động DLST có tác động giáo dục con người bảo vệ tài nguyên thiên<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">nhiên và bảo vệ môi trường, qua đó hình thành ý thức bảo vệ các nguồn tài nguyên<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">thiên nhiên cũng như thúc đẩy các hoạt động bảo tồn, đảm bảo yêu cầu phát triển<br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">bền vững.<br></span>&nbsp;<img src=\"https://dulichbavi.net/wp-content/uploads/2017/09/nha-san-Ban-xoi.png\" alt=\"Du Lịch Ba Vì - Bí Ẩn - Tiềm Năng - Đa Dạng Chờ Du Khách Khám Phá\" style=\"width: 680.5px;\"><br><span style=\"font-size:9pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">khai thác tiềm năng môi trường sinh thái tự nhiên của rừng đặc dụng với việc đầu tư cơ sở hạ tầng để kinh doanh dịch vụ DLST và hướng nghiệp; thông qua DLST để giáo dục và nâng cao ý thức cho cộng đồng và khách du lịch tham gia bảo vệ môi trường; tăng cường khả năng bảo vệ nghiêm ngặt hệ sinh thái, tài nguyên đặc dụng, bảo tồn tính đa dạng của Ba Vì.</span></h3><h3 style=\"line-height:1.38;background-color:#f7f7f7;margin-top:0pt;margin-bottom:11pt;\"><span style=\"font-size:9pt;font-family:Arial;color:#333333;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"><img src=\"https://static.tintuc.com.vn/images/ver3/2018/12/12/1544603046157-1544599551148-anhhhbaba.jpg\" alt=\"Homestay- loại hình du lịch giàu tiềm năng tại Ba Bể\" style=\"width: 680.5px;\"><br></span><span style=\"font-size:10.5pt;font-family:Arial;color:#737373;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Bắc Kạn là tỉnh giàu tiềm năng du lịch bởi sự phong phú của tài nguyên, khoáng sản và nền văn hoá đậm đà bản sắc dân tộc miền núi đông bắc Việt Nam. Tại đây có vô số các danh lam thắng cảnh tuyệt đẹp được hình thành từ thiên nhiên như hang động, hồ nước ngọt, rừng nguyên sinh….Cùng nhiều di tích lịch sử lâu đời gắn liền với nhiều sự kiện lịch sử. Cùng với đó tại Bắc Kạn cũng tồn tại vô số các hang động tự nhiên với vẻ đẹp mê hồn đối với các du khách ưa thích khám phá, mạo hiểm.&nbsp; Bắc Kạn còn thu hút du khách bởi hình thức du lịch cộng đồng, sinh sống và tìm hiểu phong tục văn hóa cùng với đồng bào các dân tộc.</span></h3><p><span id=\"docs-internal-guid-0484f1d6-7fff-af35-a566-2ed8e79cb80a\"></span></p>', '...', '...', 0, 1, 1, '2020-08-10 17:54:18', '2020-09-15 12:19:54', 0, NULL, NULL, 0, ''),
(20, 'catalog/AnhDaiDien/4861546f78bf2339b79d69d64624d886.jpg', 5, 'Tiềm năng du lịch phần 3', '<p dir=\"ltr\" style=\"line-height:1.38;background-color:#f7f7f7;margin-top:0pt;margin-bottom:11pt;\"><img src=\"https://thesinhtour.com/wp-content/uploads/2015/04/dao-ngoc-cat-ba.jpeg\" alt=\"Quần đảo Cát Bà tiềm năng du lịch - TheSinhTour\" style=\"width: 680.5px;\"><br></p><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:10.5pt;font-family:Roboto,sans-serif;color:#737373;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;background-color:#f7f7f7;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:10.5pt;font-family:Roboto,sans-serif;color:#737373;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Điểm quy hoạch thứ nhất là địa bàn thị trấn Cát Bà-Khu đô thị Cái Giá – là địa bàn du lịch quan trọng có không gian bao gồm thị trấn Cát Bà được định hướng phát triển trở thành đô thị vệ tinh của thành phố Hải Phòng, đầu mối giao thông du lịch, nơi kết nối Cát Bà với trung tâm thành phố Hải Phòng và các điểm du lịch phụ cận. Đây cũng là trung tâm cung cấp thông tin và các dịch vụ du lịch chủ yếu như lưu trú, dịch vụ mua sắm, dịch vụ ăn uống lớn nhất trên quần đảo này.</span></p><p dir=\"ltr\" style=\"line-height:1.38;background-color:#f7f7f7;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:10.5pt;font-family:Roboto,sans-serif;color:#737373;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"><img src=\"https://cdnmedia.baotintuc.vn/2017/12/27/06/52/chamtrim3.png\" alt=\"Vườn quốc gia Tràm Chim phát triển du lịch theo hướng bền vững ...\" style=\"width: 680.5px;\"><br></span></p></li></ul><ul style=\"margin-top:0;margin-bottom:0;\"><li dir=\"ltr\" style=\"list-style-type:disc;font-size:10.5pt;font-family:Roboto,sans-serif;color:#737373;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;background-color:#f7f7f7;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:13pt;font-family:Arial;color:#191820;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Vườn quốc gia Tràm Chim đã khôi phục môi trường sống tự nhiên cho 13 loài chim quý hiếm của thế giới, đặc biệt là loài sếu đầu đỏ cùng một hệ động thực vật phong phú. Cùng với đó là việc mở ra nhiều tuyến điểm, sản phẩm du lịch mới. Đặc biệt, tuyến du lịch mùa nước nổi đã trở thành điểm đến du lịch sinh thái đặc trưng, tiêu biểu về bảo tồn và khai thác hợp lý tài nguyên đa dạng sinh học. Người dân xung quanh được tạo điều kiện để tăng thêm thu nhập một cách bền vững.</span></p><p dir=\"ltr\" style=\"line-height:1.38;background-color:#f7f7f7;margin-top:0pt;margin-bottom:0pt;\" role=\"presentation\"><span style=\"font-size:13pt;font-family:Arial;color:#191820;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"><img src=\"https://cdnmedia.baotintuc.vn/2017/12/27/06/52/chamtrim3.png\" alt=\"Vườn quốc gia Tràm Chim phát triển du lịch theo hướng bền vững ...\" style=\"width: 680.5px;\"><br></span></p></li><li dir=\"ltr\" style=\"list-style-type:disc;font-size:13pt;font-family:Arial;color:#191820;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;\"><p dir=\"ltr\" style=\"line-height:1.38;background-color:#f7f7f7;margin-top:0pt;margin-bottom:11pt;\" role=\"presentation\"><span style=\"font-size:11.5pt;font-family:Arial;color:#191820;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Với tiềm năng thiên nhiên, văn hóa phong phú, Vườn Quốc gia Chư Yang Sin có thể tổ chức đa dạng các loại hình du lịch như: tham quan nghiên cứu; trekking chinh phục đỉnh Chư Yang Sin 2.442 m; du lịch văn hóa cộng đồng; tham qua rừng và quan sát chim, thú hoang dã...&nbsp;</span></p></li></ul><p dir=\"ltr\" style=\"line-height:1.38;background-color:#f7f7f7;margin-top:0pt;margin-bottom:0pt;padding:0pt 0pt 11pt 0pt;\">&nbsp;</p><p><span id=\"docs-internal-guid-f55a9048-7fff-07e7-bc59-34325128a193\"><br></span></p>', '...', '...', 0, 1, 1, '2020-08-10 17:56:32', '2020-09-15 12:20:22', 0, NULL, NULL, 0, '');
INSERT INTO `post` (`post_id`, `image`, `author_id`, `title`, `content`, `link`, `tag`, `featured`, `sort_order`, `status`, `date_added`, `date_modified`, `parent_id`, `summary`, `sub_title`, `top`, `menu`) VALUES
(21, 'catalog/AnhCongVien/a675455d2d2b25677dfe521254e316ac.jpg', 5, 'Nhiệm Vụ Chung của Tình Nguyện Viên', '<p>Mô tả công việc</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Mục tiêu chính của các tình nguyện viên là hỗ trợ công viên và nhân viên của công viên ở bất cứ đâu và vào bất cứ lúc nào họ có thể. Mọi công việc bên trong khu vực quản lý công viên, và cả công việc khoa học, hợp lý về mặt sinh thái trong khu vực công viên quốc gia được bảo vệ đều có thể được thực hiện bởi các tình nguyện viên. Loại công việc này không thể được lên kế hoạch trước, các tình nguyện viên sẽ phải sẵn sàng vào lúc bắt đầu làm việc lúc 8 giờ sáng và hỏi và tìm việc. Khối lượng công việc còn phụ thuộc vào mùa vụ, vì có những mùa lượng chim nhiều hơn nên lượng khách đặt tour và đến xem các loài chim này ngày càng nhiều. Vì vậy, loại công việc này luôn phụ thuộc vào tình hình thời gian, mùa vụ, v.v.</p><p><br></p><p>Nhiệm vụ và Trách nhiệm của tình nguyện viên:</p><p>* Hỗ trợ nhân viên</p><p>* Đưa ra những ý tưởng mới</p><p>* Dạy tiếng Anh cho phụ nữ và học sinh trung học</p><p><br></p><p>Hỗ trợ cộng đồng địa phương:</p><p>Các hoạt động của vườn quốc gia cũng bao gồm làm việc với cộng đồng địa phương, ví dụ như “câu lạc bộ nấm”. Đây là một dự án do vườn khởi xướng và hỗ trợ, nơi một số nông dân, từ cộng đồng địa phương (chủ yếu ở Giao Thiện) trồng nấm bằng rơm từ thu hoạch lúa, thay thế cho việc khai thác các nguồn lợi thủy sản (sò, ngao, cá, cua, chim, rừng ngập mặn, v.v.) trong vùng lõi quan trọng của Vườn. Nấm mang lại cho người nông dân một nguồn thu nhập quan trọng. Đây là một phần của chương trình sinh kế của các vườn quốc gia nhằm đạt được mục tiêu quan trọng là bảo vệ diện tích rừng ngập mặn khổng lồ và hàng trăm loài chim di cư và nước mà hầu hết chúng đều là loài quý hiếm và cực kỳ nguy cấp.</p><p><br></p><p>Phát triển du lịch sinh thái:</p><p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Làm việc cùng với người dân địa phương và các nhà tổ chức về du lịch sinh thái cũng có thể là một phần công việc. Mục tiêu là thu hút nhiều khách du lịch / du lịch sinh thái hơn đến khu vực này để cải thiện điều kiện sống của người dân và nhấn mạnh tầm quan trọng của vườn quốc gia và hệ sinh thái của nó. Du lịch sinh thái cũng đóng một vai trò quan trọng trong việc bảo vệ vườn quốc gia bằng cách tạo cho người dân cơ hội kiếm tiền mà không lấy tài nguyên ra khỏi vườn.</p>', '...', '...', 0, 1, 1, '2020-08-10 19:57:24', '2020-09-15 12:19:24', 0, NULL, NULL, 0, ''),
(22, 'catalog/AnhCongVien/a8c5b137f0443f9ad30d6978e29bc408.jpg', 5, 'Quy Định Chung về Tình Nguyện Viên', '<p align=\"center\" style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; text-align: center;\"><a name=\"chuong_1_name\" style=\"margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0, 0, 0); cursor: default; max-width: 100%;\"><b style=\"max-width: 100%;\"><span lang=\"VI\" style=\"max-width: 100%; font-size: 12pt;\">QUY ĐỊNH CHUNG</span></b></a></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><b style=\"margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0, 0, 0); cursor: default; max-width: 100%;\"><span lang=\"VI\" style=\"max-width: 100%;\">Điều 1. Phạm vi điều chỉnh</span></b></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">Quyết định này quy định chính sách đối với hoạt động tình nguyện của thanh niên và trách nhiệm của cơ quan, tổ chức, cá nhân liên quan&nbsp;</span>tr<span lang=\"VI\" style=\"max-width: 100%;\">ong việc thực hiện chính sách đối với hoạt động tình nguyện của thanh niên.</span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><b style=\"margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0, 0, 0); cursor: default; max-width: 100%;\"><span lang=\"VI\" style=\"max-width: 100%;\">Điều 2. Đối tượng áp dụng</span></b></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">1. Thanh niên Việt Nam đang sinh sống, lao động và học tập trên lãnh thổ Việt Nam, có năng lực hành vi dân sự đầy đủ theo quy định của pháp luật tham gia hoạt động tình nguyện.</span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">2. Cơ quan, tổ chức và cá nhân liên quan đến hoạt động tình nguyện của thanh niên.</span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><b style=\"margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0, 0, 0); cursor: default; max-width: 100%;\"><span lang=\"VI\" style=\"max-width: 100%;\">Điều 3. Hoạt động tình nguyện của thanh niên</span></b></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">1. Hoạt động tình nguyện của thanh niên bao gồm các&nbsp;</span>l<span lang=\"VI\" style=\"max-width: 100%;\">oại hình sau đây:</span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">a) Hoạt động tình nguyện thực hiện các chương trình, đề án, dự án phát triển kinh tế - xã hội và bảo vệ Tổ quốc từ 24 tháng trở lên được cấp có thẩm quyền phê duyệt;</span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">b) Hoạt động tình nguyện vì cộng đồng và xã hội do Đoàn Thanh niên Cộng sản Hồ Chí Minh hoặc các tổ chức khác của thanh niên tổ chức.</span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">2. Hoạt động tình nguyện của thanh niên thực hiện theo nguyên tắc tự nguyện, nhằm mục đích nhân đạo, mang lại lợi ích cho cộng đồng và xã hội, tuân thủ các quy định của pháp luật, phù hợp với phong tục, tập quán và điều kiện thực tế của địa phương nơi diễn ra hoạt động tình nguyện.</span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><b style=\"margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0, 0, 0); cursor: default; max-width: 100%;\">Điều 4. Chính sách đối với thanh niên trong quá trình tham gia hoạt động tình nguyện</b></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">1. Được tập huấn, bồi dưỡng về kiến thức, nghiệp vụ và kỹ năng hoạt động tình nguyện.</span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">2. Được hưởng tiền bồi dưỡng trong thời gian hoạt động tình nguyện, được trang bị phương tiện làm việc, phương tiện bảo hộ cá nhân (nếu có) bảo đảm an toàn, phù hợp với yêu cầu nhiệm vụ hoạt động tình nguyện.</span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">4. Thanh niên có hành động dũng cảm bị chết hoặc bị thương thuộc một&nbsp;</span>tr<span lang=\"VI\" style=\"max-width: 100%;\">ong các trường hợp theo quy định của pháp luật về ưu đãi người có công với cách mạng thì được Ủy ban nhân dân cấp xã nơi diễn ra hoạt động tình nguyện đề nghị cơ quan có thẩm quyền xem xét, công nhận là liệt sĩ hoặc quyết định được h</span>ưở<span lang=\"VI\" style=\"max-width: 100%;\">ng chính sách như thương binh theo quy định của pháp luật về ưu đãi người có công với cách mạng.</span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">5. Thanh niên có tham gia đóng bảo hiểm xã hội và bảo hiểm y tế thì được hưởng chế độ theo quy định của pháp luật về bảo hiểm xã hội, bảo hiểm y tế.</span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">6. Thanh niên tham gia hoạt động tình nguyện không thuộc đối tượng tham gia bảo hiểm xã hội, bảo hiểm y tế bắt buộc và chưa tham gia bảo hiểm xã hội, bảo hiểm y tế tự nguyện trong khi hoạt động tình nguyện bị tai nạn thì được cơ quan tổ chức hoạt động tình nguyện bảo đảm chính sách sau đây:</span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">a) Trường hợp bị tai nạn dẫn đến suy giảm khả năng lao động từ 5% đến 30% thì được hỗ trợ tối thiểu một nửa chi phí cứu chữa, phục hồi sức khỏe, phục hồi chức năng bị suy giảm;</span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">b) Trường hợp bị tai nạn dẫn đến suy giảm khả năng lao động từ 31% trở lên thì ngoài chế độ hỗ&nbsp;</span>tr<span lang=\"VI\" style=\"max-width: 100%;\">ợ được h</span>ưở<span lang=\"VI\" style=\"max-width: 100%;\">ng theo quy định tại Điểm a Khoản này còn được trợ cấp một lần bằng 03 l</span>ầ<span lang=\"VI\" style=\"max-width: 100%;\">n mức lương cơ sở, sau đó cứ suy giảm thêm 01% thì được hưởng thêm 0,5 lần mức lương cơ sở;</span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">c) Trường hợp bị chết do tai nạn lao động th</span>ì&nbsp;<span lang=\"VI\" style=\"max-width: 100%;\">được trợ cấp một lần bằng 36 lần mức lương cơ s</span>ở.</p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">7. Thanh niên tham gia hoạt động tình nguyện bị chết th</span>ì&nbsp;<span lang=\"VI\" style=\"max-width: 100%;\">được cơ quan tổ chức hoạt động tình nguyện hỗ trợ mai táng và phương tiện đưa về quê qu</span>á<span lang=\"VI\" style=\"max-width: 100%;\">n, gia đình.</span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><b style=\"margin: 0px; padding-left: 0px; padding-right: 0px; color: rgb(0, 0, 0); cursor: default; max-width: 100%;\">Điều 5. Chính sách đối với thanh niên sau khi kết thúc hoạt động tình nguyện</b></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">1. Được hưởng chính sách hỗ trợ đào tạo nghề và tạo việc làm theo quy định của pháp luật về giáo dục nghề nghiệp và quy định của pháp luật về việc làm.</span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">2. Được xem xét, ưu tiên tuyển chọn tham gia các chương trình, đề án, dự án phát triển kinh tế - xã hội và bảo vệ Tổ quốc nếu hoạt động tình nguyện từ 03 tháng trở lên. Trường hợp tham gia nhiều hoạt động tình nguyện theo đợt thì được cộng dồn thời gian tham gia hoạt động tình nguyện.</span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\"></span></p><p style=\"margin-top: 6pt; margin-bottom: 6pt; padding-left: 0px; padding-right: 0px; line-height: 15.6px; max-width: 100%; color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif;\"><span lang=\"VI\" style=\"max-width: 100%;\">5. Thanh niên có thành tích xuất sắc&nbsp;</span>tr<span lang=\"VI\" style=\"max-width: 100%;\">ong hoạt động tình nguyện được khen thưởng theo quy định của pháp luật về thi đua, khen thưởng.</span></p>', '...', '...', 0, 1, 1, '2020-08-10 20:02:41', '2020-09-15 12:19:32', 0, NULL, NULL, 0, ''),
(23, 'catalog/AnhCongVien/9f788ad300033aa990484229899e8379.jpg', 5, 'KẾ HOẠCH TRIỂN KHAI CHƯƠNG TRÌNH “TÌNH NGUYỆN VIÊN” BẢO TỒN RÙA BIỂN NĂM 2020 TẠI VƯỜN QUỐC GIA CÔN ĐẢO, TỈNH BÀ RỊA-VŨNG TÀU', '<h1 class=\"news-title\" style=\"margin-top: 5px; margin-bottom: 15px; font-weight: 700; color: rgb(64, 63, 63); font-size: 22px !important;\">Bà Rịa - Vũng Tàu: Thực hiện chương trình \"Tình nguyện viên\" bảo tồn rùa biển 2020</h1><div class=\"description\" style=\"color: rgb(64, 63, 63); font-weight: bold; margin-bottom: 20px; font-size: 15px !important;\">LNV - Thực hiện ý kiến chỉ đạo của Ủy ban nhân dân tỉnh Bà Rịa – Vũng Tàu, Chương trình “Tình nguyện viên” bảo tồn rùa biển năm 2020 tại Vườn quốc gia Côn Đảo đã được Ban quản lý Vườn quốc gia Côn Đảo xây dựng kế hoạch ngày 22/6/2020. Theo kế hoạch số 30/KH-VQG, chương trình sẽ có 5 đợt kéo dài từ ngày 20/6 đến 31/7/2020 với 100 tình nguyện viên.</div><div class=\"clearfix-5\"></div><div class=\"clearfix-5\"></div><div class=\"content-commom\" style=\"text-align: justify; font-size: 14px; line-height: 20px;\"><span style=\"color: rgb(51, 51, 51); font-family: arial;\">Chương trình “Tình nguyện viên” bảo tồn rùa biển được thành lập với mục đích: Nâng cao nhận thức của cộng đồng về giá trị và tầm quan trọng của bảo tồn biển, bảo tồn rùa biển thông qua việc tạo cơ hội được tham gia vào công tác bảo tồn rùa biển tại Vườn quốc gia Côn Đảo. Đào tạo đội ngũ tình nguyện viên có hiểu biết và kỹ năng có thể giúp đỡ cho lực lượng kiểm lâm thực hiện nhiệm vụ bảo tồn rùa biển. Tăng cường năng lực cho Ban quản lý Vườn quốc gia Côn Đảo trong công tác truyền thông.&nbsp; Đồng thời, đóng góp kỹ năng, tham gia tích cực vào hoạt động truyền thông bảo vệ môi trường biển nói chung và rùa biển nói riêng.</span><div style=\"color: rgb(51, 51, 51); font-family: arial; text-align: center;\"><img alt=\"\" src=\"https://langngheviet.com.vn/upload/images/04-VAN-HOA-XA-HOI/2020/07/2020-7-31-Rua-bien/r%C3%B9a%20bi%E1%BB%83n%201.jpg\" style=\"max-width: 100%; height: 294px; width: 500px; margin: 5px auto !important;\"></div><br style=\"color: rgb(51, 51, 51); font-family: arial; margin-bottom: 15px !important;\"><span style=\"color: rgb(51, 51, 51); font-family: arial;\">Thực hiện theo Công văn số 2547/UBND -VP ngày 15/4/2016 của UBND tỉnh Bà Rịa – Vũng Tàu về việc phối hợp tổ chức Chương trình trình tình nguyện viên bảo tồn rùa biển giai đoạn 2016 - 2020 tại Vườn quốc gia Côn Đảo và Biên bản thỏa thuận ngày 15/01/2020 giữa Ban quản lý Vườn quốc gia Côn Đảo và Tổ chức Bảo tồn thiên nhiên thế giới (IUCN) về việc \"Phối hợp tổ chức Chương trình tình nguyện viên bảo tồn rùa biển năm 2020\", ngày 22/6/2020, Ban quản lý Vườn quốc gia Côn Đảo xây dựng Kế hoạch số 30/KH-VQG triển khai Chương trình tình nguyện viên bảo tồn rùa biển năm 2020. Thời gian thực hiện từ ngày 20/6 đến 31/7/2020 được chia thành 5 đợt: Đợt 1: từ ngày 20/6 – 29/6/2020; Đợt 2: từ ngày 28/6 – 07/7/2020; Đợt 3: từ ngày 06/7 – 15/7/2020; Đợt 4: từ ngày 14/7 – 23/7/2020; Đợt 5: từ ngày 22/7 – 31/7/2020. Và 100 tình nguyện viên tham gia được tổ chức 5 đợt, mỗi đợt 20 tình nguyện viên tham gia.&nbsp;</span><br style=\"color: rgb(51, 51, 51); font-family: arial; margin-bottom: 15px !important;\"><span style=\"color: rgb(51, 51, 51); font-family: arial;\">&nbsp;</span><div style=\"color: rgb(51, 51, 51); font-family: arial; text-align: center;\"><img alt=\"\" src=\"https://langngheviet.com.vn/upload/images/04-VAN-HOA-XA-HOI/2020/07/2020-7-31-Rua-bien/r%C3%B9a%20bi%E1%BB%83n.jpg\" style=\"max-width: 100%; height: 294px; width: 500px; margin: 5px auto !important;\"></div><br style=\"color: rgb(51, 51, 51); font-family: arial; margin-bottom: 15px !important;\"><span style=\"color: rgb(51, 51, 51); font-family: arial;\">Chương trình được thực hiện dưới sự quản lý và giám sát chặt chẽ đảm bảo các yêu cầu về nội quy quản lý bảo tồn rùa biển, Đảm bảo an toàn về con người, tài sản cho các tình nguyện viên tham gia. Các tình nguyện viên là người có quốc tịch Việt Nam, đáp ứng các tiêu chí tuyển chọn theo quy định. Căn cứ vào số lượng rùa biển về đẻ ở các trạm bảo tồn và tình hình thời tiết để phân bổ số lượng tình nguyện viên tham gia ở các trạm bảo tồn: Bảy Cạnh, Hòn Cau, Bãi Dương, Hòn Tài, Tre Lớn. Các tình nguyện viên tại các trạm bảo tồn thực hiện các công việc vệ sinh bãi đẻ của rùa biển, san lấp các tổ đẻ không thành công.; Ghi nhận các thông tin về rùa biển, cứu hộ trứng và rùa con. Tổng kinh phí thực hiện cho 05 đợt năm 2020 là 363.500.000 đồng&nbsp;</span><br style=\"color: rgb(51, 51, 51); font-family: arial; margin-bottom: 15px !important;\"><br style=\"color: rgb(51, 51, 51); font-family: arial; margin-bottom: 15px !important;\"><span style=\"color: rgb(51, 51, 51); font-family: arial;\">Để chương trình được diễn ra thành công theo đúng kế hoạch, Phòng Bảo và Hợp tác quốc tế đảm bảo việc tuyển chọn và phổ biến nội quy, quy chế cho các tình nguyện viên; Hỗ trợ các tình nguyện viên hoàn thiện các thủ tục hành chính khi đến địa phương tham gia; Hướng dẫn tình nguyện viên đến nhận nhiệm vụ và bàn giao cho các Trạm Kiểm lâm; Tham gia họp nhận xét kết quả thực hiện nhiệm vụ bảo tồn rùa biển của tình nguyện viên tại nơi công tác, cấp giấy chứng nhận. Hạt Kiểm lâm thực hiện Chỉ đạo các Trạm kiểm lâm Bảy Cạnh, Trạm Kiểm lâm Hòn Cau, Tổ Kiểm lâm Bãi Dương, Trạm Kiểm lâm Hòn Tài, Trạm kiểm lâm hòn Tre Lớn phối hợp tổ chức thực hiện tốt chương trình tình nguyện viên; Quản lý, phân công nhiệm vụ và hướng dẫn, kiểm tra kết quả thực hiện công tác bảo tồn rùa biển, bảo tồn đa dạng sinh học, bảo vệ môi trường của tình nguyện viên. Phòng Tổ chức – Hành chính thực hiện bố trí phương tiện đưa đón các tình nguyện viên đến các trạm bảo tồn thực hiện nhiệm vụ.</span><br style=\"color: rgb(51, 51, 51); font-family: arial; margin-bottom: 15px !important;\"><br style=\"color: rgb(51, 51, 51); font-family: arial; margin-bottom: 15px !important;\"><span style=\"color: rgb(51, 51, 51); font-family: arial;\">Chương trình đang trong những giai đoạn cuối với mong muốn nâng cao nhận thức cộng đồng về giá trị và tầm quan trọng của bảo tồn biển, bảo tồn rùa biển. Thông qua đó, đào tạo đội ngũ tình nguyện viên có hiểu biết và kỹ năng có thể giúp đỡ cho lực lượng kiểm lâm thực hiện nhiệm vụ bảo tồn rùa biển. Đồng thời, tăng cường năng lực cho Ban quản lý Vườn quốc gia Côn Đảo trong công tác truyền thông.&nbsp;&nbsp;</span></div><div><span style=\"color: rgb(51, 51, 51); font-family: arial;\"><br></span></div>', 'https://langngheviet.com.vn/van-hoa-xa-hoi/ba-ria-vung-tau-thuc-hien-chuong-trinh-tinh-nguyen-vien-bao-ton-rua-bien-2020.html21845', '...', 0, 1, 1, '2020-08-10 20:06:03', '2020-09-15 12:18:12', 0, NULL, NULL, 0, ''),
(24, 'catalog/AnhCongVien/483c0d050ef236a791ffc9e9b2987359.jpg', 5, 'Chương trình tình nguyện viên mùa hè năm 2020, tham gia bảo tồn rùa biển tại Côn Đảo, tỉnh Bà Rịa - Vũng Tàu, Việt Nam', '<div class=\"imgDetail_left\" style=\"margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: \" open=\"\" sans\",=\"\" sans-serif;=\"\" font-size:=\"\" 14px;=\"\" text-align:=\"\" justify;=\"\" width:=\"\" 180px;\"=\"\"><a href=\"http://www.condaopark.com.vn/vnt_upload/conservation/05_2020/rua-de-trung-con-dao-4.jpg\" class=\"thickbox\" title=\"Zoom\" style=\"color: rgb(51, 51, 51); transition-duration: 0.5s; transition-timing-function: ease;\"><img src=\"http://www.condaopark.com.vn/vnt_upload/conservation/05_2020/rua-de-trung-con-dao-4.jpg\" width=\"180\" style=\"max-width: 100%;\"></a></div><p><span style=\"font-weight: 700; color: rgb(51, 51, 51); font-family: \" open=\"\" sans\",=\"\" sans-serif;=\"\" font-size:=\"\" 14px;=\"\" text-align:=\"\" justify;\"=\"\"></span></p><p style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px;\"><span style=\"box-sizing: border-box; font-size: 18px;\"><span style=\"box-sizing: border-box; font-family: \" times=\"\" new=\"\" roman\",=\"\" times,=\"\" serif;\"=\"\"><strong style=\"box-sizing: border-box; font-weight: 700;\">I. Mục tiêu của Chương trình TNV.</strong><br style=\"box-sizing: border-box;\">- Nâng cao nhận thức của cộng đồng về gi trị và tầm quan trọng của bảo tồn biển, bảo tồn rùa biển thông qua việc tạo cơ hội được tham gia vào công tác tuần tra, bảo vệ rùa biển tại các bãi đẻ của rùa biển&nbsp;tại Vườn quốc gia Côn Đảo;<br style=\"box-sizing: border-box;\">- Đào tạo đội ngũ tình nguyện viên có hiểu biết và kỹ năng, có thể giúp đỡ cán bộ chuyên môn tại các khu bảo tồn biển trong công tác bảo tồn rùa biển;<br style=\"box-sizing: border-box;\">- Tăng cường năng lực cho các khu bảo tồn biển, công tác truyền thông;<br style=\"box-sizing: border-box;\">- Đóng góp kỹ năng, tham gia tích cực vào hoạt động truyền thông bảo vệ môi trường biển nói chung và rùa biển nói riêng;<br style=\"box-sizing: border-box;\">- Trở thành sứ giả của đại dương cùng đồng hành với các VQG, KBTB và các Tổ chức bảo vệ môi trường.<br style=\"box-sizing: border-box;\"><strong style=\"box-sizing: border-box; font-weight: 700;\">II. Thời gian thực hiện năm 2020</strong><br style=\"box-sizing: border-box;\">- Mùa sinh sản của rùa biển tại Việt Nam kéo dài từ tháng 4 đến tháng 11 hàng năm, trong đó thời điểm có số lượng rùa biển lên đẻ nhiều nhất từ tháng 6 đến 9. Do đó, chương trình TNV sẽ được thực hiện vào tháng 6 và tháng 7.<br style=\"box-sizing: border-box;\">+ Theo yêu cầu ngày càng tăng, năm 2020 VQG Côn Đảo đồng ý sẽ tiếp nhận 05 đợt TNV, mỗi đợt có tối đa 20 TNV được lựa chọn, TNV đăng ký chương trình 10 ngày. Mỗi TNV đưa ra 1 lựa chọn tham gia 1 chương trình. Sẽ ưu tiên lựa chọn những TNV đăng ký sớm. Lưu ý: chương trình 10 ngày đã tính cả thời gian TNV đến Côn Đảo (ngày 1) và trở về (ngày thứ 10). Ví dụ chương trình từ 20/6 – 29/6 thì TNV cần có mặt tại Côn Đảo vào ngày 19/6, các hoạt động sẽ bắt đầu diễn ra từ ngày 20/6 - 29/6 và TNV sẽ trở về từ các đảo nhỏ đến Côn Sơn và trở về địa phương vào ngày 29/6. - Đợt 1: từ ngày 20/6 – 29/6/2020 - Đợt 2: từ ngày 28/6 – 07/7/2020 - Đợt 3: từ ngày 06/7 – 15/7/2020 - Đợt 4: từ ngày 14/7 – 23/7/2020 - Đợt 5: từ ngày 22/7 – 31/7/2020<br style=\"box-sizing: border-box;\">Lưu ý: Vì các đợt TNV được tổ chức liên tục nên trong điều kiện thời tiết không thuận lợi, các TNV phải ở lại đảo nhỏ hoặc đảo chính Côn Sơn một vài ngày để đợi thời tiết tốt hơn.<br style=\"box-sizing: border-box;\">Các TNV trước khi đăng ký nên cam kết chắc chắn tham gia, khi các bạn hủy sẽ mất cơ hội cho các bạn khác đã đăng ký và không được lựa chọn. Trong trường hợp không thể tham gia được, xin thông báo cho ban tổ chức ít nhất trước 2 tuần.</span></span></p>', 'http://www.condaopark.com.vn/vn/chuong-trinh-tinh-nguyen-vien-mua-he-nam-2020-tham-gia-bao-ton-rua-bien-tai-con-dao-tinh-ba-ria-vung-tau-viet-nam.html', '...', 0, 1, 1, '2020-08-10 20:06:54', '2020-09-15 12:14:19', 0, NULL, NULL, 0, ''),
(25, 'catalog/AnhCatBa/53e465cdd6c0880d81a23b7adbbb816d.jpg', 5, 'Giữa Vườn quốc gia Núi Chúa', '<div class=\"box-detail-thumb uk-text-center\" style=\"text-align: center !important; margin-bottom: 20px;\"><img src=\"https://nhandan.com.vn/imgold/media/k2/items/src/4172/6362c67b1b6db9325d3797cb37bc1e7a.jpg\" style=\"max-width: 100%; height: auto; object-fit: cover; width: 746.8px; margin: 0px;\"><em style=\"color: inherit; display: block; background: rgb(243, 241, 242); padding: 6px; font-size: medium;\">Nhân viên Vườn quốc gia Núi Chúa và các tình nguyện viên tham gia cứu hộ rùa biển. Ảnh: Thế quang</em></div><div class=\"box-des-detail this-one\" style=\"font-weight: 700; margin: 0px 0px 30px;\"><span class=\"\"></span><p style=\"margin-top: 20px; margin-bottom: 20px;\">Từ tháng sáu đến tháng chín hằng năm, nhiều bạn trẻ cả nước đến các khu vực Bãi Thịt, Bãi Hõm, Bãi Ngang thuộc Vườn quốc gia Núi Chúa, xã Vĩnh Hải, huyện Ninh Hải (Ninh Thuận) để làm tình nguyện viên bảo vệ an toàn cho loài rùa biển khi chúng lên bờ đẻ trứng. Cùng với đó, họ dạy trẻ em vùng biển học tiếng Anh, tin học, tổ chức nhặt rác làm sạch bãi biển,… đem đến cho làng biển nơi đây không khí mùa hè sôi động.</p></div><div class=\"box-sep-detail\"></div><div class=\"detail-content-body old-data\"><p style=\"margin-bottom: 20px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 18px;\"=\"\"><span lang=\"EN\">Một đêm cuối hè, tôi cùng nhóm tình nguyện viên do anh Trần Thanh Hải làm trưởng nhóm đi tuần tra khu vực rùa thường lên bãi đẻ trứng để triển khai việc bảo vệ, cứu hộ rùa. Anh Hải là chủ một cơ sở kinh doanh xe gắn máy tại TP Biên Hòa (Ðồng Nai). Tuy công việc kinh doanh bận rộn nhưng với tình yêu thiên nhiên, vào mùa hè hằng năm, anh vẫn dành thời gian tham gia các hoạt động bảo vệ môi trường, thiên nhiên tại những khu bảo tồn trên cả nước. Nhóm tình nguyện viên của anh Hải được tập huấn trước đó, cho nên nắm khá rõ về tập tính của loài rùa biển: khi lên bờ, nếu có tiếng động, cảm giác không an toàn, thì rùa quay lại biển chứ không tìm chỗ để đẻ trứng nữa. Chính vì vậy, mọi chuyển động của tình nguyện viên phải thật nhẹ nhàng.</span><span lang=\"EN\"></span></p><p style=\"margin-top: 20px; margin-bottom: 20px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 18px;\"=\"\">Ðang đi, Hải chợt phát tín hiệu để cả nhóm dừng lại. Dưới ánh trăng non, chúng tôi nhìn thấy vài con rùa mẹ đang ngoi khỏi mép nước biển. Chúng trườn khoảng chục mét rồi dừng lại, giương cao đầu quan sát, thấy yên tĩnh bèn trườn tiếp, dần bò sâu vào bãi cát khô ráo để đào ổ. Khoảng gần hai giờ sau đó, các rùa mẹ rời ổ, quay trở lại biển, cả nhóm tổ chức đánh dấu và ghi nhật ký để theo dõi cho đến khi rùa con nở. Việc cứu hộ rùa biển diễn ra khi nhân viên cứu hộ và nhóm tình nguyện viên nhận thấy ổ trứng rơi vào các điểm không bảo đảm an toàn, như: nằm ở vùng có thể bị ngập nước, nơi có người thường qua lại, nơi chăn thả gia súc... khi đó đội cứu hộ sẽ nhanh nhóng đào ổ, thu gom hết số trứng, dời sang nơi an toàn. Hoặc theo dõi, thấy đã quá thời gian trứng vùi trong ổ cát mà không nở rùa con hoặc số lượng nở rùa con không đồng đều, cán bộ bảo tồn nhanh chóng kiểm tra để có biện pháp cứu hộ bằng cách đào bới ổ, để kịp thời đưa các cá thể rùa con sức yếu, không thể ngoi qua khỏi lớp cát về với biển.</p><p style=\"margin-top: 20px; margin-bottom: 20px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 18px;\"=\"\">Hằng năm, tại Vườn quốc gia Núi Chúa có khoảng 100 lượt rùa mẹ lên bãi cát đẻ trứng. Trước khi đẻ trứng, vào ban đêm, rùa mẹ bò lên các vùng bãi cát ven biển dò tìm vị trí phù hợp. Sau đó, rùa mẹ dùng chân để quạt, đào cát tạo thành một cái ổ đường kính từ 20 đến 30 cm, sâu từ 60 đến 80 cm và đẻ trứng trong khoảng thời gian hai giờ. Rùa mẹ có thể đẻ từ một đến ba lần/năm, mỗi lần từ 70 đến 200 quả trứng. Sau khi đẻ xong, rùa mẹ rời tổ trứng, quay trở lại biển. Tùy vào nhiệt độ môi trường, trung bình từ 45 đến 60 ngày sau, trứng sẽ nở thành rùa con. Nhiệt độ càng cao, tỷ lệ trứng nở nhiều rùa cái và ngược lại, nhiệt độ càng thấp thì trứng nở rùa đực là chủ yếu. Rùa biển con sau khi nở trở về biển theo bản năng tự nhiên.</p><p style=\"margin-top: 20px; margin-bottom: 20px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 18px;\"=\"\">Ban quản lý Vườn quốc gia Núi Chúa đã phát động nhiều chương trình bảo vệ thiên nhiên thu hút đông đảo đoàn viên, thanh niên, sinh viên... cả nước hưởng ứng, đăng ký tham gia. Qua chương trình \"Ẵm rùa con về biển\", Ban quản lý Vườn quốc gia đã hướng dẫn hàng nghìn lượt học sinh và thành viên thuộc \"Nhóm gia đình em yêu thiên nhiên Việt Nam\" trong cả nước đến tham quan học tập, trải nghiệm các chương trình \"cứu hộ rùa biển\".</p><p style=\"margin-top: 20px; margin-bottom: 20px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 18px;\"=\"\">Những năm gần đây, người dân xã Vĩnh Hải luôn phấn khởi mỗi khi mùa hè đến, bởi lực lượng tình nguyện viên khắp nơi tề tựu về đây. Họ vừa tích cực tham gia công tác bảo vệ an toàn cho rùa lên bờ đẻ trứng, vừa là thầy giáo, cô giáo dạy cho con em học hè và tổ chức những cuộc vui chơi, giải trí bổ ích...</p><p style=\"margin-top: 20px; margin-bottom: 20px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 18px;\"=\"\">Phó Trưởng phòng Bảo tồn biển Vườn quốc gia Núi Chúa Phạm Anh Dũng cho biết, chương trình tình nguyện viên bảo vệ rùa biển đã lan tỏa khắp cả nước. Bên cạnh việc bảo vệ rùa lên bờ đẻ trứng, các tình nguyện viên còn tích cực tham gia công tác tuyên truyền, vận động người dân đang nuôi nhốt rùa biển giao lại cho Vườn quốc gia để thực hiện công tác bảo tồn, thả về với tự nhiên. Sau ba năm hoạt động, đến nay đã vận động người dân tự nguyện giao 19 con rùa để thả về biển. Trong ba tháng hè năm 2019, qua tám đợt triển khai công tác bảo tồn, cứu hộ rùa biển, 120 tình nguyện viên đã kịp thời cứu hộ 12 lượt rùa biển lên bãi đào ổ, trong đó có năm ổ đẻ thành công với 418 trứng; đã thả gần 300 rùa con về với môi trường tự nhiên; cứu hộ và chăm sóc bốn cá thể rùa biển tại khu vực cứu hộ sinh vật biển.</p><p style=\"margin-top: 20px; margin-bottom: 20px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 18px;=\"\" text-align:=\"\" center;\"=\"\"><img height=\"479\" src=\"https://nhandan.com.vn/imgold/images/nddt/1a86f9650704/1-332333.jpg\" width=\"640\" style=\"max-width: 100%; object-fit: cover; height: auto !important; width: auto !important;\"></p><p style=\"margin-top: 20px; margin-bottom: 20px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 18px;=\"\" text-align:=\"\" center;\"=\"\"><span lang=\"EN\"></span></p><p style=\"margin-top: 20px; margin-bottom: 20px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 18px;\"=\"\"><span style=\"color: rgb(0, 0, 255);\">Các tình nguyện viên dạy tiếng Anh cho học sinh ở thôn Thái An, xã Vĩnh Hải, huyện Ninh Hải (Ninh Thuận).</span></p><p style=\"margin-top: 20px; margin-bottom: 20px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 18px;\"=\"\"></p><p style=\"margin-top: 20px; margin-bottom: 20px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 18px;\"=\"\">Xong đợt tuần tra vào buổi tối, nhóm tình nguyện viên quay về căn nhà được xây trên lưng chừng Núi Chúa, chuẩn bị sáng hôm sau vào làng Thái An dạy tiếng Anh và tin học cho học sinh nghèo. Anh Nguyễn Trí Thành, giáo viên Trường cao đẳng Sư phạm TP Hồ Chí Minh chia sẻ: \"Mình rất yêu thích công tác tình nguyện và đã đi rất nhiều nơi nhưng lần đầu mình tham gia công tác bảo tồn rùa biển. Chương trình ở đây rất thiết thực, ý nghĩa bởi vì ngoài việc chăm sóc rùa đẻ vào ban đêm còn lồng ghép vào các hoạt động xã hội như nhặt rác làm sạch bãi biển và dạy học cho trẻ em ở các thôn lân cận. Mình sẽ tiếp tục đăng ký để tham gia hoạt động tình nguyện bảo vệ rùa biển tại đây trong nhiều năm nữa\".</p><p style=\"margin-top: 20px; margin-bottom: 20px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 18px;\"=\"\">Theo lịch công tác, từ 8 đến 11 giờ 30 phút các buổi sáng thứ hai, tư và sáu hằng tuần, các tình nguyện viên sẽ chia thành hai nhóm để dạy tiếng Anh và tin học cho học sinh nghèo. Chị Nguyễn Thị Thanh Vân, giáo viên tại TP Hồ Chí Minh cho biết, các lớp chia nhau nửa thời gian học tiếng Anh và nửa thời gian học tin học, cho nên các em rất thích thú. Thông qua chương trình, lồng ghép các hoạt động giáo dục môi trường, nhằm giúp các em hình thành thói quen tốt góp phần bảo vệ môi trường, thiên nhiên.</p><p style=\"margin-top: 20px; margin-bottom: 20px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 18px;\"=\"\">Những năm qua, Ninh Thuận đẩy mạnh phát triển du lịch sinh thái, thu hút đông đảo du khách trong nước và quốc tế đến thưởng ngoạn, cho nên công tác bảo tồn tài nguyên rừng, biển tại Vườn quốc gia Núi Chúa được chú trọng nhiều. Thông qua việc xây dựng và hỗ trợ thực hiện dự án \"Sinh kế bền vững cho đồng bào dân tộc thiểu số Ra Glai thôn Cầu Gãy, xã Vĩnh Hải, huyện Ninh Hải\" mở hướng phát triển một cộng đồng du lịch không còn phụ thuộc vào tài nguyên rừng. Ðịa phương đã thành lập các tổ thủ công mỹ nghệ, tổ múa mã la, tổ tham gia hướng dẫn du lịch; hướng dẫn người dân trồng rừng bằng các loài cây bản địa xen với cây ăn quả có giá trị kinh tế cao. Từ mô hình phát triển kinh tế gắn với nhận khoán bảo vệ rừng, Vườn quốc gia Núi Chúa đã hỗ trợ hàng tỷ đồng giúp đồng bào Ra Glai làm chuồng trại, nuôi hàng trăm con bò, dê, gà,… tạo việc làm, tăng thu nhập cho người dân vươn lên thoát nghèo. Nếu trước đây, đồng bào Ra Glai coi rừng là tài nguyên tự do, chỉ biết tận dụng khai thác, đến nay đã hiểu được lợi ích và giá trị kinh tế, môi trường do rừng mang lại, cho nên việc phá rừng đã giảm nhiều, nhờ đó độ che phủ của rừng được tăng lên. Ðồng bào tích cực tham gia công tác bảo vệ tài nguyên biển, không xả thải gây ảnh hưởng đến môi trường biển.</p><p style=\"margin-top: 20px; margin-bottom: 20px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 18px;\"=\"\">Rời Vườn quốc gia Núi Chúa khi những ngày hè khép lại, mùa rùa biển đẻ trứng trôi qua, các tình nguyện viên bịn rịn, xúc động trong giờ phút chia tay cán bộ, nhân viên Vườn quốc gia và những trẻ em nghèo vùng biển Vĩnh Hy để trở lại công việc hằng ngày của mình, với lời hứa hẹn sẽ quay trở lại vào mùa hè năm sau. Tôi tin rằng những nỗ lực của họ sẽ góp phần tích cực bảo vệ loài rùa biển vượt qua nguy cơ bị tuyệt chủng, để Vườn quốc gia Núi Chúa mãi mãi là nơi loài rùa biển tìm về cội nguồn sau 30 năm sống dưới đại dương.</p><table width=\"100%\" cellspacing=\"1\" cellpadding=\"1\" border=\"1\" style=\"object-fit: cover; padding: 2px; border: 1px solid rgb(153, 153, 153); font-size: 16px; width: 746.8px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);\"=\"\"><tbody><tr style=\"max-width: 100%; object-fit: cover; border: 0px; background: rgb(243, 241, 242);\"><td style=\"max-width: 100%; object-fit: cover; border: 1px solid rgb(238, 238, 238); padding: 3px;\"><p style=\"margin-bottom: 0px;\"><span style=\"color: rgb(255, 102, 0);\"><span style=\"font-weight: bolder;\">Vườn quốc gia Núi Chúa có hệ thực vật rừng có 1.504 loài, trong đó có 54 loài quý hiếm thuộc sách đỏ Việt Nam và sách đỏ thế giới; hệ động vật rừng có 345 loài, trong đó có 46 loài quý hiếm thuộc sách đỏ Việt Nam và sách đỏ thế giới. Phó Giám đốc Vườn quốc gia Núi Chúa Nguyễn Tường Giao cho biết, mỗi năm có ba loài rùa biển đến đây sinh sản, gồm: rùa xanh, đồi mồi và đồi mồi dứa. Ðặc biệt, loài rùa biển 30 năm tuổi mới trưởng thành và có khả năng sinh sản. Rùa biển sinh ra chỗ nào thì 30 năm sau sẽ tìm về nơi đó để đẻ, chính vì điều đó mà Vườn quốc gia Núi Chúa là một trong ba vùng trên đất liền hiện nay, gồm: Vườn quốc gia Côn Ðảo, Núi Chúa và Vũng Tàu có rùa biển lên bãi cát đẻ trứng.</span></span></p></td></tr></tbody></table><br><p style=\"margin-top: 20px; margin-bottom: 20px; color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 18px;\"=\"\"></p></div>', 'https://nhandan.com.vn/tin-tuc-xa-hoi/giua-vuon-quoc-gia-nui-chua-372339/', '...', 0, 5, 1, '2020-08-10 20:11:14', '2020-09-15 12:21:46', 0, NULL, NULL, 0, 'Bài M5'),
(26, 'catalog/0f3b580a2b5435dd8fda749ce13ff1c5.jpg', 5, 'Lễ phát động trồng cây tại vườn quốc gia Ba Vì', '<p style=\"border: 0px; margin-bottom: 15px; padding: 0px; color: rgb(5, 5, 5); font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;=\"\" font-size:=\"\" 14px;\"=\"\"><span style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" times;=\"\" font-size:=\"\" medium;\"=\"\">&nbsp;Hôm nay, ngày 22/3/2014. Tại Vườn quốc gia Ba Vì đã diễn ra lễ phát động trồng cây gây rừng do Trung tâm Nghiên cứu Tài nguyên và Môi trường Đại học Quốc gia Hà Nội, quỹ môi trường Nhật Bản AEON &nbsp;tổ chức với sự hỗ trợ của Vườn quốc gia Ba Vì.</span></p><p style=\"border: 0px; margin-bottom: 15px; padding: 0px; color: rgb(5, 5, 5); font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;=\"\" font-size:=\"\" 14px;\"=\"\"><span style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" times;=\"\" font-size:=\"\" medium;\"=\"\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tham dự buổi lễ phát động trồng cây có sự tham gia của lãnh đạo Đại học Quốc gia Hà Nội, Đại sứ quán Nhật Bản tại Việt Nam, quỹ môi trường AEON Nhật Bản, Trung tâm Nghiên cứu tài nguyên &amp; môi trường ĐHQG Hà Nội, lãnh đạo và cán bộ Vườn quốc gia Ba Vì, sinh viên Nhật Bản, sinh viên ĐHQGHN và học sinh trường dân tộc nội trú Ba Vì.</span></p><p style=\"border: 0px; margin-bottom: 15px; padding: 0px; color: rgb(5, 5, 5); font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;=\"\" font-size:=\"\" 14px;=\"\" text-align:=\"\" center;\"=\"\"><strong style=\"border: 0px; margin: 0px; padding: 0px;\"><span style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" times;=\"\" font-size:=\"\" medium;\"=\"\">Dưới đây là một số hình ảnh về buổi lễ trồng cây</span></strong></p><p style=\"border: 0px; margin-bottom: 15px; padding: 0px; color: rgb(5, 5, 5); font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;=\"\" font-size:=\"\" 14px;=\"\" text-align:=\"\" center;\"=\"\"><strong style=\"border: 0px; margin: 0px; padding: 0px;\"><img src=\"https://photo-1-baomoi.zadn.vn/w1000_r1/2019_02_15_310_29663683/6495af012740ce1e9751.jpg\" alt=\"Huyện Ba Vì phát động Tết trồng cây xuân Kỷ Hợi - Báo Lao Động Thủ Đô\" style=\"width: 680.5px;\"></strong></p><p style=\"border: 0px; margin-bottom: 15px; padding: 0px; color: rgb(5, 5, 5); font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;=\"\" font-size:=\"\" 14px;=\"\" text-align:=\"\" center;\"=\"\"><strong style=\"border: 0px; margin: 0px; padding: 0px;\"><img src=\"https://vtv1.mediacdn.vn/thumb_w/650/Uploaded/huyentrang/2014_02_03/ba_vi_030214.jpg\" alt=\"Tổng Bí thư Nguyễn Phú Trọng phát động Tết trồng cây tại Ba Vì ...\" style=\"width: 680.5px;\"></strong></p><p style=\"border: 0px; margin-bottom: 15px; padding: 0px; color: rgb(5, 5, 5); font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;=\"\" font-size:=\"\" 14px;=\"\" text-align:=\"\" center;\"=\"\"><strong style=\"border: 0px; margin: 0px; padding: 0px;\"><img src=\"https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQPpLY8MaihR80bgLfReNRsM5cXjX-dZQpEnA&amp;usqp=CAU\" alt=\"Hoạt động trồng cây tại Vườn Quốc gia Ba Vì hưởng ứng Giờ trái đất ...\" style=\"width: 680.5px;\"><span style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" times;=\"\" font-size:=\"\" medium;\"=\"\"><br></span></strong></p>', '...', '...', 0, 1, 1, '2020-08-10 20:15:44', '2020-09-15 12:18:39', 0, NULL, NULL, 0, ''),
(27, 'catalog/AnhCongVien/c6765029e65f9f84fffd72a458890f72.jpg', 5, 'Lý do bạn nên tham gia hoạt động tình nguyện', '<p style=\"margin-top: 1em; margin-bottom: 1em; color: rgb(34, 34, 34); font-family: \" roboto=\"\" condensed\",=\"\" sans-serif;=\"\" font-size:=\"\" 14px;\"=\"\"><strong>Tình nguyện là một hoạt động đầy ý nghĩa có ảnh hưởng tích cực đến cộng đồng và xã hội. Nhưng bạn có biết rằng tình nguyện cũng mang lại cho bản thân bạn rất nhiều lợi ích? Dưới đây là một vài lý do bạn nên đi làm tình nguyện.</strong></p><p style=\"margin-top: 1em; margin-bottom: 1em; color: rgb(34, 34, 34); font-family: \" roboto=\"\" condensed\",=\"\" sans-serif;=\"\" font-size:=\"\" 14px;\"=\"\"></p><div style=\"color: rgb(34, 34, 34); font-family: \" roboto=\"\" condensed\",=\"\" sans-serif;=\"\" font-size:=\"\" 14px;=\"\" text-align:=\"\" justify;\"=\"\"><span style=\"font-size: 12px;\"><span style=\"font-family: Arial;\"><strong>1. Học hỏi được nhiều kỹ năng mới<br></strong><br>Tình nguyện là một cơ hội tuyệt vời để bạn tìm ra sở trường của mình và phát triển những kỹ năng mới. Lên kế hoạch cho một chiến dịch quyên góp giúp bạn phát triển khả năng lập kế hoạch quyết toán chi tiêu. Huấn luyện và hướng dẫn các tình nguyện viên khác giúp bạn phát triển kỹ năng sư phạm.<br><br>Có rất nhiều kỹ năng cần thiết giúp bạn thăng tiến trong sự nghiệp nhưng bạn không cần thiết phải cố gắng rèn luyện những kỹ năng đó vì mục tiêu công việc. Vẽ một bức tranh tường hoặc thiết kế một poster cổ động vì ngày tình nguyện quốc tế – để kỷ niệm những hoạt động ý nghĩa mà các tình nguyện viên đã làm – có thể giúp bạn nâng cao khả năng hội họa và thiết kế. Hãy khám phá tình yêu với âm nhạc và học làm DJ để mang đến những cảm xúc mới lạ cho câu lạc bộ disco trẻ. Nói chung, có rất nhiều cơ hội. Điều quan trọng là bạn có thể tận dụng nó không.<br><br><br></span></span></div><div style=\"color: rgb(34, 34, 34); font-family: \" roboto=\"\" condensed\",=\"\" sans-serif;=\"\" font-size:=\"\" 14px;=\"\" text-align:=\"\" center;\"=\"\">&nbsp;</div><table width=\"460\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\" align=\"center\" style=\"border: 0px; color: rgb(34, 34, 34); font-family: \" roboto=\"\" condensed\",=\"\" sans-serif;=\"\" font-size:=\"\" 14px;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);\"=\"\"><tbody><tr><td style=\"border: 0px; vertical-align: top;\"><img src=\"http://tinhnguyenhe.doanthanhnien.vn/Uploads/dongha/2017/5/31/TNTN-chuan-(5)-17475.jpg\" alt=\"gh\" width=\"460\" vspace=\"5\" hspace=\"5\" height=\"345\" style=\"vertical-align: bottom;\"></td></tr><tr><td valign=\"middle\" align=\"center\" style=\"border: 0px; vertical-align: top;\"><span style=\"color: rgb(0, 0, 255);\"><span style=\"font-family: Arial;\"><span style=\"font-size: 12px;\">ảnh minh họa</span></span></span></td></tr></tbody></table><div style=\"color: rgb(34, 34, 34); font-family: \" roboto=\"\" condensed\",=\"\" sans-serif;=\"\" font-size:=\"\" 14px;=\"\" text-align:=\"\" justify;\"=\"\"><span style=\"font-size: 12px;\"><span style=\"font-family: Arial;\"><br><strong>2. Hãy trở thành một phần của cộng đồng<br></strong><br>Con người và xã hội luôn phải dựa vào nhau để sinh tồn, sự xuất hiện của thương mại hóa đã làm tổn hại đến những giá trị truyền thống, tính cộng đồng đang bị ảnh hưởng bởi sự phát triển của một xã hội hiện đại nhưng ngay bây giờ chúng ta có thể hàn gắn lại sự thay đổi đó bằng cách làm tình nguyện. Mục đích cao cả của làm tình nguyện là giúp đỡ người khác và tạo ra những ảnh hưởng tích cực tới đời sống của những người kém may mắn.<br><br><strong>3. Chinh phục mục tiêu<br></strong><br>Về cơ bản, làm tình nguyện là đầu tư thời gian, công sức và kỹ năng một cách phi lợi nhuận. Không giống như nhiều việc khác trong cuộc sống, bạn có quyền được lựa chọn khi làm tình nguyện. Tình nguyện sẽ tạo động lực và cảm giác hạnh phúc khi đã làm được việc có ích, điều này bắt nguồn từ nhu cầu và khát khao được cống hiến. Nhiều người nghĩ rằng một người không thể tạo ra sự khác biệt. Điều đó có thể là đúng khi không một ai có đủ khả năng để giải quyết tất cả vấn đề của nhân loại, nhưng những gì bạn làm được có thể đóng góp một chút công sức để làm thế giới này dần trở nên tốt đẹp hơn.<br><br><strong>4. Cải thiện sự nghiệp<br></strong><br>-Nhà tuyển dụng muốn tuyển ứng viên đã có kinh nghiệm nhiều kinh nghiệm xã hội và phối hợp hoạt động nhóm tốt.<br><br>-Nhà tuyển dụng tin rằng tình nguyện giúp cải thiện các kỹ năng công việc.<br><br>-Nhiều ứng viên từng tình nguyện để học thêm các kỹ năng mới đều được ưu tiên trong xin việc, nhận lương hoặc được thăng tiến.<br><br>Hơn nữa, nếu bạn đang nghĩ đến việc thay đổi sự nghiệp thì tình nguyện là một cách tuyệt vời để bạn để khám phá những sở thích mới. Nếu bạn có đam mê hội họa nhưng học chuyên ngành về máy vi tính thì tại sao lại không làm tình nguyện ở một rạp hát gần nơi bạn sống? Hoặc nếu bạn có hoài bão trở thành một bác sĩ thì tại sao lại không làm tình nguyện ở một bệnh viện – một cách lý tưởng để làm đẹp.<br><br><strong>5. Một sở thích mới<br></strong><br>Đôi lúc chúng ta cảm thấy bị bó buộc trong một xã hội đầy tính cạnh tranh, “Cơm- áo- gạo tiền” tác động đến cuộc sống của bạn hàng ngày thì tình nguyện sẽ là một cách để thoát khỏi những áp lực trong cuộc sống và tạo ra sự cân bằng. Tìm hiểu sở thích mới thông qua những việc làm tình nguyện là một trải nghiệm thú vị, thiết kế một poster, banner hay xây dựng một trang web một vài giờ trong tuần hoặc viết bài cho một tổ chức tình nguyện, cảm giác thỏa mãn giúp giải tỏa căng thẳng, mệt mỏi trong cuộc sống. Những trải nghiệm đó đưa bạn đến những thứ mới lạ hoặc giúp bạn khám phá những sở thích mà bạn chưa bao giờ để ý tới. Bạn có thể mở mang tầm nhìn hoặc khám phá những cơ hội mới hoặc mở rộng chân trời của bạn.<br><br><strong>6. Những kinh nghiệm mới<br></strong><br>Tình nguyện là một cơ hội tuyệt vời để học hỏi những kinh nghiệm cuộc sống. Bất kể công việc gì, từ việc xây dựng một thư viện đến thiết kế những tờ rơi để kéo sự chú ý tới một hoạt động tình nguyện, bạn đều được trải nghiệm khi bắt tay vào làm việc. Tình nguyện viên có thể làm bất cứ điều gì trong một xã hội có vô vàn cơ hội tình nguyện.<br><br>Ví dụ, bạn có thể làm tình nguyện ở một đất nước đang phát triển và xem những ảnh hưởng trực tiếp bạn đang tạo ra cho những người kém may mắn nhất trên thế giới. Bạn có thể lướt ván trên không để tình nguyện, một cách để trải nghiệm trò chơi thể thao mạo hiểm và kêu gọi quyên góp.<br><br><strong>7. Gặp gỡ những người khác nhau<br></strong><br>Tình nguyện giúp gắn kết nhiều người từ những hoàn cảnh khác nhau lại gần nhau hơn. Những kết quả từ công việc tình nguyện của bạn và những thành viên trong tổ chức đều là nguồn cảm hứng và là một cách tốt để bạn phát triển những kỹ năng mềm. Tình nguyện giúp bạn tạo một mạng lưới quan hệ tuyệt vời. Không chỉ giúp bạn phát triển những mối quan hệ lâu dài mà cũng là một cách tuyệt vời để biết thêm những người đến từ mọi hoàn cảnh trong cuộc sống, môi trường và nghề nghiệp. Mối quan hệ là một lợi ích thú vị của tình nguyện vì bạn không thể đoán trước được những người bạn sẽ gặp hoặc những điều mới bạn sẽ học được và những ảnh hưởng bạn tạo ra trong cuộc sống.<br><br><strong>8. Gửi một thông điệp tới nhà tuyển dụng, thầy cô và gia đình<br></strong><br>Khi nhìn nhận về 1 cá nhân nào đó, người khác thường quan tâm đến cuộc sống cộng đồng của bạn, những công việc xã hội mà bạn đã tham gia. Tình nguyện phản ánh một bức tranh toàn cảnh về bạn và là một ví dụ thực tế về sự nhiệt huyết, kiên trì và sở thích của bạn. Hãy cho mọi người biết bạn đam mê điều gì và bạn có cũng có thể truyền cảm hứng cho người khác.</span></span></div>', '...', '...', 0, 1, 1, '2020-08-10 20:31:54', '2020-09-15 12:19:05', 0, NULL, NULL, 0, '');
INSERT INTO `post` (`post_id`, `image`, `author_id`, `title`, `content`, `link`, `tag`, `featured`, `sort_order`, `status`, `date_added`, `date_modified`, `parent_id`, `summary`, `sub_title`, `top`, `menu`) VALUES
(28, 'catalog/NamCatTienDongVat/1a0b0054656be868d62684f50817afc1.jpg', 5, 'Động Vật - Nam Cát Tiên', '<h2 style=\"font-family: Arial, Verdana, sans-serif; background-color: rgb(240, 251, 251); color: rgb(0, 137, 201) !important;\"><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Xưa kia vùng đất Biên Hòa - Đồng Nai còn bao&nbsp; phủ bởi rừng rậm nhiệt đới, là nơi các loài động vật hoang dã tập trung sinh sống. Đặc biệt các thú lớn như voi, hổ, báo, trâu rừng, bò rừng, tê giác, gấu, hươu, nai... tụ họp từng đàn, cùng với các thú nhỏ như khỉ, chó sói, heo rừng, trăn rắn... và nhiều loài chim. Dưới sông rạch đầy cá sấu.<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Sách&nbsp;<i>Chân Lạp phong thổ ký</i>&nbsp;của Châu Đạt Quan mô tả quang cảnh vùng này năm 1296:&nbsp;<i>\"Bắt đầu vào Chân Bồ (vùng biển Vũng Tàu ngày nay) gần hết cả vùng đều là rừng rậm, bóng cây um tùm, khắp nơi vang tiếng chim hót và tiếng thú kêu... hàng trăm hàng nghìn trâu rừng tụ họp từng bầy...\"<br></i></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Ca dao Biên Hòa xưa có câu:<br></font></span><i><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Đồng Nai địa thế hãi hùng,<br></font></span></i><i><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Dưới sông sấu lội, trên giồng cọp um.<br></font></span></i><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Chuyện dân gian xứ Bưởi còn lưu truyền nhiều giai thoại về đánh sấu, diệt cọp là loài thú dữ nổi tiếng&nbsp;<i>\"Cọp Biên Hòa, ma rừng Sác\"</i>. Người dân thời đó đã dũng cảm chiến đấu diệt cọp và sấu để tồn tại.<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Cọp sống giữa sình lầy nước mặn, ở bãi bùn nước lợ với rặng dừa nước hoặc ở vùng đất trống, trong các gò đất giữa bãi cỏ thấp có rừng thưa hoặc trống trải làm nơi cư trú kết bầy. Bên kia rạch Cát có nhiều cọp, thỉnh thoảng chúng lội rạch qua bắt nai, heo rừng ở các cù lao có tên gọi là cù lao Nai, cù lao Heo.<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Sấu thì ở các sông rạch, những vùng nhiều sấu dữ thời trước là ở rạch Ông Án, rạch Lò Gốm - Ở Biên Hòa có các bàu Sấu ở ga xe lửa, ở đoạn gần đình Tân An và ở Tân Hạnh. Con sấu cuối cùng ở Biên Hòa bị dân chài xóm Hòa Quới câu bắt được ở vàm Bà Lồ (phường Tân Vạn ngày nay) trước Cách mạng tháng Tám năm 1945 (<a name=\"_ftnref2\" title=\"\" style=\"color: rgb(18, 174, 80) !important;\"><span lang=\"NL\">[2]</span></a>[3]).<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Trong thời kháng chiến chống Pháp, cán bộ bộ đội và người dân chiến khu Đ không những phải chống giặc ngoại xâm mà còn phải chống với thú dữ . Rừng chiến khu Đ hồi đó là rừng già còn nhiều loài thú lớn, thú nhỏ, chim muông, vẫn còn những đàn trâu rừng và voi. Sông Đồng Nai có cá sấu, rừng có&nbsp; cọp, con \"Cọp 3 móng\" hoành hành nhiều nơi, giết hại nhiều người. Bộ đội ta phải khó khăn lắm mới diệt được con cọp này vào đầu năm 1950 (<a name=\"_ftnref3\" title=\"\" style=\"color: rgb(18, 174, 80) !important;\"><span lang=\"NL\">[3]</span></a>[4]).<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Những tư liệu để lại và những chuyện kể trên cho chúng ta hiểu lịch sử, cảnh quan thiên nhiên cùng với cuộc sống của người dân ở vùng đất Biên Hòa - Đồng Nai xưa kia - bao phủ rừng rậm nhiệt đới mênh mông đã từng là giang sơn của các loài&nbsp; thú.<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Trải qua mấy thế kỷ, thế giới động vật ngày nay có nhiều biến đổi, nơi ở của nhiều loài sinh vật hoang dã đã bị thu hẹp lại, cũng vì thế mà nhiều loài&nbsp; động vật trước kia khá phổ biến nay trở nên hiếm, chỉ còn những nơi có rừng phân bố tập trung ít bị tàn phá thì khu hệ động vật còn thể hiện tính đa dạng và phong phú mà ta còn thấy ở rừng Nam Cát Tiên và một số vùng rừng trong tỉnh.<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Khu hệ động vật rừng Đồng Nai có những đặc trưng của miền địa lý động vật Đông Nam Bộ mang tính chất đại diện cho khu hệ động vật vùng Đông Nam Á, tiêu biểu là tính hỗn hợp của phân miền Ấn Độ - Mã Lai, có đặc trưng của khu hệ động vật nhiệt&nbsp; đới bao gồm những động vật có cấu trúc thành phần loài trong các loại hình sinh cảnh của hệ sinh thái rừng ẩm nhiệt đới mang màu sắc của hệ động vật cao nguyên Nam Trường Sơn -&nbsp; Yếu tố địa hình và môi trường sống của động vật là những yếu tố quan trọng giúp cho sự có mặt của nhiều&nbsp; loài thú chim quí hiếm ở Đồng Nai. Hầu hết các loài này thuộc diện phân bố rộng có tính thích nghi từ vùng cao xuống vùng thấp và rừng ngập nước với những dãy đồi thấp nhấp nhô xen những bãi cỏ rộng lớn dưới tán rừng thưa ven sông Đồng Nai và các suối, bàu, ao hồ có diện tích lớn, các kiểu rừng thường xanh và nửa rụng lá ẩm nhiệt đới đã giữ lại được những loài động vật mà ngày nay đang có nguy cơ mất dần như Tê giác 1 sừng, Bò Banteng, cá sấu... Vì vậy có một số loài chim, thú, bò sát phân bố ở đây rất thích ứng với hệ sinh thái trong vùng.<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Kết quả điều tra tài nguyên rừng Đồng Nai năm 1983 và các kết quả nghiên cứu về khu hệ động vật Nam Cát Tiên gần đây (1986 - 1990) cho thấy vùng rừng phía Bắc sông Đồng Nai, trong đó rừng Nam Cát Tiên là một trong những khu vực có mật độ thú và các động vật có xương sống ở cạn phong phú nhất ở Đông Nam Bộ, là trung tâm đáng chú ý về các loài động vật quý hiếm ở Việt Nam. Bước đầu các nhà nghiên cứu đã ghi nhận được 529 loài, trong đó chim có 210 loài thuộc 49 họ, thú 57 loài thuộc 25 họ, bò sát 40 loài thuộc 15 họ, côn trùng 121 loài thuộc 18 họ...<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Trong số 57 loài thú thì bộ Móng guốc có đầy đủ 5 họ với 6 loài, chiếm ưu thế là Nai, Heo rừng, Bò rừng, Trâu rừng, Cheo, Hoẵng là một vùng của Việt Nam có nhiều đại diện của họ Bò (Bovidae) sống trong một vùng lãnh thổ nhỏ.<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Các loài thú quý hiếm có 14 loài, chiếm 45% loài quí hiếm của Việt Nam. Nhiều loài có giá trị khoa học và cần phải bảo vệ như: Cầy bay (Cynocephalus variegatus), Sóc bay (Petaurista petaurista), Cầy mực (Arctictis binturong), Chó sói đỏ (Cuon alpinus), Hổ (Panthera tigris), Báo lửa (Felis temminski), Báo hoa mai (Panthera pardus), Cheo cheo (Tragulus javanicus), Bò tót (Bos gaurus), Bò Banteng (Bos Banteng), Voi Ấn Độ (Elephas maximus), Vọoc ngũ sắc (Pygathrix nemaeus), Vượn đen (Hylobates concolor), Tê tê (Manis javanica)...<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Các loài thú ở Đồng Nai đang có nguy cơ bị diệt vong như Tê giác 1 sừng (Rhinoceros sondaicus), Voọc ngũ sắc, Cầy bay... đang cần sự quan tâm bảo vệ khẩn cấp của Việt Nam và thế giới.<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Đến nay các nhà khoa học đã thống kê được 210 loài chim với thành phần loài bao gồm hầu hết các loài chim điển hình của vùng Đông Nam Á và của Đông Dương, nhiều loài là quí hiếm chiếm 40% số loài quí hiếm của Việt Nam.<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">&nbsp;Nhóm chim nước khá phong phú với các loài rất quí hiếm của thế giới như: Ngan cánh trắng (Cairina scutulata), Hạc cổ trắng (Ciconia episcopus), Già đẫy lớn và Già đẫy Java (Leptotilos dubius và L. Javanicus)... và có thể còn gặp các loài đặc biệt quí hiếm khác.<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Thêm vào đó, nhóm chim Trĩ (Phasianidae) cũng rất phong phú, có đến 8 loài với sự hiện diện của các loài quí hiếm được thế giới&nbsp; quan tâm bảo vệ như: Công xanh (Pavo muticus), Gà tiền mặt đỏ (Polyplection germanii), Trĩ sao (Rheinartia ocellata), Gà lôi hồng tía (Lophura&nbsp; diardi), Gà so cổ hung (Arborophila davidi)... Loài bò sát có 3 loài quí hiếm chiếm 23% số loài bò sát quí hiếm của Việt Nam như: Cá sấu nước ngọt (Crocodylus siamensis), Trăn gấm (Python reticulatus), Trăn đen (Python molurus).<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Trong số 56 loài động vật có xương sống được xem là quí hiếm ở Đông Dương thì ở Nam Cát Tiên có 20 loài, trong đó có 16 loài được Tổ chức quốc tế bảo vệ nguồn lợi thiên nhiên (IUCN) xếp vào danh sách bảo vệ. Trong số 121 loài côn trùng thì có 7 loài mới lần đầu tiên phát hiện được ở Việt Nam. Đó là các loài Baetis illiesi, Baelis moriharai, Neobaetiella macani, Platybaetis bishopi, Chromarcys feuerborni, Povilla cambodjensis, Tricorythus sp2.<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Khu hệ động vật Nam Cát Tiên mang tính chất đại diện cho khu hệ&nbsp; động vật vùng Đông Nam Á với các loài như Chó dơi (Cynocephalus variegatus), Cheo cheo (Tragulus javanicus), Sóc chân vàng (Callossciurus flavimanus)... và mang đầy đủ những nét đặc trưng của một khu hệ động vật nhiệt đới trong các kiểu sinh cảnh khác nhau như đại diện của họ khỉ (Cercopithecidae), họ Cầy (Viverridae), họ Khiếu (Timaliidae), họ Gà (Phasianidae)...<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Khu hệ động vật còn có những loài chim thú được liệt vào những loài chim thú đặc hữu của Đông Dương, trong số 11 loài đặc hữu của Đông Dương thì Nam Cát Tiên đã có 6 loài như: Công xanh, Trĩ sao, Cheo cheo...<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Ngoài ra còn có những loài được hình thành tại chỗ (bản địa) như: Gà rừng (G. gallus gallus), Tê tê (Manis javanica), Đa đa (Francolinus Pintadeanus).<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">Tiềm năng nguồn tài nguyên động vật&nbsp; Đồng Nai thực sự phong phú, có thể chia thành các nhóm tài nguyên như sau:<br></font></span><font color=\"#0b5394\" size=\"3\"><i><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\">- Nhóm động vật quí hiếm có giá trị khoa học đặc biệt và là nguồn gien dự trữ quan trọng</span></i><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\">&nbsp;cho việc bảo tồn và phát triển khu hệ động vật của địa phương, trong đó có nhóm Bò có thể có các loài được bảo vệ sẽ là bảo đảm nguồn gien cho chăn nuôi đại gia súc.<br></span></font><font color=\"#0b5394\" size=\"3\"><i><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\">- Nhóm động vật phục vụ nghề săn bắn</span></i><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\">&nbsp;cho thịt về thú có 40 loài, chim trên 60 loài, bò sát 12 loài.<br></span></font><i><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">- Nhóm động vật cho các sản phẩm làm dược liệu:<br></font></span></i><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">* Thú: Gấu, Hổ, Nai, Khỉ, Nhím, Tê tê...<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">* Chim: Bìm bịp.<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">* Bò sát: Tắc kè, Thằn lằn bóng, Kỳ đà, Trăn, Rắn hổ mang, Rắn cạp nong, Rùa...<br></font></span><font color=\"#0b5394\" size=\"3\"><i><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\">- Nhóm động vật nuôi làm cảnh</span></i><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\">&nbsp;có giá trị xuất khẩu lớn như: Khỉ, Gấu chó, Vượn, Sóc đen, Vàng anh, chim Trĩ, Bói cá, Rùa nắp, Kỳ đà, Tắc kè, cá Sấu nước ngọt...<br></span></font><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">- Nhóm động vật không xương sống từ côn trùng trong thảm rừng, trong đất, nước và các nhóm động vật thủy sinh có vai trò đặc biệt trong chu trình vật chất và đảm bảo năng suất của các hệ sinh thái cũng có giá trị to lớn.<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">II.1. Cấu trúc và đặc điểm khu hệ động vật:<br></font></span><span lang=\"NL\" style=\"font-family: \" times=\"\" new=\"\" roman\",=\"\" serif;\"=\"\"><font color=\"#0b5394\" size=\"3\">II.2. Giá trị tài nguyên động vật:</font></span></h2><h3 style=\"font-family: Arial, Verdana, sans-serif; background-color: rgb(240, 251, 251); color: rgb(0, 137, 201) !important;\"><a name=\"TOC-II.1.-C-u-tr-c-v-c-i-m-khu-h-ng-v-t:\" style=\"color: rgb(163, 0, 0);\"></a></h3><h3 style=\"font-family: Arial, Verdana, sans-serif; background-color: rgb(240, 251, 251); color: rgb(0, 137, 201) !important;\"><a name=\"TOC-II.2.-Gi-tr-t-i-nguy-n-ng-v-t:\" style=\"color: rgb(163, 0, 0);\"></a></h3>', '...', '...', 0, 1, 1, '2020-08-10 21:14:01', '2020-09-15 12:21:01', 0, NULL, NULL, 0, ''),
(29, 'catalog/NuiChuaDongVat/cb0e29e02e466c29b3b73821508d73f4.jpg', 5, 'Hệ động vật - Núi Chúa', '<p class=\"Body00\" style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); margin: 4px 10px; text-align: justify;\">Kết quả điều tra của Viện Sinh thái và Tài nguyên sinh vật Trung tâm Khoa học tự nhiên và Công nghệ quốc gia, thực hiện năm 2001 đã ghi nhận được 306 loài động vật hoang dã có xương sống thuộc 89 họ, 29 bộ của 4 lớp động vật. Trong 306 loài hiện diện, có 29 loài cho dược liệu, 60 loài cho thịt, 18 loài cho da lông, 21 loài có thể làm cảnh. Ngoài ra còn có một số loài có lợi cho sản xuất Nông nghiệp vì chúng có khả năng ăn côn trùng có hại, tiêu diệt chuột, tham gia thụ phấn, phát tán hạt giống cây rừng.&nbsp;</p><p class=\"Body00\" style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); margin: 4px 10px; text-align: justify;\">Khu hệ động vật ở Vườn quốc gia Núi chúa chưa được điều tra, nghiên cứu đầy đủ nhưng theo một số nhà khoa học nơi đây vẫn tồn tại nhiều loài động vật quý hiếm như: Chà chân đen&nbsp;<em><a href=\"http://vncreatures.net/chitiet.php?page=1&amp;loai=1&amp;img=1&amp;ID=5520\" class=\"aLink09\" style=\"font-family: Arial; color: rgb(0, 0, 255);\">Pygathrix nigripes</a>,</em>&nbsp;Gấu ngựa&nbsp;<em><a href=\"http://vncreatures.net/chitiet.php?page=1&amp;loai=1&amp;img=1&amp;ID=5533\" class=\"aLink09\" style=\"font-family: Arial; color: rgb(0, 0, 255);\">Ursus thibetanus</a>,</em>&nbsp;Rùa da&nbsp;<em>Dermochelys coriacea,&nbsp;</em>Đồi mồi dứa&nbsp;<em>Chelonia mydas,</em>&nbsp;Vích&nbsp;<em>Caretta caretta&nbsp;</em>....Nhiều loài chim qúy hiếm vẫn còn hiện diện như: Cốc biển bụng trắng&nbsp;<em>Fregata andrewsi,</em>&nbsp;Gà lôi&nbsp;<em><a href=\"http://vncreatures.net/chitiet.php?page=1&amp;loai=1&amp;img=1&amp;ID=5276\" class=\"aLink09\" style=\"font-family: Arial; color: rgb(0, 0, 255);\">Lophura nythemera</a>,</em>&nbsp;Phướn đất&nbsp;<em><a href=\"http://vncreatures.net/chitiet.php?page=1&amp;loai=1&amp;img=1&amp;ID=5721\" class=\"aLink09\" style=\"font-family: Arial; color: rgb(0, 0, 255);\">Carpococcyx renauldi</a></em>, công&nbsp;<em>Pavo muticus</em>...chứng tỏ mức độ đa dạng nơi đây vẫn còn khá cao.</p><p class=\"Body00\" style=\"font-family: Arial, Helvetica, sans-serif; color: rgb(0, 0, 0); margin: 4px 10px; text-align: justify;\">Bò sát lưỡng cư: Là một khu vực có hai kiểu rừng rất đặc biệt là khô hạn và thường xanh nên Vườn quốc gia Núi Chúa có hệ bò sát lưỡng cư hết sức đa dạng. Trong những năm qua các nhà nghiên cứu bò sát lưỡng cư đã công bố 2 loài bò sát mới ở Vườn quốc gia này gồm: Thằn lằn ngón cao văn sung&nbsp;<a href=\"http://vncreatures.net/chitiet.php?page=1&amp;loai=1&amp;img=1&amp;ID=5641\" class=\"aLink09\" style=\"font-family: Arial; color: rgb(0, 0, 255);\"><em>Cyrtodactylus caovansungi</em></a>, Thằn lằn chân lá arronbauer&nbsp;<em><a href=\"http://vncreatures.net/chitiet.php?page=1&amp;loai=1&amp;img=1&amp;ID=6211\" class=\"aLink09\" style=\"font-family: Arial; color: rgb(0, 0, 255);\">Dixonius aaronbaueri</a></em>&nbsp;...</p>', '...', '...', 0, 1, 1, '2020-08-10 21:33:25', '2020-09-15 12:17:07', 0, NULL, NULL, 0, ''),
(30, 'catalog/BachMaDongVat/220207abf6a08dae617a579be1c06612.jpg', 5, 'Hệ Động Vật - Bạch Mã', '<p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><br></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\"><i style=\"margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">* Khu hệ côn trùng (Insecta)</span></i></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">Thành phần côn trùng ở Vườn quốc gia Bạch Mã đã được bổ sung các loài côn trùng ít gặp, quý hiếm, chỉ xuất hiện theo mùa và ở độ cao nhất định. Trong số này có thể kể đến các loài côn trùng sống ở độ cao lớn và hoạt động về đêm như: bọ que, côn trùng sống ở rừng rậm như bổ củi, bổ củi giả, cánh cứng ăn lá..., các loại bướm rừng thuộc họ Amathusidae và bướm tro thuộc họ Lycaenidae (Lepidoptera) côn trùng sống ở rừng rậm, hoạt động về đêm, bay nhanh, ưa ánh sáng đèn như ngài diều hâu, bướm mắt rắn.&nbsp;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">Đối với hệ sinh thái ở Vườn quốc gia Bạch Mã, côn trùng có vai trò bổ sung nguồn gen quý hiếm, cải tạo đất, thụ phấn, đóng vai trò thiên địch, cung cấp mật ong, cung cấp thức ăn cho động vật lớn. Ngoài tác động tích cực, côn trùng cũng gây hại như ăn cá, đục thân cây cối và ngoại ký sinh, hút máu, truyền bệnh.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\"><i style=\"margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">* Khu hệ cá (Pisces)<o:p></o:p></span></i></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">Cho đến nay, ở khu hệ cá Vườn quốc gia Bạch Mã đã xác định được 57 loài thuộc 48 giống, 17 họ, 6 bộ cá nước ngọt khác nhau.&nbsp;</span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span lang=\"PT-BR\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">Trong 57 loài cá thống kê đã xác định được 8 loài cá có tên trong Sách đỏ Việt Nam năm 2000 với các tình trạng khác nhau: cá chình hoa (Anguilla marmorata) bậc R, cá ngạnh (Cranoglanis bouderius) bậc V, cá quả (Ophiocephalus striatus) bậc T.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\"><i style=\"margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\"><span lang=\"PT-BR\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">* Khu hệ lưỡng cư và bò sát (Amphibia và Reptilia)<o:p></o:p></span></i></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span lang=\"PT-BR\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">Kết quả của nhiều công trình nghiên cứu gần đây cho thấy tại Vườn quốc gia Bạch Mã có 52 loài lưỡng cư (Amphibia) và bò sát (Reptilia) thuộc 3 bộ và 15 họ và 31 giống khác nhau.&nbsp;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span lang=\"PT-BR\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">Trong số các loài lưỡng cư đã thống kê có 3 loài quý hiếm nằm trong danh mục Sách đỏ Việt Nam và cần được bảo vệ: cóc mắt chân dài (Megophrys longipes), chàng Anđécson (Rana andersonii), ếch cây chân đen (Phacphorus nigropalmatus). Có 8 loài bò sát được ghi vào Sách đỏ Việt Nam năm 2000.&nbsp;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span lang=\"PT-BR\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">Trong các loài lưỡng cư - bò sát đã biết có nhiều loài có giá trị về sinh thái, kinh tế và dược liệu<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\"><i style=\"margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\"><span lang=\"PT-BR\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">* Khu hệ chim (Aves)<o:p></o:p></span></i></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span lang=\"PT-BR\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">Hiện nay Khu hệ chim Vườn quốc gia Bạch Mã đã ghi nhận được 358 loài, 186 giống, 55 họ và 15 bộ. Các loài, giống, họ phân bố không đồng đều trong các bộ chim khác nhau. Tính đa dạng của các loài chim thể hiện ở các bậc taxon, đặc biệt là ở taxon bậc giống.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span lang=\"PT-BR\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">Các loài chim đặc hữu tiêu biểu có thể kể đến như: gà so Trung bộ (Arborophila merlini), gà lôi lam mào trắng (Lophura edwardsi), trĩ sao (Rheinartia ocellata), đuôi cụt bụng vằn (Pitta elliota), chim khách đuôi cờ (Temnurus temnurus), thầy chùa đít đỏ (Megalaima lagrandieri), chim mào vàng (Melanochlora sultanea gaeti), khướu mỏ dài (Jabouillera danjoui), hút mật đuôi nhọn (Aethopyga christinae).&nbsp;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span lang=\"PT-BR\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">Trong tổng số 358 loài chim ở Vườn quốc gia Bạch Mã có 18 loài có tên trong Sách đỏ Việt Nam và trong Danh lục đỏ Việt Nam (2002) và 26 loại có tên trong Danh lục thực vật, động vật cấm buôn bán, săn bắt của Nghị định 48/2002/NĐ-CP.&nbsp;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span lang=\"PT-BR\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">Cũng giống với các động vật khác, chim ở Vườn quốc gia Bạch Mã có ý nghĩa lớn cả về sinh thái, nguồn gen, thiên địch lẫn thực phẩm và nguyên liệu.<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\"><i style=\"margin: 0px; padding: 0px; border: 0px; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\"><span lang=\"PT-BR\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">* Khu hệ thú (Mammalia)<o:p></o:p></span></i></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span lang=\"PT-BR\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">Kết quả điều tra và phân loại thú ở Vườn quốc gia Bạch Mã cho đến nay đã xác định được 132 loài thú nằm trong 72 giống, 28 họ và 10 bộ.&nbsp;<o:p></o:p></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span lang=\"PT-BR\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">Vườn quốc gia Bạch Mã có gần đủ bộ thú trên cạn của Việt Nam, trừ 2 bộ guốc ngón lẻ (Perissodactyla) và bộ có vòi (Proboscidae).</span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span lang=\"PT-BR\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\">Thú là lớp động vật khai thác, là mắt xích thức ăn sau cùng trong dòng vật chất của hệ sinh thái, có vai trò điều tiết, kiểm soát số lượng các loài, góp phần điều hòa cân bằng sinh thái của Vườn quốc gia Bạch Mã. Bên cạnh vai trò sinh thái, thú còn có ý nghĩa lớn về mặt thực phẩm, dược liệu, văn hóa, du lịch và thể thao giải trí.</span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span lang=\"PT-BR\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\"><a href=\"https://docs.google.com/document/d/1YMpBF3FtFIX2U5i1OuOkE4o5E-ZvZS4Si8J8E03DqXc/edit?usp=sharing\" target=\"_blank\" style=\"background-color: rgb(255, 255, 255); font-family: Arial, Helvetica, sans-serif; font-size: 13px; text-indent: 37.7953px;\">Link chi tiết</a><br></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span lang=\"PT-BR\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\"><br></span></p><p class=\"MsoNormal\" style=\"margin: 6pt 0cm 0pt; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; text-indent: 1cm; color: rgb(0, 0, 0); text-align: justify;\"><span lang=\"PT-BR\" style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: 10pt; line-height: inherit; font-family: Arial; vertical-align: baseline;\"><br></span></p>', '...', '...', 0, 1, 1, '2020-08-10 21:35:08', '2020-09-15 12:16:27', 0, NULL, NULL, 0, ''),
(31, 'catalog/BaViDongVat/843f12a99d69bf10a92a20d86035a67b.jpg', 5, 'Hệ động vật - Ba Vì', '<p><span style=\"color: rgb(0, 0, 0); font-family: \" times=\"\" new=\"\" roman\",=\"\" times;=\"\" font-size:=\"\" large;\"=\"\">heo kết quả điều tra bổ sung mới nhất năm 2008, Khu hệ động vật có xương sống (ĐVCXS) ở VQG Ba Vì&nbsp; thống kê được 342 loài.&nbsp;</span><span style=\"color: rgb(0, 0, 0); font-family: \" times=\"\" new=\"\" roman\",=\"\" times;=\"\" font-size:=\"\" large;\"=\"\">&nbsp;Yếu tố đặc hữu của khu hệ ĐVCXS ở Ba Vì ở 2 lớp Bò sát và Lưỡng thê.&nbsp;</span><span style=\"color: rgb(0, 0, 0); font-family: \" times=\"\" new=\"\" roman\",=\"\" times;=\"\" font-size:=\"\" large;\"=\"\">Nhóm động vật quí hiếm ở VQG Ba Vì có 66 loài, phần lớn&nbsp; là loài ĐVR nhỏ, hoặc trung bình.</span></p><p><strong style=\"border: 0px; margin: 0px; padding: 0px; color: rgb(0, 0, 0); font-family: \" times=\"\" new=\"\" roman\",=\"\" times;=\"\" font-size:=\"\" large;\"=\"\">Thực trạng bảo vệ động vật rừng:</strong><span style=\"color: rgb(0, 0, 0); font-family: \" times=\"\" new=\"\" roman\",=\"\" times;=\"\" font-size:=\"\" large;\"=\"\">&nbsp;do địa hình vùng Ba Vì độc lập nên việc di cư của các thú rừng từ nơi khác tới là rất hạn chế, dễ bị săn bắt.&nbsp;</span></p><p><span style=\"color: rgb(0, 0, 0); font-family: \" times=\"\" new=\"\" roman\",=\"\" times;=\"\" font-size:=\"\" large;\"=\"\">Theo kết quả điều tra chuyên đề của Vườn, đã phát hiện được 552 loài côn trùng thuộc 364 giống, 65 họ, 14 bộ.</span><span style=\"color: rgb(0, 0, 0); font-family: \" times=\"\" new=\"\" roman\",=\"\" times;=\"\" font-size:=\"\" large;\"=\"\"><br></span><br></p>', '...', '...', 0, 1, 1, '2020-08-11 15:29:25', '2020-09-15 12:16:10', 0, NULL, NULL, 0, ''),
(32, 'catalog/RungCucPhuongDongVat/cefe3e96e9155e557dc995b494f2a301.jpg', 5, 'Hệ động vật - RỪNG CÚC PHƯƠNG', '<p>Rừng Cúc Phương có hệ sinh thái khá phong phú và đa dạng, gồm 97 loài thú (trong đó nổi bật nhất là các loài khỉ châu Á), 137 loài chim, 76 loài bò sát, 46 loài lưỡng cư, 11 loài cá và hàng ngàn loài côn trùng. Nhiều loài nằm trong Sách đỏ Việt Nam.<br></p><p>Cúc Phương là nơi sinh sống của một số quần thể thú quan trọng về mặt bảo tồn, trong đó có loài linh trưởng đang bị đe dọa tuyệt chủng trên toàn cầu ở mức đe dọa cực kỳ nguy cấp là voọc quần đùi trắng và loài sẽ bị nguy cấp trên toàn cầu là Cầy vằn, loài báo hoa mai là loài bị đe dọa ở mức quốc gia. Cúc Phương cũng có hơn 40 loài dơi đã được ghi nhận tại đây.</p><p>động vật rừng Cúc Phương&nbsp;</p><p>Đến nay, đã có 313 loài chim được xác định ở Cúc Phương. Cúc Phương nằm tại vị trí tận cùng phía bắc của vùng chim đặc hữu vùng đất thấp Trung Bộ, tuy nhiên, chỉ có một loài có vùng phân bố giới hạn được ghi nhận tại đây là khướu mỏ dài. Cúc Phương được công nhận là một vùng chim quan trọng tại Việt Nam.</p><p>Nhiều nhóm sinh vật khác cũng đã được điều tra, nghiên cứu ở Cúc Phương trong đó có ốc. Khoảng 111 loài ốc đã được ghi nhận trong một chuyến điều tra gần đây trong đó có 27 loài đặc hữu[9]. Khu hệ cá trong các hang động ngầm cũng đã được nghiên cứu, ít nhất đã có một loài cá được ghi nhận tại đây là loài đặc hữu đối với vùng núi đá vôi, đó là Cá niết hang Cúc Phương. Cúc Phương đã xác định được 280 loài bướm, 7 loài trong số đó lần đầu tiên được ghi nhận ở Việt Nam tại Cúc Phương vào năm 1998.</p>', '...', '...', 0, 1, 1, '2020-08-11 15:34:55', '2020-09-15 12:17:15', 0, NULL, NULL, 0, ''),
(33, 'catalog/BaViDongVat/79ac70b931514d3d4e5e54d17a250c3a.jpg', 5, 'Hệ động vật - Ba Bể', '<p>Động vật:<br></p><p>Khu hệ động vật rất phong phú với 81 loài thú, 27 loài bò sát, 17 loài lưỡng cư, 322 loài chim, 106 loài cá, 553 loài côn trùng và nhện. Trong đó có nhiều loài có giá trị, quý hiếm đã được Việt Nam và Quốc tế ghi vào Sách Đỏ. Về khu hệ cá, hồ Ba Bể và các sông suối phụ cận có đến 106 loài cá được xác định phong phú nhất ở Việt Nam, bởi các hồ khác như hồ Lắc cũng chỉ có 35 loài, hồ Tây - 36 loài, hồ Châu Trúc - 47 loài... Vườn Quốc gia Ba Bể còn có ý nghĩa đặc biệt quan trọng với sự có mặt của một số loài đang bị đe dọa trên toàn cầu như Voọc đen má trắng Trachypithecus francoisi và Cầy vằn bắc Hemigalus owstoni, mặc dù vậy số lượng Voọc đen má trắng hiện còn tồn tại trong khu vực rất ít.</p><p>Có 322 loài chim được ghi nhận trong những cuộc khảo sát mới đây ở Vườn quốc gia. Các quần thể chim của Ba Bể bổ sung một số loài của danh mục cấm; 7 loài bị cấm khai thác thuộc khu hệ rừng cận nhiệt đới Sino - Himalayan gồm: Cắt nhỏ bụng trắng - Microhierax melanoleucos, Dẻ cùi vàng - Kitta flavirostris, Hoét ngực đen - Turdus dissimilis, Bông lau trung quốc - Pycnonotus sinensis, Cành cạch đen -Hypsipetes leucocephalus, Cành cạch núi - Hypsipetes macclellandi, Sáo đá đuôi ngắn - Sturnus malabarica</p><p>Sáu loài giới hạn trong vùng Rừng Nhiệt đới ẩm Đông Dương bao gồm:&nbsp; Gà tiền mặt vàng, Thầy chùa đít đỏ, Đớp ruồi Hải Nam, Cành cạch nhỏ, Khướu bạc má, Khướu khoang cổ,</p><p>Ba loài giới hạn phân bố trong Vùng Nhiệt đới Khô Indo – Malay gồm: Thầy chùa bụng nâu Megalaima lineata, Chim khách Crypsirina temia, Bông lau tai trắng Pycnonotus blanfordi</p><p>Các quần thể chim thấy ở rừng thường xanh, rừng thứ sinh và môi trường nhân sinh tương đối khác về thành phần loài. Chim ăn thịt tìm thấy trong cuộc khảo sát tương đối đa dạng với sự có mặt của 6 loài kiếm ăn ngày và 3 loài ăn đêm. Diều ăn ong - Pernis ptilorhychus, Diều hoa - Spilornis cheela, Ưng xám - Accipiter badius, Cắt nhỏ bụng trắng- Microhierax melanoleucos, Cắt lưng hung - Falco tinnunlus, Cú vọ - Glaucidium cuculoides, Cú vọ mặt trắng - Glaucidium brodiei, Cú mèo latusơ - Otus spilocephalus lutouchei</p><p>Sự phong phú về loài chim ăn thịt cho thấy rằng điều kiện môi trường khu vực nghiên cứu là tốt. Tuy nhiên có hai loài chim bị đe doạ biến mất khỏi khu vực, đó là Hồng hoàng Buceros bicornis và Diều cá đầu xám Icthyophaga icthyaetus trước đây có nhưng gần đây không thấy nữa.</p><p>Có 44 loài bò sát và ếch nhái được tìm thấy trong Vườn quốc gia trong những cuộc khảo sát gần đây, những loài trong danh mục bảo vệ, trong đó có các loài quí hiếm như: Tắc kè Gekko gecko, Ô rô vẩy Acanthosaura lepidogaster Rắn sọc đuôi khoanh Orthriophis moellendorffii, Rồng đất Physignathus cocincinus</p>', '...', '...', 0, 1, 1, '2020-08-11 15:41:43', '2020-09-15 12:16:04', 0, NULL, NULL, 0, ''),
(34, 'catalog/CatBaDongVat/2e0baa27ce950b4c6380088205703195.jpg', 5, 'Hệ động vật - Cát Bà', '<p><br></p><p>Hệ sinh thái tại vườn quốc gia Cát Bà</p><p>Vườn Quốc gia Cát Bà là nơi hội tụ nhiều hệ sinh thái khác nhau: hệ sinh thái rừng ngập mặn, hệ sinh thái rừng biển với các rạn san hô, hệ sinh thái, hệ sinh thái rừng trên núi đá vôi. Nơi đây có hệ động, thực vật phong phú với 282 loài động vật sống trong rừng, 538 loài động vật sống ở đáy biển, 196 loài cá biển, 177 loài san hô, 75 loài rong biển, 771 loài cây cỏ trên cạn, 741 loài thực vật khác nhau, có nhiều loài cây gỗ quý như: lim xẹt, dẻ hoa, gõ trắng, chò đãi, những cây chỉ có ở dãy núi Himalaya, thực vật ngập mặn có 23 loài, thực vật phù du có 199 loài...</p><p>Vườn Quốc gia Cát Bà cũng có chim yến và rùa biển được chăm sóc và bảo tồn. Ngoài các loài động vật quý hiếm, vườn Quốc gia Cát Bà còn có nhiều cây thuốc quý, có những cây cần được bảo tồn như chò chỉ, lát hoa, kim giao, trai lý, cọ Bắc Sơn.&nbsp;</p><p>Quạ khoang - động vật quý hiếm</p><p>Đặc biệt quần đảo Cát Bà còn có 60 loài đặc hữu quý hiếm, được đưa vào Sách đỏ Việt Nam như: voọc quần đùi trắng, quạ khoang, sóc đen...&nbsp;</p><p>Khu dự trữ sinh quyển Cát Bà có hai vùng lõi được bảo tồn rất nghiêm ngặt, nơi không có tác động nào của con người trừ các hoạt động nghiên cứu và giám sát, đó là vùng lõi phía Đông Nam và vùng lõi phía Tây Bắc. Vườn quốc gia Cát Bà là một cụm sinh thái thiên nhiên không thể tách rời. Là nơi vừa có rừng, vừa có biển và nguồn tài nguyên thiên nhiên phong phú, cảnh quan đẹp, nhiều loài động, thực vật quý hiếm, nơi đây còn có nhiều di tích khảo cổ học thuộc thời kỳ đồ đá mới, những di tích văn hóa Hạ Long, nơi có dấu tích của người Việt cổ..</p>', '...', '...', 0, 1, 1, '2020-08-11 15:50:46', '2020-09-15 12:16:40', 0, NULL, NULL, 0, ''),
(35, 'catalog/TramChimDongVat/8fbecc92ed785d3495a7dd8de571488b.jpg', 5, 'Hệ động vật - Tràm Chim', '<p>Hệ sinh thái động vật<br></p><p>Vườn quốc gia Tràm Chim, có diện tích 7.588 ha, thuộc huyện Tam Nông tỉnh Đồng Tháp. Đây là nơi cư trú của trên 100 loài động vật có xương sống, 40 loài cá và 147 loài chim nước. Trong đó, có 13 loài chim quý hiếm của thế giới. Đặc biệt là một loài chim hạc còn gọi là sếu đầu đỏ (Grus antigone) hay sếu cổ trụi</p>', '...', '...', 0, 1, 1, '2020-08-11 15:58:52', '2020-09-15 12:17:23', 0, NULL, NULL, 0, ''),
(36, 'catalog/ChuYangSInDongVat/e534b384b9d33999ae60ca8a00c85ea4.jpg', 5, 'Hệ động vật - Chư Yang Sin', '<p>Vườn quốc gia Chư Yang Sin là điểm cuối cùng của dãy trường sơn thuộc Nam Tây Nguyên là điểm nóng về bảo tồn đa dạng sinh học. Theo như điều tra bước đầu đã có 46 loài thú, 212 loài chim. 17 loài thú đang bị đe doạ tuyệt chủng. Nơi đây sẽ là mẫu chuẩn hệ sinh thái Tây Nguyên. Là một phần của Vùng chim đặc hữu của cao nguyên Đà Lạt, tại đây có tới 8 loài chim đặc hữu và phân bố hẹp, trong đó có hai loài đang bị đe doạ toàn cầu ở mức Nguy cấp là Khướu đầu đen má xám Garrulax yersini và Mi núi bà Crocias langbianis. Hơn thế nữa, CYS với diện tích rừng rộng lớn, địa hình phức tạp là nơi sinh sống của nhiều quần thể của hai loài linh trưởng có ý nghĩa bảo tồn trên toàn cầu là Chà vá chân đen Pygathrix nigripes và Vượn má hung Nomascus gabriellae. Bò tót Bos gaurus, Beo lửa Catopuma temminckii.<br></p><p>Vườn quốc gia Chư Yang Sin là khu vực rất đa dạng các loài bò sát, lưỡng cư. Với kiểu địa hình núi cao, vực sâu và suối, thác là nơi sống rất phù hợp cho các loài bò sát, lưỡng cư. Mới đây các nhà nghiên cứu bò sát đã công bố loài Thằn lằn ngón ziegler Cyrtodactylus ziegleri và ếch cây chư yang sin Rhacophorus calcaneus là 2 loài mới thuộc Vườn quốc gia.</p><p>Với hơn 200 loài bướm ngày đã được ghi nhận và phân loại Chư Yang Sin là quê hương của các loài bướm thuộc cao nguyên trung phần Việt Nam. Các loài bướm đẹp, đặc hữu chỉ tìm thấy ở Vườn quốc gia như Bướm cối xay gió Atrophaneura polyeuctes, Bướm phượng cánh chim chấm rời Troides aeacus, Bướm báo hoa đỏ Cethosia biblis, Bướm phù thủy rừng xanh Hestina nama</p><p><br></p><p><br></p>', '...', '...', 0, 1, 1, '2020-08-11 16:07:33', '2020-09-15 12:16:51', 0, NULL, NULL, 0, '');
INSERT INTO `post` (`post_id`, `image`, `author_id`, `title`, `content`, `link`, `tag`, `featured`, `sort_order`, `status`, `date_added`, `date_modified`, `parent_id`, `summary`, `sub_title`, `top`, `menu`) VALUES
(37, 'catalog/Anh_Dai_Dien/060fc6f501edb760ec20d93e48ff960c.jpg', 5, 'Giới Thiệu Chung về các công viên quốc gia ở Việt Nam', '<h2 linux=\"\" libertine\",=\"\" \"palatino=\"\" linotype\",=\"\" \"times=\"\" new=\"\" roman\",=\"\" serif;=\"\" line-height:=\"\" 1.3;\"=\"\" style=\"font-family: \" open=\"\" sans\",=\"\" \"helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);=\"\" margin:=\"\" 1em=\"\" 0px=\"\" 0.25em;=\"\" font-size:=\"\" 1.5em;=\"\" padding:=\"\" 0px;=\"\" overflow:=\"\" hidden;=\"\" border-bottom:=\"\" 1px=\"\" solid=\"\" rgb(162,=\"\" 169,=\"\" 177);\"=\"\"><span class=\"mw-headline\" id=\"Định_nghĩa\">Định nghĩa</span></h2><h1 id=\"H1Title\" class=\"title-site\" open=\"\" sans\",=\"\" sans-serif;=\"\" font-weight:=\"\" 600;=\"\" color:=\"\" rgb(51,=\"\" 51,=\"\" 51);\"=\"\" style=\"font-size: 26px; margin-top: 0px; margin-bottom: 15px; font-family: \" \"helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;\"=\"\"><p style=\"margin-top: 0.5em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">Theo định nghĩa của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Li%C3%AAn_minh_B%E1%BA%A3o_t%E1%BB%93n_Thi%C3%AAn_nhi%C3%AAn_Qu%E1%BB%91c_t%E1%BA%BF\" title=\"Liên minh Bảo tồn Thiên nhiên Quốc tế\" style=\"background-image: none; color: rgb(11, 0, 128);\">Liên minh Quốc tế Bảo tồn Thiên nhiên và Tài nguyên Thiên nhiên</a>&nbsp;(IUCN)<span id=\"cite_ref-1\" class=\"reference\" style=\"font-size: 11.2px; line-height: 1em; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; unicode-bidi: isolate;\"><a href=\"https://vi.wikipedia.org/wiki/V%C6%B0%E1%BB%9Dn_qu%E1%BB%91c_gia#cite_note-1\" style=\"background-image: none; color: rgb(11, 0, 128);\">[1]</a></span>&nbsp;thì vườn quốc gia là:</p><dl style=\"margin-top: 0.2em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\"><dd style=\"line-height: 1.42857; margin-left: 1.6em; margin-bottom: 0.1em; margin-right: 0px;\"><i>Khu vực tự nhiên của vùng đất và/hoặc vùng biển, được chọn để</i></dd><dd style=\"line-height: 1.42857; margin-left: 1.6em; margin-bottom: 0.1em; margin-right: 0px;\"><i>(a) bảo vệ tình trạng nguyên vẹn sinh thái của một hay nhiều hệ sinh thái cho các thế hệ hiện tại và tương lai,</i></dd><dd style=\"line-height: 1.42857; margin-left: 1.6em; margin-bottom: 0.1em; margin-right: 0px;\"><i>(b) loại bỏ việc khai thác hay chiếm giữ không thân thiện đối với các mục đích của việc chọn lựa khu vực và</i></dd><dd style=\"line-height: 1.42857; margin-left: 1.6em; margin-bottom: 0.1em; margin-right: 0px;\"><i>(c) chuẩn bị cơ sở cho các cơ hội tinh thần, khoa học, giáo dục, giải trí và tham quan, tất cả các cơ hội đó phải có tính tương thích về văn hóa và môi trường.</i></dd></dl><p style=\"margin-top: 0.5em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">Theo quyết định số 186/2006/QĐ-TTg ngày 14 tháng 8 năm 2006 của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Th%E1%BB%A7_t%C6%B0%E1%BB%9Bng_Vi%E1%BB%87t_Nam\" title=\"Thủ tướng Việt Nam\" style=\"background-image: none; color: rgb(11, 0, 128);\">Thủ tướng Chính phủ Việt Nam</a><span id=\"cite_ref-2\" class=\"reference\" style=\"font-size: 11.2px; line-height: 1em; position: relative; vertical-align: baseline; top: -0.5em; white-space: nowrap; unicode-bidi: isolate;\"><a href=\"https://vi.wikipedia.org/wiki/V%C6%B0%E1%BB%9Dn_qu%E1%BB%91c_gia#cite_note-2\" style=\"background-image: none; color: rgb(11, 0, 128);\">[2]</a></span>, thay thế cho Quyết định số 08/2001/QĐ-TTg ngày 11 tháng 01 năm 2001 về Quy chế quản lý rừng thì vườn quốc gia là một dạng&nbsp;<a href=\"https://vi.wikipedia.org/wiki/R%E1%BB%ABng_%C4%91%E1%BA%B7c_d%E1%BB%A5ng\" title=\"Rừng đặc dụng\" style=\"background-image: none; color: rgb(11, 0, 128);\">rừng đặc dụng</a>, được xác định trên các tiêu chí sau:</p><ul w=\"\" skins=\"\" vector=\"\" resources=\"\" skins.vector.styles=\"\" images=\"\" bullet-icon.svg?d4515\");=\"\" margin:=\"\" 0.3em=\"\" 0px=\"\" 1.6em;=\"\" padding:=\"\" 0px;=\"\" color:=\"\" rgb(32,=\"\" 33,=\"\" 34);=\"\" font-family:=\"\" sans-serif;=\"\" font-size:=\"\" 14px;\"=\"\" style=\"list-style-image: url(\" \");\"=\"\"><li style=\"margin-bottom: 0.1em;\">Vườn quốc gia là&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=Khu_v%E1%BB%B1c_t%E1%BB%B1_nhi%C3%AAn&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Khu vực tự nhiên (trang chưa được viết)\" style=\"background-image: none; color: rgb(165, 88, 88);\">khu vực tự nhiên</a>&nbsp;trên&nbsp;<a href=\"https://vi.wikipedia.org/wiki/%C4%90%E1%BA%A5t_li%E1%BB%81n\" title=\"Đất liền\" style=\"background-image: none; color: rgb(11, 0, 128);\">đất liền</a>&nbsp;hoặc ở&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=V%C3%B9ng_%C4%91%E1%BA%A5t_ng%E1%BA%ADp_n%C6%B0%E1%BB%9Bc&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Vùng đất ngập nước (trang chưa được viết)\" style=\"background-image: none; color: rgb(165, 88, 88);\">vùng đất ngập nước</a>,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/%C4%90%E1%BA%A3o\" title=\"Đảo\" style=\"background-image: none; color: rgb(11, 0, 128);\">hải đảo</a>, có&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Di%E1%BB%87n_t%C3%ADch\" title=\"Diện tích\" style=\"background-image: none; color: rgb(11, 0, 128);\">diện tích</a>&nbsp;đủ lớn được xác lập để bảo tồn một hay nhiều&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=H%E1%BB%87_sinh_th%C3%A1i_%C4%91%E1%BA%B7c_tr%C6%B0ng&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Hệ sinh thái đặc trưng (trang chưa được viết)\" style=\"background-image: none; color: rgb(165, 88, 88);\">hệ sinh thái đặc trưng</a>&nbsp;hoặc đại diện không bị tác động hay chỉ bị tác động rất ít từ bên ngoài; bảo tồn các loài&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=Sinh_v%E1%BA%ADt_%C4%91%E1%BA%B7c_h%E1%BB%AFu&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Sinh vật đặc hữu (trang chưa được viết)\" style=\"background-image: none; color: rgb(165, 88, 88);\">sinh vật đặc hữu</a>&nbsp;hoặc&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=%C4%90ang_nguy_c%E1%BA%A5p&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Đang nguy cấp (trang chưa được viết)\" style=\"background-image: none; color: rgb(165, 88, 88);\">đang nguy cấp</a>.</li><li style=\"margin-bottom: 0.1em;\">Vườn quốc gia được quản lý, sử dụng chủ yếu phục vụ cho việc bảo tồn rừng và&nbsp;<a href=\"https://vi.wikipedia.org/wiki/H%E1%BB%87_sinh_th%C3%A1i_r%E1%BB%ABng\" title=\"Hệ sinh thái rừng\" style=\"background-image: none; color: rgb(11, 0, 128);\">hệ sinh thái rừng</a>, nghiên cứu khoa học, giáo dục môi trường và&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Du_l%E1%BB%8Bch_sinh_th%C3%A1i\" title=\"Du lịch sinh thái\" style=\"background-image: none; color: rgb(11, 0, 128);\">du lịch sinh thái</a>.</li><li style=\"margin-bottom: 0.1em;\">Vườn quốc gia được xác lập dựa trên các tiêu chí và chỉ số: về hệ sinh thái đặc trưng; các loài động vật, thực vật đặc hữu; về diện tích tự nhiên của vườn và&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=T%E1%BB%B7_l%E1%BB%87_di%E1%BB%87n_t%C3%ADch_%C4%91%E1%BA%A5t_n%C3%B4ng_nghi%E1%BB%87p&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Tỷ lệ diện tích đất nông nghiệp (trang chưa được viết)\" style=\"background-image: none; color: rgb(165, 88, 88);\">tỷ lệ diện tích đất nông nghiệp</a>,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/%C4%90%E1%BA%A5t_th%E1%BB%95_c%C6%B0\" title=\"Đất thổ cư\" style=\"background-image: none; color: rgb(11, 0, 128);\">đất thổ cư</a>&nbsp;so với diện tích tự nhiên của vườn</li></ul><ul w=\"\" skins=\"\" vector=\"\" resources=\"\" skins.vector.styles=\"\" images=\"\" bullet-icon.svg?d4515\");=\"\" margin:=\"\" 0.3em=\"\" 0px=\"\" 1.6em;=\"\" padding:=\"\" 0px;=\"\" color:=\"\" rgb(32,=\"\" 33,=\"\" 34);=\"\" font-family:=\"\" sans-serif;=\"\" font-size:=\"\" 14px;\"=\"\" style=\"list-style-image: url(\" \");\"=\"\"><img src=\"http://localhost/images-epj1-blog-parks/catalog/%E1%BA%A3nh%20c%C3%A1t%20b%C3%A0/93c23afaf9c61b75e73f94054df49b5f.gif\"></ul></h1>', '...', '...', 0, 2, 1, '2020-08-17 07:21:32', '2020-09-15 12:15:34', 0, NULL, NULL, 0, ''),
(39, 'catalog/Anh_Dai_Dien/81f8b7e937f733c926382b0708f1436d.jpg', 5, 'Giới thiệu về công viên quốc gia chung', '<p><b style=\"color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">Vườn quốc gia</b><span style=\"color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">&nbsp;là một khu vực đất hay biển được bảo tồn bằng các quy định pháp luật của chính quyền sở tại. Vườn quốc gia được bảo vệ nghiêm ngặt khỏi sự khai thác, can thiệp bởi con người. Vườn quốc gia thường được thành lập ở những khu vực có địa mạo độc đáo có giá trị khoa học hoặc những khu vực có hệ sinh thái phong phú, có nhiều loài&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/%C4%90%E1%BB%99ng_v%E1%BA%ADt\" title=\"Động vật\" style=\"color: rgb(11, 0, 128); background-image: none; background-color: rgb(255, 255, 255); font-family: sans-serif; font-size: 14px;\">động</a><span style=\"color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">-</span><a href=\"https://vi.wikipedia.org/wiki/Th%E1%BB%B1c_v%E1%BA%ADt\" title=\"Thực vật\" style=\"color: rgb(11, 0, 128); background-image: none; background-color: rgb(255, 255, 255); font-family: sans-serif; font-size: 14px;\">thực vật</a><span style=\"color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">&nbsp;có nguy cơ tuyệt chủng cao cần được bảo vệ nghiêm ngặt trước sự khai thác của con người. Các vườn quốc gia là một khu vực được bảo vệ theo quy định của&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/Li%C3%AAn_minh_B%E1%BA%A3o_t%E1%BB%93n_Thi%C3%AAn_nhi%C3%AAn_Qu%E1%BB%91c_t%E1%BA%BF\" title=\"Liên minh Bảo tồn Thiên nhiên Quốc tế\" style=\"color: rgb(11, 0, 128); background-image: none; background-color: rgb(255, 255, 255); font-family: sans-serif; font-size: 14px;\">IUCN</a><span style=\"color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">&nbsp;loại II. Vườn quốc gia lớn nhất thế giới là&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/C%C3%B4ng_vi%C3%AAn_qu%E1%BB%91c_gia_%C4%90%C3%B4ng_B%E1%BA%AFc_Greenland\" class=\"mw-redirect\" title=\"Công viên quốc gia Đông Bắc Greenland\" style=\"color: rgb(11, 0, 128); background-image: none; background-color: rgb(255, 255, 255); font-family: sans-serif; font-size: 14px;\">Vườn quốc gia Đông Bắc đảo Greenland</a><span style=\"color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">&nbsp;được thành lập năm 1974.</span></p><p style=\"margin-top: 0.5em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">Theo định nghĩa của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Li%C3%AAn_minh_B%E1%BA%A3o_t%E1%BB%93n_Thi%C3%AAn_nhi%C3%AAn_Qu%E1%BB%91c_t%E1%BA%BF\" title=\"Liên minh Bảo tồn Thiên nhiên Quốc tế\" style=\"color: rgb(11, 0, 128); background-image: none;\">Liên minh Quốc tế Bảo tồn Thiên nhiên và Tài nguyên Thiên nhiên</a>&nbsp;(IUCN)&nbsp;thì vườn quốc gia là:</p><dl style=\"margin-top: 0.2em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\"><dd style=\"margin-left: 1.6em; margin-bottom: 0.1em; margin-right: 0px;\"><i>Khu vực tự nhiên của vùng đất và/hoặc vùng biển, được chọn để</i></dd><dd style=\"margin-left: 1.6em; margin-bottom: 0.1em; margin-right: 0px;\"><i>(a) bảo vệ tình trạng nguyên vẹn sinh thái của một hay nhiều hệ sinh thái cho các thế hệ hiện tại và tương lai,</i></dd><dd style=\"margin-left: 1.6em; margin-bottom: 0.1em; margin-right: 0px;\"><i>(b) loại bỏ việc khai thác hay chiếm giữ không thân thiện đối với các mục đích của việc chọn lựa khu vực và</i></dd><dd style=\"margin-left: 1.6em; margin-bottom: 0.1em; margin-right: 0px;\"><i>(c) chuẩn bị cơ sở cho các cơ hội tinh thần, khoa học, giáo dục, giải trí và tham quan, tất cả các cơ hội đó phải có tính tương thích về văn hóa và môi trường.</i></dd></dl><h2 style=\"color: rgb(0, 0, 0); margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); font-size: 1.5em; font-family: \" linux=\"\" libertine\",=\"\" \"palatino=\"\" linotype\",=\"\" \"times=\"\" new=\"\" roman\",=\"\" serif;=\"\" line-height:=\"\" 1.3;\"=\"\"><span class=\"mw-headline\" id=\"Lịch_sử\">Lịch sử</span></h2><p style=\"margin-top: 0.5em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">Ý tưởng về việc thiết lập cảnh quan thiên nhiên đáng được bảo vệ cụ thể nào đó dưới sự bảo vệ có nguồn gốc từ đầu thế kỷ 19. Nhà thơ người Anh&nbsp;<a href=\"https://vi.wikipedia.org/wiki/William_Wordsworth\" title=\"William Wordsworth\" style=\"color: rgb(11, 0, 128); background-image: none;\">William Wordsworth</a>&nbsp;năm&nbsp;<a href=\"https://vi.wikipedia.org/wiki/1810\" title=\"1810\" style=\"color: rgb(11, 0, 128); background-image: none;\">1810</a>&nbsp;đã viết về&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=H%E1%BB%93_District&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Hồ District (trang chưa được viết)\" style=\"color: rgb(165, 88, 88); background-image: none;\">hồ District</a>&nbsp;như là&nbsp;<i>\"một loại tài sản quốc gia trong đó mọi người có quyền và lợi ích, những người có mắt để nhận biết và trái tim để thưởng thức\"</i>. Họa sĩ Mỹ&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=George_Catlin&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"George Catlin (trang chưa được viết)\" style=\"color: rgb(165, 88, 88); background-image: none;\">George Catlin</a>&nbsp;năm 1832, trong những chuyến đi về miền tây nước Mỹ, đã viết rằng thổ dân Bắc Mỹ tại Hoa Kỳ cần được bảo toàn:&nbsp;<i>bằng một số chính sách bảo vệ lớn của chính quyền... trong khu vườn tráng lệ... Một vườn quốc gia, chứa người và động vật, tất cả trong sự hoang dã và trong sạch của vẻ đẹp tự nhiên của họ!</i>. Nam tước Thụy Điển gốc Phần Lan&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Adolf_Erik_Nordenski%C3%B6ld\" title=\"Adolf Erik Nordenskiöld\" style=\"color: rgb(11, 0, 128); background-image: none;\">Adolf Erik Nordenskiöld</a>&nbsp;cũng đưa ra ý kiến tương tự vào năm&nbsp;<a href=\"https://vi.wikipedia.org/wiki/1880\" title=\"1880\" style=\"color: rgb(11, 0, 128); background-image: none;\">1880</a>. Nhà tự nhiên học người Mỹ gốc Scotland&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=John_Muir&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"John Muir (trang chưa được viết)\" style=\"color: rgb(165, 88, 88); background-image: none;\">John Muir</a>&nbsp;cũng đã đưa ra các cảm hứng trong việc thiết lập các vườn quốc gia, đề cập tới nhiều ý tưởng của các phong trào bảo tồn, môi trường và quyền động vật sau này. Ý tưởng chung của họ là giữ lại những điều kỳ diệu của thiên nhiên sao cho các thế hệ tương lai có thể thưởng thức chúng và tìm lại được chúng tại nơi đó.</p><h2 style=\"color: rgb(0, 0, 0); margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); font-size: 1.5em; font-family: \" linux=\"\" libertine\",=\"\" \"palatino=\"\" linotype\",=\"\" \"times=\"\" new=\"\" roman\",=\"\" serif;=\"\" line-height:=\"\" 1.3;\"=\"\"><span id=\"Ra_.C4.91.E1.BB.9Di\"></span><span class=\"mw-headline\" id=\"Ra_đời\">Ra đời</span></h2><p style=\"margin-top: 0.5em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">Cố gắng đầu tiên để thiết lập những vùng đất được bảo vệ là tại&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Hoa_K%E1%BB%B3\" title=\"Hoa Kỳ\" style=\"color: rgb(11, 0, 128); background-image: none;\">Hoa Kỳ</a>, vào ngày&nbsp;<a href=\"https://vi.wikipedia.org/wiki/20_th%C3%A1ng_4\" title=\"20 tháng 4\" style=\"color: rgb(11, 0, 128); background-image: none;\">20 tháng 4</a>&nbsp;năm&nbsp;<a href=\"https://vi.wikipedia.org/wiki/1832\" title=\"1832\" style=\"color: rgb(11, 0, 128); background-image: none;\">1832</a>, khi tổng thống&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Andrew_Jackson\" title=\"Andrew Jackson\" style=\"color: rgb(11, 0, 128); background-image: none;\">Andrew Jackson</a>&nbsp;ký một sắc luật để dự trữ 4 vùng đất xung quanh khu vực ngày nay là&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Hot_Springs,_Arkansas\" title=\"Hot Springs, Arkansas\" style=\"color: rgb(11, 0, 128); background-image: none;\">Hot Springs, Arkansas</a>&nbsp;nhằm bảo vệ các suối nước nóng tự nhiên và các khu vực núi cận kề để chính quyền Hoa Kỳ sử dụng trong tương lai. Nó được biết đến như là&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=Khu_b%E1%BA%A3o_t%E1%BB%93n_Hot_Springs&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Khu bảo tồn Hot Springs (trang chưa được viết)\" style=\"color: rgb(165, 88, 88); background-image: none;\">Khu bảo tồn Hot Springs</a>. Tuy nhiên đã không có cơ quan quyền lực nhà nước nào được thành lập và việc kiểm soát của liên bang đối với khu vực đã không được thiết lập một cách rõ ràng cho tới tận năm 1877.</p><p style=\"margin-top: 0.5em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">Cố gắng tiếp theo nhằm thiết lập những vùng đất được bảo vệ cũng là tại Hoa Kỳ, khi tổng thống&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Abraham_Lincoln\" title=\"Abraham Lincoln\" style=\"color: rgb(11, 0, 128); background-image: none;\">Abraham Lincoln</a>&nbsp;ký sắc luật của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Qu%E1%BB%91c_h%E1%BB%99i_Hoa_K%E1%BB%B3\" title=\"Quốc hội Hoa Kỳ\" style=\"color: rgb(11, 0, 128); background-image: none;\">Quốc hội Hoa Kỳ</a>&nbsp;ngày&nbsp;<a href=\"https://vi.wikipedia.org/wiki/30_th%C3%A1ng_6\" title=\"30 tháng 6\" style=\"color: rgb(11, 0, 128); background-image: none;\">30 tháng 6</a>&nbsp;năm&nbsp;<a href=\"https://vi.wikipedia.org/wiki/1864\" title=\"1864\" style=\"color: rgb(11, 0, 128); background-image: none;\">1864</a>, nhượng lại&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=Thung_l%C5%A9ng_Yosemite&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Thung lũng Yosemite (trang chưa được viết)\" style=\"color: rgb(165, 88, 88); background-image: none;\">thung lũng Yosemite</a>&nbsp;và&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=R%E1%BB%ABng_Mariposa&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Rừng Mariposa (trang chưa được viết)\" style=\"color: rgb(165, 88, 88); background-image: none;\">rừng Mariposa</a>&nbsp;với các cây&nbsp;<a href=\"https://vi.wikipedia.org/wiki/C%E1%BB%B1_sam\" title=\"Cự sam\" style=\"color: rgb(11, 0, 128); background-image: none;\">cự sam</a>&nbsp;(hay cù tùng khổng lồ) (sau này là&nbsp;<a href=\"https://vi.wikipedia.org/wiki/V%C6%B0%E1%BB%9Dn_qu%E1%BB%91c_gia_Yosemite\" title=\"Vườn quốc gia Yosemite\" style=\"color: rgb(11, 0, 128); background-image: none;\">Vườn quốc gia Yosemite</a>) cho bang&nbsp;<a href=\"https://vi.wikipedia.org/wiki/California\" title=\"California\" style=\"color: rgb(11, 0, 128); background-image: none;\">California</a>, trong đó ghi rõ:</p><dl style=\"margin-top: 0.2em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\"><dd style=\"margin-left: 1.6em; margin-bottom: 0.1em; margin-right: 0px;\"><i>Bang đã đề cập [California] tới sẽ chấp nhận sự chuyển nhượng này với các điều kiện rõ ràng rằng các tài sản sẽ được duy trì để sử dụng công cộng, làm trung tâm nghỉ ngơi và tiêu khiển; sẽ không được chuyển nhượng vào bất kỳ thời gian nào.</i></dd></dl><div class=\"thumb tleft\" style=\"margin: 0.5em 1.4em 1.3em 0px; width: auto; float: left; clear: left; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\"><div class=\"thumbinner\" style=\"border: 1px solid rgb(200, 204, 209); padding: 3px; background-color: rgb(248, 249, 250); font-size: 13.16px; text-align: center; overflow: hidden; width: 302px;\"><a href=\"https://vi.wikipedia.org/wiki/T%E1%BA%ADp_tin:Yellowstone_Grand_Geysir_02.jpg\" class=\"image\" style=\"color: rgb(11, 0, 128); background-image: none;\"><img alt=\"\" src=\"https://upload.wikimedia.org/wikipedia/commons/thumb/8/85/Yellowstone_Grand_Geysir_02.jpg/300px-Yellowstone_Grand_Geysir_02.jpg\" decoding=\"async\" width=\"300\" height=\"225\" class=\"thumbimage\" srcset=\"//upload.wikimedia.org/wikipedia/commons/thumb/8/85/Yellowstone_Grand_Geysir_02.jpg/450px-Yellowstone_Grand_Geysir_02.jpg 1.5x, //upload.wikimedia.org/wikipedia/commons/thumb/8/85/Yellowstone_Grand_Geysir_02.jpg/600px-Yellowstone_Grand_Geysir_02.jpg 2x\" data-file-width=\"1280\" data-file-height=\"960\" style=\"border-width: 1px; border-style: solid; border-color: rgb(200, 204, 209); background-color: rgb(255, 255, 255);\"></a><div class=\"thumbcaption\" style=\"border: 0px; line-height: 1.4em; padding: 3px; font-size: 12.3704px; text-align: left;\"><div class=\"magnify\" style=\"float: right; margin-left: 3px; margin-right: 0px;\"><a href=\"https://vi.wikipedia.org/wiki/T%E1%BA%ADp_tin:Yellowstone_Grand_Geysir_02.jpg\" class=\"internal\" title=\"Phóng lớn\" style=\"color: rgb(11, 0, 128); background-image: linear-gradient(transparent, transparent), url(\" w=\"\" resources=\"\" src=\"\" mediawiki.skinning=\"\" images=\"\" magnify-clip-ltr.svg?8330e\");=\"\" background-position-x:=\"\" initial,=\"\" initial;=\"\" background-position-y:=\"\" background-size:=\"\" background-repeat:=\"\" repeat,=\"\" repeat;=\"\" background-attachment:=\"\" background-origin:=\"\" background-clip:=\"\" display:=\"\" block;=\"\" text-indent:=\"\" 15px;=\"\" white-space:=\"\" nowrap;=\"\" overflow:=\"\" hidden;=\"\" width:=\"\" height:=\"\" 11px;=\"\" user-select:=\"\" none;\"=\"\"></a></div><a href=\"https://vi.wikipedia.org/wiki/V%C6%B0%E1%BB%9Dn_qu%E1%BB%91c_gia_Yellowstone\" title=\"Vườn quốc gia Yellowstone\" style=\"color: rgb(11, 0, 128); background-image: none;\">Vườn quốc gia Yellowstone</a>,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Hoa_K%E1%BB%B3\" title=\"Hoa Kỳ\" style=\"color: rgb(11, 0, 128); background-image: none;\">Hoa Kỳ</a>.</div></div></div><p style=\"margin-top: 0.5em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">Năm&nbsp;<a href=\"https://vi.wikipedia.org/wiki/1872\" title=\"1872\" style=\"color: rgb(11, 0, 128); background-image: none;\">1872</a>,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/V%C6%B0%E1%BB%9Dn_qu%E1%BB%91c_gia_Yellowstone\" title=\"Vườn quốc gia Yellowstone\" style=\"color: rgb(11, 0, 128); background-image: none;\">Vườn quốc gia Yellowstone</a>&nbsp;đã được thành lập như là vườn quốc gia thật sự đầu tiên trên thế giới. Khi tin tức về các kỳ quan thiên nhiên của khu vực Yellowstone lần đầu tiên được công bố thì vùng đất này, khác với Yosemite, đang là một phần của lãnh thổ mà chưa một bang nào chiếm quyền quản lý, vì thế chính quyền liên bang đã chịu trách nhiệm trực tiếp quản lý, một quá trình chính thức được hoàn thành vào ngày&nbsp;<a href=\"https://vi.wikipedia.org/wiki/1_th%C3%A1ng_10\" title=\"1 tháng 10\" style=\"color: rgb(11, 0, 128); background-image: none;\">1 tháng 10</a>&nbsp;năm&nbsp;<a href=\"https://vi.wikipedia.org/wiki/1890\" title=\"1890\" style=\"color: rgb(11, 0, 128); background-image: none;\">1890</a>. Nó là cố gắng và lợi ích tổ hợp của các nhà bảo tồn, các chính khách và đặc biệt là các doanh nhân (cụ thể là công ty quản lý tuyến đường sắt Bắc Thái Bình Dương, mà hành trình đi qua Montana đã thu được lợi ích lớn nhờ sự tạo ra điểm hấp dẫn du khách này), để đảm bảo rằng Quốc hội Hoa Kỳ sẽ thông qua sắc luật nhằm thành lập Vườn quốc gia Yellowstone.</p><p style=\"margin-top: 0.5em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\"><a href=\"https://vi.wikipedia.org/w/index.php?title=Wallace_Stegner&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Wallace Stegner (trang chưa được viết)\" style=\"color: rgb(165, 88, 88); background-image: none;\">Wallace Stegner</a>&nbsp;đã viết rằng vườn quốc gia là&nbsp;<i>ý tưởng tốt nhất của người Mỹ</i>&nbsp;– xuất phát từ việc bảo tồn mang tính hoàng tộc mà các chính thể Cựu thế giới phục vụ cho chính bản thân họ - để tạo ra sự bảo tồn dân chủ, mở cho tất cả mọi người, \"nó phản ánh chúng ta ở khía cạnh tốt nhất, chứ không phải ở khía cạnh tệ nhất.\"<sup id=\"cite_ref-3\" class=\"reference\" style=\"line-height: 1em; font-size: 11.2px; white-space: nowrap; unicode-bidi: isolate;\"><a href=\"https://vi.wikipedia.org/wiki/V%C6%B0%E1%BB%9Dn_qu%E1%BB%91c_gia#cite_note-3\" style=\"color: rgb(11, 0, 128); background-image: none;\">[3]</a></sup>. Tuy vậy, chỉ 44 năm sau khi thành lập các vườn quốc gia Yellowstone, Yosemite và gần 37 vườn quốc gia, khu bảo tồn khác thì cơ quan nhà nước quản lý toàn diện các khu vực này mới được thành lập tại Hoa Kỳ - đó là&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=C%E1%BB%A5c_V%C6%B0%E1%BB%9Dn_Qu%E1%BB%91c_gia_Hoa_K%E1%BB%B3&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Cục Vườn Quốc gia Hoa Kỳ (trang chưa được viết)\" style=\"color: rgb(165, 88, 88); background-image: none;\">Cục Vườn Quốc gia Hoa Kỳ</a>&nbsp;(NPS). Một điều thú vị là một doanh nhân, ông&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=Stephen_Mather&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Stephen Mather (trang chưa được viết)\" style=\"color: rgb(165, 88, 88); background-image: none;\">Stephen Mather</a>, đã là người thúc đẩy mạnh mẽ nhất cho việc thành lập NPS, ông dã viết cho&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=Franklin_Knight_Lane&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Franklin Knight Lane (trang chưa được viết)\" style=\"color: rgb(165, 88, 88); background-image: none;\">Franklin Knight Lane</a>, khi đó là Bộ trưởng Nội vụ về nhu cầu đó. Lane đã mời Mather đến Washington, DC để làm việc cùng ông trong soạn thảo và chờ sự thông qua của dự luật về tổ chức NPS, được Quốc hội Hoa Kỳ phê chuẩn và ký ngày&nbsp;<a href=\"https://vi.wikipedia.org/wiki/25_th%C3%A1ng_8\" title=\"25 tháng 8\" style=\"color: rgb(11, 0, 128); background-image: none;\">25 tháng 8</a>&nbsp;năm&nbsp;<a href=\"https://vi.wikipedia.org/wiki/1916\" title=\"1916\" style=\"color: rgb(11, 0, 128); background-image: none;\">1916</a>.</p><p style=\"margin-top: 0.5em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">Số lượng các khu vực hiện tại do NPS quản lý tại Hoa Kỳ là 391, trong đó chỉ có 58 là các vườn quốc gia.</p><p style=\"margin-top: 0.5em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">Tại các quốc gia khác, ý tưởng về thành lập vườn quốc gia như Yellowstone cũng đã dần dần được chấp nhận. Tại&nbsp;<a href=\"https://vi.wikipedia.org/wiki/%C3%9Ac\" title=\"Úc\" style=\"color: rgb(11, 0, 128); background-image: none;\">Australia</a>,&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=V%C6%B0%E1%BB%9Dn_qu%E1%BB%91c_gia_Ho%C3%A0ng_gia&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Vườn quốc gia Hoàng gia (trang chưa được viết)\" style=\"color: rgb(165, 88, 88); background-image: none;\">Vườn quốc gia Hoàng gia</a>&nbsp;đã được thành lập ở phía nam&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Sydney\" title=\"Sydney\" style=\"color: rgb(11, 0, 128); background-image: none;\">Sydney</a>&nbsp;năm&nbsp;<a href=\"https://vi.wikipedia.org/wiki/1879\" title=\"1879\" style=\"color: rgb(11, 0, 128); background-image: none;\">1879</a>. Tại&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Canada\" title=\"Canada\" style=\"color: rgb(11, 0, 128); background-image: none;\">Canada</a>,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/V%C6%B0%E1%BB%9Dn_qu%E1%BB%91c_gia_Banff\" title=\"Vườn quốc gia Banff\" style=\"color: rgb(11, 0, 128); background-image: none;\">Vườn quốc gia Banff</a>&nbsp;(khi đó gọi là Vườn quốc gia núi Rocky) là vườn quốc gia đầu tiên của nước này vào năm&nbsp;<a href=\"https://vi.wikipedia.org/wiki/1885\" title=\"1885\" style=\"color: rgb(11, 0, 128); background-image: none;\">1885</a>.&nbsp;<a href=\"https://vi.wikipedia.org/wiki/New_Zealand\" title=\"New Zealand\" style=\"color: rgb(11, 0, 128); background-image: none;\">New Zealand</a>&nbsp;có vườn quốc gia đầu tiên vào năm&nbsp;<a href=\"https://vi.wikipedia.org/wiki/1887\" title=\"1887\" style=\"color: rgb(11, 0, 128); background-image: none;\">1887</a>. Tại&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Ch%C3%A2u_%C3%82u\" title=\"Châu Âu\" style=\"color: rgb(11, 0, 128); background-image: none;\">châu Âu</a>&nbsp;các vườn quốc gia đầu tiên là tập hợp gồm 9 vườn tại&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Th%E1%BB%A5y_%C4%90i%E1%BB%83n\" title=\"Thụy Điển\" style=\"color: rgb(11, 0, 128); background-image: none;\">Thụy Điển</a>&nbsp;vào năm&nbsp;<a href=\"https://vi.wikipedia.org/wiki/1909\" title=\"1909\" style=\"color: rgb(11, 0, 128); background-image: none;\">1909</a>. Hiện tại, châu Âu có 370 vườn quốc gia&nbsp;<a rel=\"nofollow\" class=\"external autonumber\" href=\"http://jozef.phpnet.us/index.php/National_parks_in_Europe\" style=\"color: rgb(102, 51, 102); background-image: url(\" w=\"\" skins=\"\" vector=\"\" resources=\"\" skins.vector.styles=\"\" images=\"\" external-link-ltr-icon.svg?b4b84\");=\"\" background-position:=\"\" right=\"\" center;=\"\" background-repeat:=\"\" no-repeat;=\"\" padding-right:=\"\" 13px;\"=\"\">[1]</a>.</p><p style=\"margin-top: 0.5em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">Sau&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Chi%E1%BA%BFn_tranh_th%E1%BA%BF_gi%E1%BB%9Bi_th%E1%BB%A9_hai\" title=\"Chiến tranh thế giới thứ hai\" style=\"color: rgb(11, 0, 128); background-image: none;\">Đại chiến thế giới lần thứ hai</a>, các vườn quốc gia đã được thành lập trên khắp thế giới.&nbsp;<a href=\"https://vi.wikipedia.org/wiki/V%C6%B0%E1%BB%9Dn_qu%E1%BB%91c_gia_Vanoise\" title=\"Vườn quốc gia Vanoise\" style=\"color: rgb(11, 0, 128); background-image: none;\">Vườn quốc gia Vanoise</a>&nbsp;trong khu vực dãy núi&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Anp%C6%A1\" title=\"Anpơ\" style=\"color: rgb(11, 0, 128); background-image: none;\">Alps</a>&nbsp;là vườn quốc gia đầu tiên của Pháp, thành lập năm&nbsp;<a href=\"https://vi.wikipedia.org/wiki/1963\" title=\"1963\" style=\"color: rgb(11, 0, 128); background-image: none;\">1963</a>&nbsp;sau khi diễn ra các cuộc biểu tình ngăn chặn một dự án du lịch tại đây. Tại Việt Nam,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/V%C6%B0%E1%BB%9Dn_qu%E1%BB%91c_gia_C%C3%BAc_Ph%C6%B0%C6%A1ng\" title=\"Vườn quốc gia Cúc Phương\" style=\"color: rgb(11, 0, 128); background-image: none;\">Vườn quốc gia Cúc Phương</a>&nbsp;thành lập năm&nbsp;<a href=\"https://vi.wikipedia.org/wiki/1966\" title=\"1966\" style=\"color: rgb(11, 0, 128); background-image: none;\">1966</a>&nbsp;là vườn quốc gia đầu tiên của nước này.</p><h2 style=\"color: rgb(0, 0, 0); margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); font-size: 1.5em; font-family: \" linux=\"\" libertine\",=\"\" \"palatino=\"\" linotype\",=\"\" \"times=\"\" new=\"\" roman\",=\"\" serif;=\"\" line-height:=\"\" 1.3;\"=\"\"><span id=\"C.C3.A1c_.C4.91.E1.BA.B7c_tr.C6.B0ng_c.E1.BB.A7a_v.C6.B0.E1.BB.9Dn_qu.E1.BB.91c_gia\"></span><span class=\"mw-headline\" id=\"Các_đặc_trưng_của_vườn_quốc_gia\">Các đặc trưng của vườn quốc gia</span></h2><div class=\"thumb tright\" style=\"margin: 0.5em 0px 1.3em 1.4em; width: auto; clear: right; float: right; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\"><div class=\"thumbinner\" style=\"border: 1px solid rgb(200, 204, 209); padding: 3px; background-color: rgb(248, 249, 250); font-size: 13.16px; text-align: center; overflow: hidden; width: 302px;\"><a href=\"https://vi.wikipedia.org/wiki/T%E1%BA%ADp_tin:Bryce_Canyon_1_md.jpg\" class=\"image\" style=\"color: rgb(11, 0, 128); background-image: none;\"><img alt=\"\" src=\"https://upload.wikimedia.org/wikipedia/commons/thumb/3/3c/Bryce_Canyon_1_md.jpg/300px-Bryce_Canyon_1_md.jpg\" decoding=\"async\" width=\"300\" height=\"200\" class=\"thumbimage\" srcset=\"//upload.wikimedia.org/wikipedia/commons/thumb/3/3c/Bryce_Canyon_1_md.jpg/450px-Bryce_Canyon_1_md.jpg 1.5x, //upload.wikimedia.org/wikipedia/commons/thumb/3/3c/Bryce_Canyon_1_md.jpg/600px-Bryce_Canyon_1_md.jpg 2x\" data-file-width=\"1231\" data-file-height=\"821\" style=\"border-width: 1px; border-style: solid; border-color: rgb(200, 204, 209); background-color: rgb(255, 255, 255);\"></a><div class=\"thumbcaption\" style=\"border: 0px; line-height: 1.4em; padding: 3px; font-size: 12.3704px; text-align: left;\"><div class=\"magnify\" style=\"float: right; margin-left: 3px; margin-right: 0px;\"><a href=\"https://vi.wikipedia.org/wiki/T%E1%BA%ADp_tin:Bryce_Canyon_1_md.jpg\" class=\"internal\" title=\"Phóng lớn\" style=\"color: rgb(11, 0, 128); background-image: linear-gradient(transparent, transparent), url(\" w=\"\" resources=\"\" src=\"\" mediawiki.skinning=\"\" images=\"\" magnify-clip-ltr.svg?8330e\");=\"\" background-position-x:=\"\" initial,=\"\" initial;=\"\" background-position-y:=\"\" background-size:=\"\" background-repeat:=\"\" repeat,=\"\" repeat;=\"\" background-attachment:=\"\" background-origin:=\"\" background-clip:=\"\" display:=\"\" block;=\"\" text-indent:=\"\" 15px;=\"\" white-space:=\"\" nowrap;=\"\" overflow:=\"\" hidden;=\"\" width:=\"\" height:=\"\" 11px;=\"\" user-select:=\"\" none;\"=\"\"></a></div><a href=\"https://vi.wikipedia.org/wiki/V%C6%B0%E1%BB%9Dn_qu%E1%BB%91c_gia_h%E1%BA%BBm_n%C3%BAi_Bryce\" class=\"mw-redirect\" title=\"Vườn quốc gia hẻm núi Bryce\" style=\"color: rgb(11, 0, 128); background-image: none;\">Vườn quốc gia hẻm núi Bryce</a>&nbsp;tại miền nam&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Utah\" title=\"Utah\" style=\"color: rgb(11, 0, 128); background-image: none;\">Utah</a>,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Hoa_K%E1%BB%B3\" title=\"Hoa Kỳ\" style=\"color: rgb(11, 0, 128); background-image: none;\">Hoa Kỳ</a>&nbsp;được thành lập năm 1928. Trước đây nó là&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=Khu_b%E1%BA%A3o_t%E1%BB%93n_qu%E1%BB%91c_gia_Hoa_K%E1%BB%B3&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Khu bảo tồn quốc gia Hoa Kỳ (trang chưa được viết)\" style=\"color: rgb(165, 88, 88); background-image: none;\">khu bảo tồn quốc gia</a>.</div></div></div><p style=\"margin-top: 0.5em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">Các vườn quốc gia thông thường nằm tại các khu vực chủ yếu là chưa phát triển, thường là những khu vực với động-thực vật bản địa quý hiếm và các&nbsp;<a href=\"https://vi.wikipedia.org/wiki/H%E1%BB%87_sinh_th%C3%A1i\" title=\"Hệ sinh thái\" style=\"color: rgb(11, 0, 128); background-image: none;\">hệ sinh thái</a>&nbsp;đặc biệt (chẳng hạn cụ thể là các loài đang nguy cấp), sự&nbsp;<a href=\"https://vi.wikipedia.org/wiki/%C4%90a_d%E1%BA%A1ng_sinh_h%E1%BB%8Dc\" title=\"Đa dạng sinh học\" style=\"color: rgb(11, 0, 128); background-image: none;\">đa dạng sinh học</a>, hay các đặc trưng địa chất đặc biệt. Đôi khi, các vườn quốc gia cũng được thành lập tại các khu vực đã phát triển với mục tiêu làm cho khu vực đó trở lại gần giống như tình trạng ban đầu của nó, càng gần càng tốt.</p><p style=\"margin-top: 0.5em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">Tại một số quốc gia, chẳng hạn như tại&nbsp;<a href=\"https://vi.wikipedia.org/wiki/V%C6%B0%C6%A1ng_qu%E1%BB%91c_Anh\" title=\"Vương quốc Anh\" style=\"color: rgb(11, 0, 128); background-image: none;\">Vương quốc Anh</a>&nbsp;và&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Wales\" title=\"Wales\" style=\"color: rgb(11, 0, 128); background-image: none;\">Wales</a>, các khu vực được dùng làm vườn quốc gia không phải là vùng hoang vu, cũng không do nhà nước sở hữu, và có thể bao gồm các khu dân cư và việc sử dụng đất là đáng kể, thông thường chúng là một bộ phận hợp thành của cảnh quan khu vực. Vườn quốc gia đầu tiên của Scotland,&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=V%C6%B0%E1%BB%9Dn_qu%E1%BB%91c_gia_Loch_Lomond_v%C3%A0_Trossachs&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Vườn quốc gia Loch Lomond và Trossachs (trang chưa được viết)\" style=\"color: rgb(165, 88, 88); background-image: none;\">vườn quốc gia Loch Lomond và Trossachs</a>, được thành lập tháng 7 năm&nbsp;<a href=\"https://vi.wikipedia.org/wiki/2002\" title=\"2002\" style=\"color: rgb(11, 0, 128); background-image: none;\">2002</a>&nbsp;và&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=V%C6%B0%E1%BB%9Dn_qu%E1%BB%91c_gia_Cairngorms&amp;action=edit&amp;redlink=1\" class=\"new\" title=\"Vườn quốc gia Cairngorms (trang chưa được viết)\" style=\"color: rgb(165, 88, 88); background-image: none;\">vườn quốc gia Cairngorms</a>&nbsp;được thành lập tháng 3 năm&nbsp;<a href=\"https://vi.wikipedia.org/wiki/2003\" title=\"2003\" style=\"color: rgb(11, 0, 128); background-image: none;\">2003</a>.</p><h2 style=\"color: rgb(0, 0, 0); margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); font-size: 1.5em; font-family: \" linux=\"\" libertine\",=\"\" \"palatino=\"\" linotype\",=\"\" \"times=\"\" new=\"\" roman\",=\"\" serif;=\"\" line-height:=\"\" 1.3;\"=\"\"><span id=\"Qu.E1.BA.A3n_l.C3.BD.2C_s.E1.BB.AD_d.E1.BB.A5ng\"></span><span class=\"mw-headline\" id=\"Quản_lý,_sử_dụng\">Quản lý, sử dụng</span></h2><div class=\"thumb tright\" style=\"margin: 0.5em 0px 1.3em 1.4em; width: auto; clear: right; float: right; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\"><div class=\"thumbinner\" style=\"border: 1px solid rgb(200, 204, 209); padding: 3px; background-color: rgb(248, 249, 250); font-size: 13.16px; text-align: center; overflow: hidden; width: 302px;\"><a href=\"https://vi.wikipedia.org/wiki/T%E1%BA%ADp_tin:Ranthambore_National_Park.JPG\" class=\"image\" style=\"color: rgb(11, 0, 128); background-image: none;\"><img alt=\"\" src=\"https://upload.wikimedia.org/wikipedia/commons/thumb/7/7f/Ranthambore_National_Park.JPG/300px-Ranthambore_National_Park.JPG\" decoding=\"async\" width=\"300\" height=\"225\" class=\"thumbimage\" srcset=\"//upload.wikimedia.org/wikipedia/commons/thumb/7/7f/Ranthambore_National_Park.JPG/450px-Ranthambore_National_Park.JPG 1.5x, //upload.wikimedia.org/wikipedia/commons/thumb/7/7f/Ranthambore_National_Park.JPG/600px-Ranthambore_National_Park.JPG 2x\" data-file-width=\"2576\" data-file-height=\"1932\" style=\"border-width: 1px; border-style: solid; border-color: rgb(200, 204, 209); background-color: rgb(255, 255, 255);\"></a><div class=\"thumbcaption\" style=\"border: 0px; line-height: 1.4em; padding: 3px; font-size: 12.3704px; text-align: left;\"><div class=\"magnify\" style=\"float: right; margin-left: 3px; margin-right: 0px;\"><a href=\"https://vi.wikipedia.org/wiki/T%E1%BA%ADp_tin:Ranthambore_National_Park.JPG\" class=\"internal\" title=\"Phóng lớn\" style=\"color: rgb(11, 0, 128); background-image: linear-gradient(transparent, transparent), url(\" w=\"\" resources=\"\" src=\"\" mediawiki.skinning=\"\" images=\"\" magnify-clip-ltr.svg?8330e\");=\"\" background-position-x:=\"\" initial,=\"\" initial;=\"\" background-position-y:=\"\" background-size:=\"\" background-repeat:=\"\" repeat,=\"\" repeat;=\"\" background-attachment:=\"\" background-origin:=\"\" background-clip:=\"\" display:=\"\" block;=\"\" text-indent:=\"\" 15px;=\"\" white-space:=\"\" nowrap;=\"\" overflow:=\"\" hidden;=\"\" width:=\"\" height:=\"\" 11px;=\"\" user-select:=\"\" none;\"=\"\"></a></div>Công viên quốc gia Ranthambore</div></div></div><p style=\"margin-top: 0.5em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">Phần lớn các vườn quốc gia có vai trò kép, một mặt đây là khu vực cung cấp nơi cư trú cho sự sống hoang dã, mặt khác nó lại phục vụ như là nơi du lịch phổ biến cho quần chúng. Việc quản lý các mâu thuẫn tiềm ẩn giữa hai mục đích này có thể là một vấn đề, cụ thể là du khách sẽ đem lại thu nhập cho vườn quốc gia và vườn quốc gia sử dụng nguồn thu nhập này để duy trì và phát triển các dự án bảo tồn. Các vườn quốc gia cũng là nguồn cung cấp các tài nguyên thiên nhiên có giá trị, chẳng hạn như&nbsp;<a href=\"https://vi.wikipedia.org/wiki/G%E1%BB%97\" title=\"Gỗ\" style=\"color: rgb(11, 0, 128); background-image: none;\">gỗ</a>,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Kho%C3%A1ng_s%E1%BA%A3n\" title=\"Khoáng sản\" style=\"color: rgb(11, 0, 128); background-image: none;\">khoáng sản</a>&nbsp;và các loại tài nguyên có giá trị khác. Sự cân bằng giữa nhu cầu khai thác các tài nguyên này với tổn thất do việc khai thác gây ra, thường là thách thức rất quan trọng đối với hệ thống quản lý vườn quốc gia. Các vườn quốc gia cũng hay bị đốn hạ bất hợp pháp và các dạng khai thác lậu khác, đôi khi là do&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Tham_nh%C5%A9ng\" title=\"Tham nhũng\" style=\"color: rgb(11, 0, 128); background-image: none;\">tham nhũng</a>. Điều này đe dọa tính nguyên vẹn của nhiều môi trường sống có giá trị.</p><h2 style=\"color: rgb(0, 0, 0); margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); font-size: 1.5em; font-family: \" linux=\"\" libertine\",=\"\" \"palatino=\"\" linotype\",=\"\" \"times=\"\" new=\"\" roman\",=\"\" serif;=\"\" line-height:=\"\" 1.3;\"=\"\"><span id=\"C.C3.A1c_d.E1.BA.A1ng_b.E1.BA.A3o_t.E1.BB.93n_kh.C3.A1c\"></span><span class=\"mw-headline\" id=\"Các_dạng_bảo_tồn_khác\">Các dạng bảo tồn khác</span></h2><p style=\"margin-top: 0.5em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">Một vài quốc gia cũng chọn các khu vực với tầm quan trọng lịch sử, khoa học hay văn hóa đặc biệt làm vườn quốc gia hoặc là các thực thể đặc biệt trong hệ thống vườn quốc gia của mình. Các quốc gia khác lại sử dụng kiểu khác cho việc bảo tồn các khu vực lịch sử. Một số các khu vực này, nếu đạt được các tiêu chuẩn đã đề ra, có thể được&nbsp;<a href=\"https://vi.wikipedia.org/wiki/T%E1%BB%95_ch%E1%BB%A9c_Gi%C3%A1o_d%E1%BB%A5c,_Khoa_h%E1%BB%8Dc_v%C3%A0_V%C4%83n_h%C3%B3a_Li%C3%AAn_Hi%E1%BB%87p_Qu%E1%BB%91c\" title=\"Tổ chức Giáo dục, Khoa học và Văn hóa Liên Hiệp Quốc\" style=\"color: rgb(11, 0, 128); background-image: none;\">UNESCO</a>&nbsp;công nhận là&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Di_s%E1%BA%A3n_th%E1%BA%BF_gi%E1%BB%9Bi\" title=\"Di sản thế giới\" style=\"color: rgb(11, 0, 128); background-image: none;\">Di sản thế giới</a>.</p><p style=\"margin-top: 0.5em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">Tại nhiều quốc gia, các cơ quan&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Ch%C3%ADnh_quy%E1%BB%81n_%C4%91%E1%BB%8Ba_ph%C6%B0%C6%A1ng\" title=\"Chính quyền địa phương\" style=\"color: rgb(11, 0, 128); background-image: none;\">chính quyền địa phương</a>&nbsp;có thể là tổ chức chịu trách nhiệm duy trì hệ thống vườn. Một số trong số các hệ thống vườn này cũng được gọi là vườn quốc gia.</p>', '...', '...', 0, 1, 1, '2020-08-18 20:10:08', '2020-09-15 12:15:45', 0, NULL, NULL, 0, ''),
(40, 'catalog/Anh_Dai_Dien/9df757962b6650a1da20c6520392ad3a.jpg', 5, 'Hình ảnh về thiên nhiên trong công viên quốc gia', '<p>thực vật ở nam cát tiên</p><p><img src=\"https://orchardresort.vn/wp-content/uploads/2019/12/Bang-Lang-Cat-Tien.jpg\" alt=\"rung nam cat tien, rung bang lang\" style=\"width: 340.25px;\"><img src=\"https://orchardresort.vn/wp-content/uploads/2019/12/1_dcfygI50i7Tvgn3y98_wNg-1.jpeg\" alt=\"rung nam cat tien, buom buom nam cat tien\" style=\"width: 340.25px;\"><img src=\"https://orchardresort.vn/wp-content/uploads/2019/12/nhung-trai-nghiem-thu-vi-khong-nen-bo-lo-o-nam-cat-tien-1.jpg\" alt=\"orchard home resort\" style=\"width: 340.25px;\"><img src=\"https://orchardresort.vn/wp-content/uploads/2019/12/43153071_479766699099508_5943221743857434624_n.jpg\" alt=\"rung nam cat tien, cay si tram than\" style=\"width: 340.25px;\"><img src=\"https://orchardresort.vn/wp-content/uploads/2019/12/ab_1.jpg\" alt=\"cay go bac dong, nam cat tien\" style=\"width: 340.25px;\"></p><p>núi chúa</p><p><img src=\"http://www.vqgnuichua.vn/upload/images/7.bmp\" alt=\"7.bmp\" style=\"width: 340.25px;\"><img src=\"http://www.vqgnuichua.vn/upload/images/mk.bmp\" alt=\"mk.bmp\" style=\"width: 340.25px;\"><img src=\"http://www.vqgnuichua.vn/upload/images/23.bmp\" alt=\"23.bmp\" style=\"width: 680.5px;\"><br></p>', '...', 'thực vật', 0, 1, 1, '2020-08-18 21:33:40', '2020-09-15 12:17:46', 0, NULL, NULL, 0, '');
INSERT INTO `post` (`post_id`, `image`, `author_id`, `title`, `content`, `link`, `tag`, `featured`, `sort_order`, `status`, `date_added`, `date_modified`, `parent_id`, `summary`, `sub_title`, `top`, `menu`) VALUES
(41, 'catalog/6ac3f5c6dfac46535ee096f03f13657e.jpg', 5, 'Loại Hình ẩm thực', '<figure id=\"attachment_4250\" class=\"wp-caption alignnone\" aria-describedby=\"caption-attachment-4250\" style=\"margin-bottom: 2em; font-size: 14px; max-width: 100%; font-family: Roboto, sans-serif;\"><figcaption id=\"caption-attachment-4250\" class=\"wp-caption-text\" style=\"padding: 0.4em; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 15pt; margin-bottom: 0pt; padding: 0pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Ẩm thực độc đáo&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">- Nam Cát Tiên</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;\"=\"\">Điểm nổi bật là tất tần tật rau quả tươi sạch được tự tay trồng trong nông trại của Orchard. Và những đặc sản thiên nhiên, mà khi về Sài Gòn thật khó để tìm được một hương vị giống y như vậy </span><span style=\"font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\">Cá lóc, cá rô rừng: Hai loại cá này ở trong rừng quốc gia Nam Cát Tiên rất nhiều nên chỉ cần bạn muốn ăn thì các nhà hàng tại đây sẽ luôn sẵn sàng phục vụ cho bạn như cá nướng, cá nấu canh, cá om…</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">+ Đặc sản cá lóc ở Nam Cát Tiên</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;\"=\"\">Canh lục bình</span><span style=\"font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\">: Lục bình hay còn gọi là cây bèo nước cũng là một món ăn ngon ở Nam Cát Tiên mà bạn cũng nên ăn thử. Món canh này ăn vừa ngọt vừa mát, người ta sẽ dùng cả thân và hoa của cây lục bình nấu canh, nếu bạn chưa ăn bao giờ thì nhất định phải thử một lần nha.</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">+ Canh lục bình ở rừng Nam Cát Tiên</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;\"=\"\">Lẩu cá sông, rau rừng</span><span style=\"font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\">: Nhờ vào nguồn thủy sản và nhiều loại rau rừng độc đáo mà tại vườn quốc gia này có món lẩu cực kỳ ngon và lạ. Ở trong rừng thời tiết lúc nào cũng mát mẻ nên ăn lẩu giữa một không gian xanh tươi bao la thì không còn gì tuyệt vời hơn.</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">+ Các món ăn từ rau rừng Nam Cát Tiên</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">- Núi chúa</span><span style=\"color: rgb(0, 0, 0);\">&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;+&nbsp;</span><span style=\"color: rgb(0, 0, 0); font-family: \" times=\"\" new=\"\" roman\";=\"\" font-size:=\"\" 12pt;=\"\" font-weight:=\"\" 700;=\"\" white-space:=\"\" pre-wrap;\"=\"\">Cá mú hấp ở Vĩnh Hy (Ảnh – _heavenof_hai)</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Cá mú là loại cá nước mặn, được người tiêu dùng ưa chuộng bởi thịt của nó trắng, dai và ngọt. Có ba loại cá mú là cá mú đỏ, cá mú cọp và cá mú đen, nhưng cá mú đỏ là loài cá ngon nhất.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">+ Cơm gà Phan Rang</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">+ Cơm gà Phan Rang</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">+ Cơm gà Phan Rang được nấu bằng gạo dẻo, thơm. Trước khi nấu, gạo được vo sạch, ướp thêm gừng, tỏi giã nhuyễn để tạo mùi thơm. Muốn có dĩa cơm bắt mắt, thường người nấu trộn thêm vào gạo chút bột nghệ. Gà chặt từng miếng khổ vừa lớn. Món này ăn nóng kèm với một ít rau răm, dưa leo và nước mắm chua ngọt đã pha chế hay chấm cùng muối ớt. Loại nước chấm đặc biệt khác nên kể thêm là nước hèm, loại nước chấm độc đáo chỉ có ở Phan Rang. Cũng tỏi cũng gừng, nhưng ta thêm vào đó một ít hèm rượu và đường tạo nên vị chua ngọt khá đặc trưng. Hèm chan vào cơm để giảm bớt độ ngấy do nhiều dầu mỡ. Cơm gà Phan Rang phải ăn kèm rau răm mới “đúng bài”, có lẽ rau răm đặc biệt hợp với gà, nên càng ăn càng thấy ngon.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">+ Nho Ninh Thuận</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span><span style=\"color: rgb(0, 0, 0); font-family: \" times=\"\" new=\"\" roman\";=\"\" font-size:=\"\" 12pt;=\"\" font-weight:=\"\" 700;=\"\" white-space:=\"\" pre-wrap;\"=\"\">Nho là một loại trái cây nổi tiếng đối với bất kỳ ai khi nhắc về Ninh Thuận (Ảnh – doan_my)</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Có thể nói khí hậu và điều kiện Ninh Thuận rất thích hợp cho cây nho sinh trưởng và phát triển. Nho Ninh Thuận thu hoạch ba vụ/năm, có hương vị đặc trưng hơn các loại nho khác. Với vị ngọt hơi chua nhẹ, vỏ mỏng và có hạt nên khi thưởng thức chắc chắn sẽ nhớ mãi. Có nhiều giống khác nhau nhưng trong đó có hai loại nổi tiếng là nho đỏ và nho xanh. Nho đỏ có dạng hình cầu, khi chín vỏ có màu đỏ hoặc đỏ thẫm trong rất đẹp mắt. Nho xanh có vỏ dày hơn nho đỏ, khi chín quả ngả sang xanh phơn phơn vàng.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">+ Thịt cừu</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span><span style=\"color: rgb(0, 0, 0); font-family: \" times=\"\" new=\"\" roman\";=\"\" font-size:=\"\" 12pt;=\"\" font-weight:=\"\" 700;=\"\" white-space:=\"\" pre-wrap;\"=\"\">Đừng quên thưởng thức thịt cừu khi tới Ninh Thuận nhé (Ảnh – jessechou58)</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Cừu là đặc sản nổi tiếng của Ninh Thuận được chế biến thành những món ăn ngon như: cừu nướng, cừu đút lò, chả cừu, cari cừu, lẩu cừu…được rất nhiều du khách yêu thích khi tới du lịch Ninh Thuận.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">+Bánh căn Ninh Thuận (Ảnh – mintgreenvu)</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Bánh căn là món cực kỳ đơn giản và dễ làm, không cần những nguyên liệu cầu kỳ, khó tìm kiếm, không cần phải qua nhiều công đoạn chế biến, chỉ cần một chút khéo tay là bạn có thể cho ra lò những chiếc bánh ngon. Muốn có bánh căn ngon, người ta thường dùng loại gạo hạt tròn, loại gạo địa phương mà phải là gạo cũ càng tốt. Điểm độc đáo là bánh căn được đổ bằng những khuôn đất, được chế tạo từ bàn tay tài hoa của những người thợ gốm Chăm Bàu Trúc.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Riêng nước chấm của loại bánh này ở Phan Rang cũng khá đặc biệt. Người sành ăn thì sẽ pha trộn vào tô của mình cả bốn loại nước chấm gồm: nước mắm chua ngọt, mắm đậu phộng, mắm nêm và nước cá kho với dưa hồng.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">-Bạch Mã</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">+ Bún bò Huế nóng hổi thơm ngon</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;\"=\"\">Đến với Huế ngoài </span><span style=\"font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\">thưởng thức ẩm thực Hoàng gia, du khách còn được thử những món ăn ngon, phổ biến rộng rãi ngày nay. Một trong đó là bún bò Huế. So với phở - món ăn ngon của Việt Nam, bún bò Huế mang lại hương vị hoàn toàn khác nhau. Hương vị đặc trưng của bún bò Huế rất cay, nóng và có tinh chất của sả. Bún Bò Huế được ăn kèm với nhiều loại rau và thảo mộc như giá đỗ, hoa chuối xắt lát, bắp cải thái nhỏ và rau diếp.</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">&nbsp;</span><span style=\"color: rgb(0, 0, 0); font-size: 12pt; font-style: italic; font-weight: 700; white-space: pre-wrap;\">Bún bò Huế đậm đà hương vị&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Huế là một thành phố ở miền Trung Việt Nam gắn liền với phong cách nấu ăn của triều đình. Bún bò có nguồn gốc ở Huế, một Thủ đô cũ của Việt Nam. Bên ngoài thành phố Huế và một số vùng miền Trung, nó được gọi là bún bò Huế để chỉ nguồn gốc.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Ẩm thực Huế phải nhắc tới nem lụi</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Nem lụi được làm bằng thịt lợn ướp gia vị theo công thức riêng, được quấn quanh thanh sả và nướng trên bếp than hồng rực lửa. Nem lụi ăn kèm với bánh tráng, bạn thêm xà lách thái lát, dưa chuột, quả sung, cà chua và đu đủ xanh thái sợi... Một điều nữa mà không thể bỏ qua món đặc sản này là chấm với nước sốt địa phương được làm từ đậu phộng.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: italic; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">+Nem lụi ngon ngất ngây không thể bỏ lỡ khi đến Huế</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">+ Chè Huế ngon, mát và bổ dưỡng</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Huế không chỉ là một thành phố độc đáo giàu lịch sử, văn hoá, kiến ​​trúc, nghệ thuật mà còn nổi tiếng với nền ẩm thực các món ăn của nó: ẩm thực hoàng gia cho vua và quý tộc, ẩm thực chay cho các nhà sư và ẩm thực dân gian cho tất cả mọi người. Trong số đó, chè – món tráng miệng nổi tiếng với nhiều loại bao gồm cả hoàng gia và dân gian. Mỗi loại có hương vị riêng.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">&nbsp;</span><span style=\"color: rgb(0, 0, 0); font-size: 12pt; font-style: italic; font-weight: 700; white-space: pre-wrap;\">Sự đa dạng các loại chè Huế</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Chè ngon, tinh tế giống như người dân ở đây. Chè bắp (ngô) mát và ngọt ngọt, nguyên liệu được trồng từ cồn Hến. Chè hạt sen có mùi hương độc đáo của hoa sen từ hồ Tịnh Tâm - nơi mà sen trước đây dành cho hoàng tộc. Ngoài ra, còn có rất nhiều loại chè khác nhau như lựu, khoai sọ, và nhiều hơn nữa... Nếu bạn có cơ hội thăm thành phố Huế, hãy chắc chắn rằng đừng bỏ lỡ việc thưởng thức chè Huế.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">+ Món chay tịnh tâm</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Huế được biết đến như là thành phố đạo Phật ở Việt Nam. Đây là lý do tại sao thực phẩm chay trở nên quen thuộc với nhiều người dân địa phương. Bạn có thể ngạc nhiên khi thấy nhiều gian hàng thực phẩm và nhà hàng phục vụ thức ăn chay vào ngày đầu tiên và ngày thứ mười lăm của mỗi tháng âm lịch. Từ các thành phần tự nhiên có thể dễ dàng tìm thấy ở bất kỳ khu chợ nào ở địa phương, đầu bếp tinh tế chế biến nấu một số thực phẩm chay tốt nhất, làm bạn ăn một lần nhớ mãi.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: italic; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">+ Món chay Huế tịnh tâm</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">+ Bánh bèo Huế</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;\"=\"\">Huế là một trong những </span><span style=\"font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\">điểm du lịch lớn ở miền Trung Việt Nam, là di sản văn hoá thế giới. Khách du lịch nước ngoài yêu thích nơi này vì môi trường yên tĩnh. Trên hết, thực phẩm của Huế, làm bạn có thể nhận ra sự khác biết về vị giác của người dân miền Trung, miền Bắc và miền Nam. Đến với </span><a href=\"https://tour.dulichvietnam.com.vn/du-lich-trong-nuoc/du-lich-hue.html/50\" style=\"text-decoration:none;\"><span style=\"font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: normal; font-variant: normal; text-decoration: underline; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;\">du lịch Huế</span></a><span style=\"font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;\">, đừng quên thưởng thức những chiếc bánh bèo nhỏ nhỏ, xinh xinh. Nguyên liệu chính là bột gạo xay thành bột, đổ bột vào những chiếc chén nhỏ, hấp chín, cho bột tôm, hành phi, tóp mỡ lên trên. Một khay bánh khoảng chừng 20 chén, linh hồn bánh ngon hay không phải kể tới nước chấm. Nước chấm ngọt, cay thì mới đạt độ chuẩn.</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: italic; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">+Bánh bèo Huế nhỏ nhỏ và xinh xinh</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Ẩm thực núi rừng Cúc Phương vốn nổi tiếng bởi nhiều món đặc sản như: dê núi, lợn mường, gà đồi… những món ăn ngon này có thể bắt gặp ở hầu hết các nhà hàng trong cũng như ngoài rừng Cúc Phương. Nếu may mắn đến đúng mùa, các bạn sẽ có cơ hội thưởng thức một số đặc sản Ninh Bình như cua núi, ốc núi, châu chấu…</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">+Thịt dê Ninh Bình</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Thịt dê núi Ninh Bình (Ảnh – tieu_panda)</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Thịt dê núi Ninh Bình có đặc trưng săn chắc, ít mỡ và có vị thơm. Người ta cho rằng sở dĩ như vậy vì ở Ninh Bình có nhiều núi đá, dê chạy nhảy nhiều nên cơ thịt săn chắc, ít mỡ hơn hẳn dê chăn thả trên đồi. Mặt khác, với địa hình đặc trưng của núi đá vôi ngập nước có rất nhiều các loại rau, cỏ, thảo dược thích hợp là thức ăn cho dê như giò gai, giò vàng, bách bộ, ô zô, lim xẹt, móng bò, dướng, bầu trích, mộc sông, mõm chuột, xoan dù, cà gai leo, tạo nên chất lượng và vị ngon của thịt dê. Một số nguyên liệu và đặc sản sở tại khác cũng góp phần làm nổi bật các món thịt dê Ninh Bình phải kể đến các loại rau ăn kèm đặc trưng địa hình núi đá, rượu Kim Sơn, rượu cần Nho Quan và cơm cháy Ninh Bình.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">+ Cơm cháy Ninh Bình</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span><span style=\"color: rgb(0, 0, 0); font-family: \" times=\"\" new=\"\" roman\";=\"\" font-size:=\"\" 12pt;=\"\" font-weight:=\"\" 700;=\"\" white-space:=\"\" pre-wrap;\"=\"\">Cơm cháy Ninh Bình, đặc sản nổi tiếng cùng thịt dê (Ảnh – jessjamesjo)</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Món cơm cháy không phải là món ăn cổ truyền của người Ninh Bình, nhưng do một người con cố đô sáng tạo ra và được lưu giữ, phát triển cho tới nay. Một món ăn mộc mạc, thể hiện sự khéo léo của bàn tay con người, đã được lưu truyền cả trăm năm nay, trở thành đặc sản của vùng đất cố đô,một trong những món ngon nổi tiếng của Ninh Bình.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">+ Gà ri Cúc Phương</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span><span style=\"color: rgb(0, 0, 0); font-family: \" times=\"\" new=\"\" roman\";=\"\" font-size:=\"\" 12pt;=\"\" font-weight:=\"\" 700;=\"\" white-space:=\"\" pre-wrap;\"=\"\">Gà ri nướng, ngay trong một số quán ăn dọc đường rừng (Ảnh – phuong.thao183)</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Gà ri Cúc Phương từ lâu đã trở thành loài vật nuôi đặc sản của huyện Nho Quan (Ninh Bình), loài gà này được nuôi ở vùng rừng núi Cúc Phương, với khí hậu và điều kiện tự nhiên phù hợp nên thịt thơm và ngon, rất được ưa thích.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">+ Ốc núi Cúc Phương</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span><span style=\"color: rgb(0, 0, 0); font-family: \" times=\"\" new=\"\" roman\";=\"\" font-size:=\"\" 12pt;=\"\" font-weight:=\"\" 700;=\"\" white-space:=\"\" pre-wrap;\"=\"\">Ốc núi Cúc Phương thường dày ruột, có hương vị đặc trưng do ăn các loại rong rêu, lá, rễ cây rừng. (Ảnh – _beo.iu.____)</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Khoảng tháng 3 là tháng ốc sinh sôi, nảy nở và đến tháng 7, tháng tám đã có thể bắt được. ốc núi rất đặc biệt, nó chỉ xuất hiện khoảng từ tháng ba đến tháng 7 âm lịch. Đến tháng 8, tháng 9 khi gió heo may về thì ốc biến mất không một dấu vết như chưa từng tồn tại trên trái đất này.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Ốc xuất hiện nhiều khi trời mưa. Bởi vậy, sau cơn mưa là bà con lại hò nhau đi vào rừng, lên núi tìm ốc. Ốc bám trĩu cây, bắt con này, con khác thấy động đã nhả miệng rơi lộp bộp mà không sợ bị vỡ vì nó được trang bị một lớp vỏ rất đặc biệt.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Ốc núi Cúc Phương thường dày ruột, có hương vị đặc trưng do ăn các loại rong rêu, lá, rễ cây rừng.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">- Ba Vì</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"text-decoration: underline; font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: normal; font-variant: normal; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">+ <span style=\"text-decoration-style: initial; text-decoration-color: initial;\">Thịt lợn mán luộc</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Món thịt lợn mán được người dân khu vực vùng núi Ba Vì thường làm món luộc thơm nức mũi, chấm mắm ớt hoặc muối chanh tiêu ớt cực ngon. Thịt lợn chắc, ít mỡ, ngon, dai giòn thơm ngon hơn</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span><span style=\"color: rgb(0, 0, 0); font-size: 12pt; font-weight: 700; text-decoration-line: underline; white-space: pre-wrap;\">Thịt Lợn Mán Cuộn Lá Móc Mật Rán Thơm Lừng Ai Cũng Phát Thèm</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Thịt lợn mán rán hoặc nướng với lá móc mật một món ăn rất ngon hấp dẫn những người sành ăn, vị ngọt béo của miếng thịt cùng với mùi thơm lừng của lá móc mật khiến ai cũng phải tấm tắc khen ngon. Miếng thịt ...</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"text-decoration: underline; font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: normal; font-variant: normal; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">+ <span style=\"text-decoration-style: initial; text-decoration-color: initial;\">Cá rô phi sốt cà chua</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Cá rô phi được nuôi ở ao hồ vùng núi Ba Vì rất nhiều, hiện nay cá rô phi được lai tạo nên con cá to hơn, thịt cá dày hơn, ăn ngon hơn. Cá rô phi bán rất rẻ ở đầy các chợ, để chế biến cá ăn ...</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"text-decoration: underline; font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: normal; font-variant: normal; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">+ Chia sẻ cách nấu bún Riêu Cua đơn giản tại nhà ngon tuyệt</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Bữa ăn cuối tuần có nhiều thời gian chị em mình có thể làm bún riêu cua cho cả nhà thưởng thức nhé! Cách nấu bún riêu cua bò không khó và ăn ngon tuyệt vời. Các bạn có công thức hay thì ...</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span><span style=\"color: rgb(0, 0, 0); font-size: 12pt; font-weight: 700; text-decoration-line: underline; white-space: pre-wrap;\">+ </span><span style=\"color: rgb(0, 0, 0); font-size: 12pt; font-weight: 700; white-space: pre-wrap;\">Cua đồng rang muối vừa giòn lại thơm phức</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Thưởng thức món cua đồng rang muối vừa giòn giòn lại thơm phức, chấm tương ớt cay cay thật hấp dẫn.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"text-decoration: underline; font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: normal; font-variant: normal; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">+ <span style=\"text-decoration-style: initial; text-decoration-color: initial;\">Cá tép thầu dầu chiên giòn</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Cá tép thầu dầu còn gọi là cá mương, tép dầu, mài mại, ... phần nhiều chỉ bằng cái lá tre, kích cỡ chiều dài của từ 5-7cm, chiều ngang chừng 1cm. Cá thầu dầu là loại cá không cần đầu tư nuôi ...</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"text-decoration: underline; font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: normal; font-variant: normal; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">+  miếng thịt ngon nhất trên con lợn - Bạn đã biết để chọn mua chưa?</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Hằng ngày khi đi chợ, bạn vẫn thường hay nghe mọi người chuyền tai hỏi nhau rằng mua thịt lợn nào, thịt ba chỉ, thịt thăn, sườn cốt lết, thịt vai, thịt đùi… Vậy bạn đã thật sự hiểu hết phân loại các loại miếng cắt thịt heo và ...</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"text-decoration: underline; font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: normal; font-variant: normal; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">+ <span style=\"text-decoration-style: initial; text-decoration-color: initial;\">Châu chấu rang lá chanh</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Những con châu chấu lúa béo ngậy thường xuất hiện nhiều ở các cánh đồng lớn vào tháng 6 - 7, Đặc sản châu chấu rang lá chanh là món ăn có hương vị đậm đà khó cưỡng, cuốn hút du khách (ngày ...</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"text-decoration: underline; font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: normal; font-variant: normal; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">+ <span style=\"text-decoration-style: initial; text-decoration-color: initial;\">Bọ xít nhãn rang</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Vào mùa nhãn, bọ xít thường xuất hiện nhiều ở các vườn nhãn, bà con bắt về rang ăn rất ngon và thơm. Món bọ xít rang có hương vị độc lạ thơm ngon, được rất nhiều người ưa chuộng và săn đón</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"text-decoration: underline; font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: normal; font-variant: normal; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">+ <span style=\"text-decoration-style: initial; text-decoration-color: initial;\">Nhộng tằm rang lá chanh</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Đặc sản nhộng tằm rang lá chanh được rất nhiều du khách gần xa ưa chuộng, khi đặt chân đến khu vực Ba Vì du lịch, nhộng tằm rang lá chanh ăn rất tốn cơm, món ăn dân dã của người nông dân.</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"text-decoration: underline; font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: normal; font-variant: normal; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">+ <span style=\"text-decoration-style: initial; text-decoration-color: initial;\">Rau sắn xào</span></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"font-size: 12pt; font-family: \" times=\"\" new=\"\" roman\";=\"\" font-weight:=\"\" 700;=\"\" font-style:=\"\" normal;=\"\" font-variant:=\"\" text-decoration:=\"\" none;=\"\" vertical-align:=\"\" baseline;=\"\" white-space:=\"\" pre-wrap;=\"\" background-color:=\"\" rgb(255,=\"\" 255,=\"\" 255);=\"\" color:=\"\" rgb(0,=\"\" 0,=\"\" 0);\"=\"\">Núi Ba Vì ngoài cảnh đẹp núi rừng và những phong tục tập quán của người dân nơi đây, có một điều làm nên nét đặc sắc cho vùng đất này mà rất ít người biết đến, đó là ẩm thực Ba ...</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt; padding: 7pt 0pt 8pt;\"><span style=\"text-decoration: underline; font-size: 12pt; font-family: Roboto, sans-serif; font-weight: 700; font-style: normal; font-variant: normal; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">+ Gà nướng đất sét - Gà Đắp Đất</span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 0pt; margin-bottom: 8pt; padding: 7pt 0pt 0pt;\"><span style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\"><br></span></p><p class=\"MsoNormal\" style=\"margin: 15pt 0in 7.5pt; line-height: normal; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><span id=\"docs-internal-guid-26bda820-7fff-4f33-2478-2ea4324ea75f\" style=\"background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\"></span></p><p dir=\"ltr\" style=\"line-height: 1.38; margin-top: 12pt; margin-bottom: 12pt;\"><span style=\"font-size: 11pt; font-family: Arial; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; background-color: rgb(255, 255, 255); color: rgb(0, 0, 0);\">&nbsp;</span></p></figcaption></figure><figure id=\"attachment_4250\" class=\"wp-caption alignnone\" aria-describedby=\"caption-attachment-4250\" style=\"margin-bottom: 2em; font-size: 14px; max-width: 100%; font-family: Roboto, sans-serif;\"><figcaption id=\"caption-attachment-4250\" class=\"wp-caption-text\" style=\"padding: 0.4em; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\"><div class=\"code-block code-block-12\" style=\"color: rgb(94, 94, 94); margin: 8px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: top; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Tahoma, Geneva, sans-serif; text-align: center; clear: both;\"><div style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: top; background: transparent;\"></div></div><div class=\"code-block code-block-5\" style=\"color: rgb(94, 94, 94); margin: 8px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: top; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Tahoma, Geneva, sans-serif; text-align: center; clear: both;\"><div style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: top; background: transparent;\"></div></div></figcaption></figure>', '...', '...', 0, 7, 1, '2020-08-20 21:37:53', '2020-09-15 12:18:57', 0, NULL, NULL, 0, '');
INSERT INTO `post` (`post_id`, `image`, `author_id`, `title`, `content`, `link`, `tag`, `featured`, `sort_order`, `status`, `date_added`, `date_modified`, `parent_id`, `summary`, `sub_title`, `top`, `menu`) VALUES
(42, 'catalog/AnhCatBa/fc7173613637af6e4ec5017276ba39a1.jpg', 5, 'Du lịch văn hóa', '<h3 dir=\"ltr\" style=\"line-height:1.32;margin-top:0pt;margin-bottom:7pt;\"><span style=\"font-size:16.5pt;font-family:Arial;color:#4f4f4f;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Khu di tích văn hóa Óc Eo</span></h3><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:8pt;\"><span style=\"font-size:12pt;font-family:Roboto,sans-serif;color:#353535;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Tại Nam Cát Tiên, bạn cũng có cơ hội để khám phá những nét văn hóa lịch sử đặc sắc Óc Eo. Từ trung tâm vườn, bạn sẽ được đưa đến kinh đô cổ của vương quốc Phù Nam, nơi tập hợp những công trình kiến trúc cổ xưa cùng nhiều hiện vật vô cùng quý hiếm. </span><span style=\"font-size:12pt;font-family:Roboto,sans-serif;color:#353535;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"><span style=\"border:none;display:inline-block;overflow:hidden;width:624px;height:417px;\"><img alt=\"Chiêm ngưỡng những công trình kiến trúc lịch sử của khu di tích Óc Eo \" src=\"https://lh5.googleusercontent.com/RRfhMxEi8V-wODA3n9C9Rl8EtV78kdB-3w1ZFeMON5enlqrRzll1oxK5_Zxnv9kODIdb4RltiQ1d6anzot99Pt2ZvSuVjcFsphf09iUm3mS_acayJoFwvQLtshoIOvXOQnzJ_N40\" width=\"624\" height=\"417\" style=\"margin-left:0px;margin-top:0px;\"></span></span><span style=\"font-size:12pt;font-family:Roboto,sans-serif;color:#353535;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"> </span><span style=\"font-size:12pt;font-family:Roboto,sans-serif;color:#353535;background-color:transparent;font-weight:400;font-style:italic;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Chiêm ngưỡng những công trình kiến trúc lịch sử của khu di tích Óc Eo</span><span style=\"font-size:12pt;font-family:Roboto,sans-serif;color:#353535;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"> Khu di chỉ Văn hóa Óc Eo Bạn là người có đam mê khám phá lịch sử văn hóa, hãy nên tham quan địa điểm này. Từ trung tâm vườn, bạn sẽ được đưa đi bằng xe đến huyện Cát Tiên để đến với kinh đô cổ của vương quốc Phù Nam, nơi có nền văn hóa Óc Eo cùng bề dày lịch sử đa dạng. Là thánh địa Bà La Môn giáo, xung quanh là rừng lồ ô, bạn sẽ không khỏi ngạc nhiên với các công trình kiến trúc cổ xưa và huyền bí như gạch đá và các hiện vật thờ: Linga – Yoni, Thần Phúc Ganesa, Thần Hủy Diệt ShiVa, Đấng Sáng Tạo Bradma… Ngoài ra, khu di tích còn lưu giữ những hiện vật quý hiếm như: những mảnh kim loại vàng khắc hình Nam Thần, Nữ Thần, Rùa, Bò…, hộp k’lon dùng trong việc hỏa táng… Từ năm 1997, khu di tích văn hóa Óc Eo đã được công nhận là khu di tích lịch sử – văn hóa quốc gia.</span></p><h3 dir=\"ltr\" style=\"line-height:1.32;margin-top:0pt;margin-bottom:7pt;\"><span style=\"font-size:16.5pt;font-family:Arial;color:#4f4f4f;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Tìm hiểu văn hóa dân tộc Tà Là</span></h3><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:8pt;\"><span style=\"font-size:16.5pt;font-family:Arial;color:#4f4f4f;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"><span style=\"border:none;display:inline-block;overflow:hidden;width:624px;height:413px;\"><img alt=\" ta lai, dan toc s\'tieng, nam cat tien\" src=\"https://lh3.googleusercontent.com/QiuJCqzxe0H4eqWbj52XnHKTd5xJLBr7rrxdkNukydRL3D-jyrMmLFCiAKrrw8G4-KfAreEXLjHGxrI7eRGrGodQf719dv1WP7HDcDun_V3U3-BonnuoXZlEC1i33KE4Z0K-oT0q\" width=\"624\" height=\"413\" style=\"margin-left:0px;margin-top:0px;\"></span></span><span style=\"font-size:12pt;font-family:Roboto,sans-serif;color:#353535;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"> Lễ hội tạ ơn thần rừng của dân tộc Mạ Hoặc nếu các bạn vẫn còn đủ sức để khám phá, hãy thử đến tham quan làng dân tộc Mạ, X’tiêng ở Tà Xài chỉ với khoảng 20 phút đi xe. Tại đây, bạn sẽ được tìm hiểu đời sống của người dân tộc thiểu số bình dị và được ngắm nhìn những di vật ngàn năm của dân tộc nơi đây. </span><span style=\"font-size:12pt;font-family:Roboto,sans-serif;color:#353535;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"><span style=\"border:none;display:inline-block;overflow:hidden;width:624px;height:432px;\"><img src=\"https://lh6.googleusercontent.com/nzwsnCx2yl3q_Or2u73DRvPj_rh3cD5EFmVTtiIdsSPpP84-F-vWuP_Ch1AGkZaRr-q8-IntMDTKUjD7lgZKMXfXlZV2fPEA4cPTd9z0kd9r4RQ4CHxpkRHTc8R143cAO1usA8Oy\" width=\"624\" height=\"432\" style=\"margin-left:0px;margin-top:0px;\"></span></span><span style=\"font-size:12pt;font-family:Roboto,sans-serif;color:#353535;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"> Đến với làng dân tộc, bạn sẽ được xem và học hỏi cách dệt thổ cẩm (ngành nghề truyền thống của dân tộc Mạ) cũng như hòa mình vào không khí núi rừng cùng những người dân hiếu khách nơi đây</span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-top:0pt;margin-bottom:8pt;\"><span style=\"font-size:12pt;font-family:Roboto,sans-serif;color:#353535;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">- Bạch Mã</span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-right: 16pt;margin-top:0pt;margin-bottom:10pt;\"><span style=\"font-size:12pt;font-family:Roboto,sans-serif;color:#353535;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"><span style=\"border:none;display:inline-block;overflow:hidden;width:200px;height:200px;\"><img alt=\"Làng nghề làm nón Huế\" src=\"https://lh6.googleusercontent.com/XyoCJP6yGJiOSTk_Y5GM4MZxi-oXqfsiLL8B_kt1upkpRgRvDEVi1Ag8ezDkkwnfYvl8IPXe2hCtIJ4x-GU6a6htqKkHJ6cyyM-SC95HabrS8TVcVhtaZgq4VXM8Q8tuKH77XlHi\" width=\"200\" height=\"200\" style=\"margin-left:0px;margin-top:0px;\"></span></span></p><h3 dir=\"ltr\" style=\"line-height:1.32;margin-top:0pt;margin-bottom:0pt;\"><span style=\"font-size:18pt;font-family:Arial;color:#222222;background-color:transparent;font-weight:700;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Làng nghề làm nón Huế</span></h3><p dir=\"ltr\" style=\"line-height:1.4727272727272727;margin-right: 8pt;margin-top:0pt;margin-bottom:8pt;\"><span style=\"font-size:11pt;font-family:Arial;color:#222222;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Từ lâu chiếc nón bài thơ gắn với tà áo dài đã trở thành biểu tượng của con người và du lịch Huế. Để mục sở thị những chiếc nón lá thân thương và tìm hiểu thêm về văn hóa, con người xứ Huế, bạn nên ghé thăm làng nghề làm nón nơi đây.</span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-right: 16pt;margin-top:0pt;margin-bottom:10pt;\"><span style=\"font-size:11pt;font-family:Arial;color:#222222;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"><span style=\"border:none;display:inline-block;overflow:hidden;width:200px;height:200px;\"><img alt=\"Làng cổ Phước Tích - đặc trưng của nét đẹp cố đô Huế xưa\" src=\"https://lh5.googleusercontent.com/c65GcIeJzpbXvI_8awhAVmCILB6riXdAdIRRp8WRXa6o0zKNnFc2yzQ2WFadBuz8JFO3TZb2TG0r-dkifoyWiMzswZ506v29eY3_OygCZ-JO9YRnHtcQpTVn4nySAV4kt_AZ4JZ3\" width=\"200\" height=\"200\" style=\"margin-left:0px;margin-top:0px;\"></span></span></p><h3 dir=\"ltr\" style=\"line-height:1.32;margin-top:0pt;margin-bottom:0pt;\"><span style=\"font-size:18pt;font-family:Arial;color:#222222;background-color:transparent;font-weight:700;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Làng cổ Phước Tích - đặc trưng của nét đẹp cố đô Huế xưa</span></h3><p dir=\"ltr\" style=\"line-height:1.4727272727272727;margin-right: 8pt;margin-top:0pt;margin-bottom:8pt;\"><span style=\"font-size:11pt;font-family:Arial;color:#222222;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Nằm bên bờ Ô Lâu hiền hòa, cách quốc lộ 1A chỉ 1 km, ngôi làng nhỏ yên bình này xưa kia từng nổi tiếng với làng nghề gốm cổ truyền.</span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-right: 16pt;margin-top:0pt;margin-bottom:10pt;\"><span style=\"font-size:11pt;font-family:Arial;color:#222222;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"><span style=\"border:none;display:inline-block;overflow:hidden;width:200px;height:200px;\"><img alt=\"Nét đẹp rất Huế ở làng cổ Phước Tích\" src=\"https://lh5.googleusercontent.com/JpeGBNUKBLSwkk1ASDX6c_2IOuiDqNU18g9541O1lNlMo_cN_AHRXivRPmiuozEjpBC0OrxQ4_7wSa7qRSIcNHtWGBPOd4wUFMxynw2IX52x6cRphhvBV0er1HTqqPAzNCJ2NNmz\" width=\"200\" height=\"200\" style=\"margin-left:0px;margin-top:0px;\"></span></span></p><h3 dir=\"ltr\" style=\"line-height:1.32;margin-top:0pt;margin-bottom:0pt;\"><span style=\"font-size:18pt;font-family:Arial;color:#222222;background-color:transparent;font-weight:700;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Nét đẹp rất Huế ở làng cổ Phước Tích</span></h3><p dir=\"ltr\" style=\"line-height:1.4727272727272727;margin-right: 8pt;margin-top:0pt;margin-bottom:8pt;\"><span style=\"font-size:11pt;font-family:Arial;color:#222222;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Nằm bên bờ Ô Lâu hiền hòa, cách quốc lộ 1A chỉ 1 km, ngôi làng nhỏ yên bình này xưa kia từng nổi tiếng với làng nghề gốm cổ truyền.</span></p><p dir=\"ltr\" style=\"line-height:1.38;margin-right: 16pt;margin-top:0pt;margin-bottom:10pt;\"><span style=\"font-size:11pt;font-family:Arial;color:#222222;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"><span style=\"border:none;display:inline-block;overflow:hidden;width:200px;height:200px;\"><img alt=\"Gác Trịnh, điểm dừng chân thú vị khi đến Huế\" src=\"https://lh5.googleusercontent.com/yFaTfKZ35wiAorRFA6OeeJfaPv1MttOGqd83H5K6u6kaHTmSSqo2RMz6pCqWY7kcpvkHkdKPQsqp-OXw_jxhDtxQgirG44hxcGvcsA0N4Ard8UK6Q4GtEHICC7eJaVvKsyAfvqLT\" width=\"200\" height=\"200\" style=\"margin-left:0px;margin-top:0px;\"></span></span></p><h3 dir=\"ltr\" style=\"line-height:1.32;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;\"><span style=\"font-size:18pt;font-family:Arial;color:#222222;background-color:transparent;font-weight:700;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Gác Trịnh, điểm dừng chân thú vị khi đến Huế</span></h3><p dir=\"ltr\" style=\"line-height:1.7999999999999998;margin-right: 8pt;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;padding:0pt 0pt 8pt 0pt;\"><span style=\"font-size:9pt;font-family:Arial;color:#222222;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Gác Trịnh mở của từ 1/4/2013 và đã trở thành địa điểm quen thuộc của những người yêu nhạc Trịnh Công Sơn ở Huế, không gian quán gợi nhớ tới người nhạc sỹ đại tài của nền tân nhạc Việt Nam.</span></p><p dir=\"ltr\" style=\"line-height:1.7999999999999998;margin-right: 8pt;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;padding:0pt 0pt 8pt 0pt;\"><span style=\"font-size:9pt;font-family:Arial;color:#222222;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">- Cúc Phương</span></p><h3 dir=\"ltr\" style=\"line-height:1.32;margin-right: 8pt;background-color:#ffffff;margin-top:0pt;margin-bottom:16pt;padding:7pt 0pt 0pt 0pt;\"><span style=\"font-size:18pt;font-family:Arial;color:#222222;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">BẢN SẮC VĂN HOÁ.</span></h3><p dir=\"ltr\" style=\"line-height:1.7999999999999998;text-align: justify;background-color:#ffffff;margin-top:0pt;margin-bottom:0pt;padding:0pt 0pt 16pt 0pt;\"><span style=\"font-size:9pt;font-family:Arial;color:#222222;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Từ xa xưa, Cúc Phương là nơi cư trú và sinh sống của cộng đồng người Mường với những nét văn hoá độc đáo và đặc trưng, đó là những nếp nhà sàn, ruộng bậc thang, những cối giã gạo nương, những khung dệt thổ cẩm, độc đáo hơn là những lễ hội, phong tục tập quán và nếp sống của cộng đồng mà du khách có thể cảm nhận được trong thời gian thăm bản.</span></p><p dir=\"ltr\" style=\"line-height:1.7999999999999998;text-align: justify;background-color:#ffffff;margin-top:0pt;margin-bottom:16pt;\"><span style=\"font-size:9pt;font-family:Arial;color:#222222;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">(còn nhiều hơn nữa)</span></p><p dir=\"ltr\" style=\"line-height:1.7999999999999998;margin-right: 8pt;background-color:#ffffff;margin-top:0pt;margin-bottom:8pt;\">&nbsp;</p><p><span id=\"docs-internal-guid-4d51e42a-7fff-2e47-02cc-582bf0055c37\"><br></span></p>', '...', '...', 0, 1, 1, '2020-08-20 21:38:26', '2020-09-15 12:15:16', 0, NULL, NULL, 0, ''),
(43, 'catalog/Anh_Dai_Dien/4b07480385bbc57a0f3c36bf892b6228.jpg', 5, 'TIỀM NĂNG DU LỊCH CỦA VIỆT NAM', '<p><span style=\"background-color: transparent; color: rgb(68, 68, 68); font-family: Arial; font-size: 10.5pt; white-space: pre-wrap; text-align: justify;\">Việt Nam có tiềm năng lớn để phát triển du lịch vì nó được ưu đãi với vị trí thuận lợi về địa lý, khí hậu và điều kiện tự nhiên, bao gồm cả một đường bờ biển dài hơn 3.000 km dọc theo nước với rừng cây xanh và cảnh quan hùng vĩ. Việt Nam tự hào có hơn 125 bãi biển và nằm trong danh sách 12 quốc gia hàng đầu cho những vịnh đẹp nhất trên thế giới. Du lịch đóng góp đáng kể vào sự phát triển kinh tế của Việt Nam với kim ngạch xấp xỉ 200.000 tỷ đồng vào năm 2013. Trong tháng Mười Một năm 2014, số lượng khách quốc tế ước đạt trên 7,2 triệu người.</span><br></p><p dir=\"ltr\" style=\"line-height:1.92;text-align: center;margin-top:0pt;margin-bottom:8pt;\"><span style=\"font-size:10.5pt;font-family:Arial;color:#444444;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"><span style=\"border:none;display:inline-block;overflow:hidden;width:299px;height:184px;\"><img alt=\"tp_of_vn1.jpg\" src=\"https://lh4.googleusercontent.com/QYGGNLiUBqDFCD4QhOjkgw1iEHSMbtuV84sAbnSBu0w-M-if-n4Fauwa7qneaRpiV-7yahNuu8kchsQo8Z2TyggxjuYVaNhG800JUazyl2ZmsjW-pRwW6-jbLYMzVhD1c-ft2utW\" width=\"299\" height=\"184\" style=\"margin-left: 0px; margin-top: 0px; width: 330.022px; height: 129.2px;\"></span></span><span style=\"font-size:10.5pt;font-family:Arial;color:#444444;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\"><span style=\"border:none;display:inline-block;overflow:hidden;width:163px;height:182px;\"><img alt=\"tp_of_vn2.jpg\" src=\"https://lh5.googleusercontent.com/wlK4TTGePU1oIEWl4uM4y7TCpedq2OtV2beGxtsoG5FBYF_KA9mKK_5Y6wTryJk0I2LT-XQW1ZFPinfs1eiD6J9wB-VSf5ZSd7ij-ltJDoA4rbwYyOeX3pGwYJUKJIx8jcliYH3Y\" width=\"163\" height=\"182\" style=\"margin-left: 0px; margin-top: 0px; width: 164.7px; height: 127.555px;\"></span></span></p><p dir=\"ltr\" style=\"line-height:1.92;text-align: justify;margin-top:0pt;margin-bottom:8pt;\"><span style=\"font-size:10.5pt;font-family:Arial;color:#444444;background-color:transparent;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Việt Nam có hơn 3.000 cảnh quan và di tích lịch sử được liệt kê là di sản quốc gia. Tám di sản thiên nhiên và văn hóa của Việt Nam được UNESCO công nhận là di sản thế giới và được Việt Nam công nhận là di sản quốc gia</span></p><p dir=\"ltr\" style=\"line-height:1.92;text-align: justify;margin-top:0pt;margin-bottom:8pt;\"><span style=\"font-size:10.5pt;font-family:Arial;color:#555555;background-color:#ffffff;font-weight:400;font-style:normal;font-variant:normal;text-decoration:none;vertical-align:baseline;white-space:pre;white-space:pre-wrap;\">Hiện nay Việt Nam có 30 vườn quốc gia gồm Ba Bể, Bái Tử Long, Hoàng Liên, Tam Đảo, Xuân Sơn, Ba Vì, Cát Bà, Cúc Phương, Xuân Thủy, Bạch Mã, Bến En, Phong Nha-Kẻ Bàng, Pù Mát, Vũ Quang, Bidoup Núi Bà, Chư Mom Ray, Chư Yang Sin, Kon Ka Kinh, Yok Đôn, Côn Đảo, Lò Gò-Xa Mát, Mũi Cà Mau, Núi Chúa, Phú Quốc, Phước Bình, Tràm Chim, U Minh Hạ, U Minh Thượng.</span></p><p><br></p>', '...', '...', 0, 9, 1, '2020-08-17 09:00:41', '2020-09-15 12:19:37', 0, NULL, NULL, 0, '');
INSERT INTO `post_category` (`category_id`, `image`, `parent_id`, `top`, `column`, `sort_order`, `status`, `date_added`, `date_modified`, `name`, `description`, `featured`) VALUES
(1, 'catalog/AnhCatBa/f69116c53d193a98ae077e1f8b108d03.jpg', 0, 1, 0, 1, 1, '2020-07-14 22:57:13', '2020-08-17 07:21:12', 'Giới Thiệu Chung', '<h2 style=\"color: rgb(0, 0, 0); margin: 1em 0px 0.25em; padding: 0px; overflow: hidden; border-bottom: 1px solid rgb(162, 169, 177); font-size: 1.5em; font-family: \" linux=\"\" libertine\",=\"\" \"palatino=\"\" linotype\",=\"\" \"times=\"\" new=\"\" roman\",=\"\" serif;=\"\" line-height:=\"\" 1.3;\"=\"\"><span style=\"color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">Theo định nghĩa của&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/Li%C3%AAn_minh_B%E1%BA%A3o_t%E1%BB%93n_Thi%C3%AAn_nhi%C3%AAn_Qu%E1%BB%91c_t%E1%BA%BF\" title=\"Liên minh Bảo tồn Thiên nhiên Quốc tế\" style=\"font-family: sans-serif; font-size: 14px; background-color: rgb(255, 255, 255); color: rgb(11, 0, 128); background-image: none;\">Liên minh Quốc tế Bảo tồn Thiên nhiên và Tài nguyên Thiên nhiên</a><span style=\"color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">&nbsp;(IUCN)</span><sup id=\"cite_ref-1\" class=\"reference\" style=\"color: rgb(32, 33, 34); font-family: sans-serif; line-height: 1em; font-size: 11.2px; white-space: nowrap; unicode-bidi: isolate;\"><a href=\"https://vi.wikipedia.org/wiki/V%C6%B0%E1%BB%9Dn_qu%E1%BB%91c_gia#cite_note-1\" style=\"color: rgb(11, 0, 128); background-image: none;\">[1]</a></sup><span style=\"color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\">&nbsp;thì vườn quốc gia là:</span></h2><h1 id=\"H1Title\" class=\"title-site\" style=\"margin-top: 0px; margin-bottom: 15px; font-size: 26px; font-family: \" open=\"\" sans\",=\"\" sans-serif;=\"\" font-weight:=\"\" 600;=\"\" color:=\"\" rgb(51,=\"\" 51,=\"\" 51);\"=\"\"><dl style=\"margin-top: 0.2em; margin-bottom: 0.5em; color: rgb(32, 33, 34); font-family: sans-serif; font-size: 14px;\"><dd style=\"margin-left: 1.6em; margin-bottom: 0.1em; margin-right: 0px;\"><i>Khu vực tự nhiên của vùng đất và/hoặc vùng biển, được chọn để</i></dd><dd style=\"margin-left: 1.6em; margin-bottom: 0.1em; margin-right: 0px;\"><i>(a) bảo vệ tình trạng nguyên vẹn sinh thái của một hay nhiều hệ sinh thái cho các thế hệ hiện tại và tương lai,</i></dd><dd style=\"margin-left: 1.6em; margin-bottom: 0.1em; margin-right: 0px;\"><i>(b) loại bỏ việc khai thác hay chiếm giữ không thân thiện đối với các mục đích của việc chọn lựa khu vực và</i></dd><dd style=\"margin-left: 1.6em; margin-bottom: 0.1em; margin-right: 0px;\"><i>(c) chuẩn bị cơ sở cho các cơ hội tinh thần, khoa học, giáo dục, giải trí và tham quan, tất cả các cơ hội đó phải có tính tương thích về văn hóa và môi trường.</i></dd></dl></h1>', 1),
(2, 'catalog/429f9c250052f43f455dd217726d7df0.jpg', 0, 1, 0, 2, 1, '2020-07-13 21:55:59', '2020-09-13 21:57:27', 'Du Lịch', '<h1 style=\"margin-top: 10px; font-size: 30px; font-family: UVNHongHaHep; color: rgb(69, 103, 38); text-transform: uppercase; padding-left: 15px;\">HOẠT ĐỘNG DU LỊCH</h1><div class=\"news-single\" style=\"padding-left: 15px;\"><div class=\"news-content\" style=\"\"><div class=\"like-form\" style=\"color: rgb(51, 51, 51); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 14px;\"=\"\"><div id=\"fb-root\" class=\" fb_reset\" style=\"background: none; border: 0px; border-spacing: 0px; color: rgb(0, 0, 0); cursor: auto; direction: ltr; font-family: \" lucida=\"\" grande\",=\"\" tahoma,=\"\" verdana,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 11px;=\"\" font-variant-numeric:=\"\" normal;=\"\" font-variant-east-asian:=\"\" line-height:=\"\" 1;=\"\" margin:=\"\" 0px;=\"\" overflow:=\"\" visible;=\"\" padding:=\"\" text-shadow:=\"\" none;=\"\" visibility:=\"\" word-spacing:=\"\" normal;\"=\"\"><div style=\"overflow: hidden; position: absolute; top: -10000px; width: 0px; height: 0px;\"><div></div></div></div><div class=\"fb-like fb_iframe_widget\" data-href=\"https://catbanationalpark.vn/cat-ba-national-park-7-6-3\" data-layout=\"button_count\" data-action=\"like\" data-show-faces=\"true\" data-share=\"false\" fb-xfbml-state=\"rendered\" fb-iframe-plugin-query=\"action=like&amp;app_id=&amp;container_width=731&amp;href=https%3A%2F%2Fcatbanationalpark.vn%2Fcat-ba-national-park-7-6-3&amp;layout=button_count&amp;locale=vi_VN&amp;sdk=joey&amp;share=false&amp;show_faces=true\" style=\"vertical-align: top; display: inline-block; position: relative;\"><span style=\"display: inline-block; position: relative; text-align: justify; vertical-align: bottom; width: 78px; height: 20px;\"><br><br></span></div></div><div class=\"single-content\" style=\"\"><p style=\"vertical-align: inherit; line-height: inherit; list-style: inherit;\"><span style=\"color: rgb(0, 0, 255); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 24px;\"=\"\">Hoạt động du lịch</span></p><p style=\"vertical-align: inherit; line-height: inherit; list-style: inherit;\"><span style=\"color: rgb(0, 0, 255); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 24px;\"=\"\"><br></span></p><p style=\"vertical-align: inherit; line-height: inherit; list-style: inherit;\"><span style=\"color: rgb(0, 0, 255); font-family: \" helvetica=\"\" neue\",=\"\" helvetica,=\"\" arial,=\"\" sans-serif;=\"\" font-size:=\"\" 24px;\"=\"\">1. Du lịch sinh thái lâm nghiệp</span></p><p style=\"vertical-align: inherit; line-height: inherit; list-style: inherit;\">Đến với VQG Cát Bà, du khách có thể lựa chọn nhiều tuyến tham quan các hệ sinh thái rừng khác nhau để khám phá nguồn tài nguyên động vật hoang dã quý hiếm của chúng như rừng nguyên sinh nhiệt đới xanh tươi, các loại cây đại thụ, thảm thực vật đá vôi hay rừng cây gỗ Kim Giao, v.v.</p><p style=\"vertical-align: inherit; line-height: inherit; list-style: inherit;\"><br></p><p style=\"vertical-align: inherit; line-height: inherit; list-style: inherit;\">Các tuyến đường ngắn (1-2 giờ đi bộ và đi bộ đường dài): Rừng Kim Giao - Đỉnh Ngự Lâm; Đường mòn Giáo dục Môi trường; Động Trung Trang</p><p style=\"vertical-align: inherit; line-height: inherit; list-style: inherit;\"><br></p><p style=\"vertical-align: inherit; line-height: inherit; list-style: inherit;\">Các tuyến đường dài (4 - 7 tiếng treking) như: Ao Ếch - Việt Hải; Mây Bầu - Hang Bệnh viện Quân đội; Mê Đi - Mây Bầu, Trung Trang - Liên Minh Suối Gỗ</p><table width=\"762\" style=\"color: rgb(51, 51, 51); font-family: inherit; font-size: inherit; vertical-align: inherit; font-weight: inherit; font-style: inherit; line-height: inherit; list-style: inherit; text-decoration: inherit; height: 10px;\"><tbody style=\"vertical-align: inherit; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit;\"><tr style=\"font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit;\"><td style=\"font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit;\"><img src=\"https://catbanationalpark.vn/uploads/str33.jpg\" alt=\"\" width=\"180\" height=\"120\" style=\"vertical-align: inherit; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit; max-width: 100%; height: auto; display: block; margin: auto;\"></td><td style=\"font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit;\"><img src=\"https://catbanationalpark.vn/uploads/str52.jpg\" alt=\"\" width=\"123\" height=\"120\" style=\"vertical-align: inherit; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit; max-width: 100%; height: auto; display: block; margin: auto;\"></td><td style=\"font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit;\"><img src=\"https://catbanationalpark.vn/uploads/str-4.jpg\" alt=\"\" width=\"180\" height=\"120\" style=\"vertical-align: inherit; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit; max-width: 100%; height: auto; display: block; margin: auto;\"></td><td style=\"font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit;\"><img src=\"https://catbanationalpark.vn/uploads/thumbs/dlstr1.jpg\" alt=\"\" width=\"200\" height=\"126\" style=\"vertical-align: inherit; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit; max-width: 100%; height: auto; display: block; margin: auto;\"></td></tr></tbody></table><p style=\"color: inherit; font-family: inherit; font-size: inherit; vertical-align: inherit; font-weight: inherit; font-style: inherit; line-height: inherit; list-style: inherit; text-decoration: inherit;\">&nbsp;</p><p style=\"vertical-align: inherit; line-height: inherit; list-style: inherit;\"><span style=\"color: rgb(0, 0, 255); font-size: 18.6667px;\">2. Du lịch Biển</span></p><p style=\"vertical-align: inherit; line-height: inherit; list-style: inherit;\">Bên cạnh hoạt động du lịch sinh thái rừng, Vườn cũng đang xúc tiến đầu tư vào loại hình du lịch sinh thái sông ngòi để đáp ứng nhu cầu khám phá tài nguyên biển rộng lớn. Tại khu vực biển của Vườn, du khách có thể lựa chọn các loại hình du lịch biển như đi thuyền tham quan Vịnh Lan Hạ, Vạn Bội, Việt Hải, Vân Tả, Trà Bàu, chèo thuyền kayak khám phá hệ sinh thái vịnh, vịnh, lặn ngắm san hô, leo lên một số vách đá của những hòn đảo xinh đẹp ở Vịnh Lan Hạ, câu cá, bơi và tắm biển ở những bãi biển biệt lập và các môn thể thao dưới biển khác</p><p style=\"vertical-align: inherit; line-height: inherit; list-style: inherit;\"><br></p><p style=\"vertical-align: inherit; line-height: inherit; list-style: inherit;\">Các chuyến du lịch biển thường kéo dài từ 2 - 5 giờ.</p><table width=\"689\" style=\"color: rgb(51, 51, 51); font-family: inherit; font-size: inherit; vertical-align: inherit; font-weight: inherit; font-style: inherit; line-height: inherit; list-style: inherit; text-decoration: inherit; height: 29px;\"><tbody style=\"vertical-align: inherit; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit;\"><tr style=\"font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit;\"><td style=\"font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit;\"><img src=\"https://catbanationalpark.vn/uploads/t.jpg\" alt=\"\" width=\"160\" height=\"90\" style=\"vertical-align: inherit; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit; max-width: 100%; height: auto; display: block; margin: auto; float: left;\"></td><td style=\"font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit;\"><img src=\"https://catbanationalpark.vn/uploads/cheo-kayak1.jpg\" alt=\"\" width=\"160\" height=\"120\" style=\"vertical-align: inherit; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit; max-width: 100%; height: auto; display: block; margin: auto;\"></td><td style=\"font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit;\"><img src=\"https://catbanationalpark.vn/uploads/1.jpg\" alt=\"\" width=\"160\" height=\"120\" style=\"vertical-align: inherit; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit; max-width: 100%; height: auto; display: block; margin: auto;\"></td><td style=\"font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit;\"><img src=\"https://catbanationalpark.vn/uploads/stb2.JPG\" alt=\"\" width=\"160\" height=\"106\" style=\"vertical-align: inherit; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit; max-width: 100%; height: auto; display: block; margin: auto;\"></td></tr></tbody></table><p style=\"color: inherit; font-family: inherit; font-size: inherit; vertical-align: inherit; font-weight: inherit; font-style: inherit; line-height: inherit; list-style: inherit; text-decoration: inherit;\">&nbsp;</p><p style=\"color: inherit; font-family: inherit; font-size: inherit; vertical-align: inherit; font-weight: inherit; font-style: inherit; line-height: inherit; list-style: inherit; text-decoration: inherit;\"><span style=\"vertical-align: inherit; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: rgb(0, 0, 255); list-style: inherit; text-decoration: inherit;\"><span style=\"font-weight: inherit; vertical-align: inherit; font-family: inherit; font-style: inherit; font-size: inherit; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit;\"><span style=\"vertical-align: inherit; font-family: inherit; font-weight: inherit; font-style: inherit; font-size: 14pt; line-height: inherit; color: inherit; list-style: inherit; text-decoration: inherit;\">3. Khám phá hang động</span></span></span></p><p style=\"color: inherit; font-family: inherit; font-size: inherit; vertical-align: inherit; font-weight: inherit; font-style: inherit; line-height